css:
body { background: #0f83d6 url(http://i.piccy.info/i7/e9fdb11f8677a95170f5d9613e6e58b8/4-67-159/58405877/Sci_Fi_art.jpg) top left no-repeat fixed; }
.top-posts, .navbar, .content { margin: auto; width: 1200px; }
.post-item { width: 599px; float: left; background: rgba(0,0,0,1.0); height: 400px; overflow: hidden; border-bottom: 1px dashed #5b5b5b; border-right: 1px dashed #5b5b5b; position: relative; 
background: -o-linear-gradient(top, #202020, #000000);
background: -webkit-gradient(linear, left top, left bottom, from(#202020), to(#000000));
background: -moz-linear-gradient(top, #202020, #000000); 
}

.background-text-video .widescreen { float: left; width: 349px; }
.background-text-video .main { float: left; width: 250px; margin-left: 0; margin-right: 0; }

.background-text-video .main .row-fluid { padding: 0 10px; background: rgba(0,0,0,1.0); height: 400px; }
.background-text-video .body { padding: 10px; }
.background-text-video .flex-video { margin-top: 105px; }
.background-text-video .span12 { min-height: 0; }
.flex-video.widescreen { padding-bottom: 45%; }

.row-fluid { width: auto; }
.row-fluid .span6 .body-content { width: 210px; position: absolute; top: 0; right: 0;}
.span6 { margin: 0; }

.span6 .body-title { padding: 0 20px; position: absolute; top: 0; left: 0; width: 210px; }
.post-item .image img { height: 200px; padding-top: 105px; }

.post-item-image .body-content { padding-top: 15px; background: rgba(0,0,0,1.0); background-image: none; background: #0a0a0a; opacity: 0.96;
}

.span12 { min-height: 0; }

.span6 .body-title h1 a, .span6 .body-title h1 { font-size: 16px; line-height: 25px !important; color: #08c; }
.top-post-title h5 { font-size: 14px; line-height: 25px; color: #eeeeee; }

.posts-wrapper br { display: none; }

.main, .small { font-size: 13px; line-height: 20px; }
.small { font-size: 11px; }

.span12 h3 a, .span12 h3, .span12 h2 { font-size: 16px; line-height: 25px !important; position: absolute; top: 0; left: 10px; width: 220px; }
.container-fluid { background: #ffffff; }

.top-post-title { height: 70px; }

.top-post:last-child { border: 0; }

.post-item-image .body-title, .span12 h3 a, .span12 h3, .span12 h2, .span6 .body-title h1 a, .top-post-title h5 { text-shadow: 1px 2px 0 #000; color: #0f83d6; }
.top-post-title h5 { color: #d3d3d3; }
