css:
@-moz-document url-prefix(http://), url-prefix(ftp://), url-prefix(file://), url-prefix(https://), url(about:home){

* { color: #aeaeae !important; border-color: #333333 !important; text-decoration: none !important; text-shadow: none !important; background: #202020 !important} 

div,a,span {background: none !important}
.footer, .header {background: #202020 !important}

a,a:link 		{ color: #bbbbbb !important; text-shadow: none !important}
a:hover,
a:hover *		{ color: #cfcfcf !important; text-shadow: none !important}
a:visited,
a:visited *		{ color: #3aea1d !important; text-shadow: none !important}
a:visited:hover,
a:visited:hover *	{ color: #93f882 !important; text-shadow: none !important}

}

@-moz-document url(about:newtab) {
.newtab-title, div {background: #202020 !important}
a,a:link 		{ color: #bbbbbb !important; text-shadow: none !important}
a:hover,
a:hover *		{ color: #cfcfcf !important; text-shadow: none !important}
a:visited,
a:visited *		{ color: #3aea1d !important; text-shadow: none !important}
a:visited:hover,
a:visited:hover *	{ color: #93f882 !important; text-shadow: none !important}

}

/* CSS code from 'stormi' style: http://userstyles.org/styles/40405/perfect-dark-google-maps */

@-moz-document url-prefix(https://maps.google) {
 .mv-hc-desc-c,.mv-primary:hover, .mv-primary-label,
 .mv-secondary:hover, .mv-control:hover, .mv-control,
 .mv-secondary-title-parent, .mv-manage-parent { background: rgba(0,0,0,.8) !important}
 img  {color: #999 !important; border-color: #333 !important; background: transparent !important}
 #mv-secondary-container {border: 1px solid #000 !important; box-shadow: 0 0 5px #000, 0 0 5px #000 !important; border-radius: 3px !important}
.mv-primary-label {border-color: #111 !important; margin-left: 4px !important}
.mv-primary-label, .mv-secondary, .mv-control, .mv-secondary-title-parent {background: -moz-linear-gradient(#222, #2D2D2D) !important}
.mv-whiteline {background: transparent !important}
.mv-secondary-title-parent, .mv-manage-parent {border-radius: 0 !important}
 .mapfooter {background: -moz-linear-gradient(right, rgba(39,39,39,0.7), rgba(39,39,39,0.7), rgba(39,39,39,0)) !important;}
#copyright span {color: #DDD !important; text-shadow: 0 0 3px #000 !important}
#traffic_options {background: rgba(9,9,9,.9) !important; border: 1px solid #444 !important; border-radius: 5px; box-shadow: 0 0 9px #000, 0 0 9px #000}
table.pt_disabled {opacity: .4 !important}
.mv-primary-preview-frame, .mv-primary-preview-lens, .mv-maptype-icon-m, .mv-maptype-icon-k, .mv-dropdown {background-image: url(http://www.abload.de/img/hpimgs6_new7aueo.png) !important}
.mv-primary-preview-frame {background-position: -76px -44px !important}
.mv-primary-preview-lens {background-position: 0 -44px !important}
.mv-maptype-icon-m {background-position: 0 0 !important}
.mv-maptype-icon-k {background-position: -68px 0 !important}
.mv-dropdown {background-position: -136px 0 !important}

}
