css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(www.)?torrentsm((d.(com|eu|me))|(oldova.(com|org|net))).*") {
div,form * {background: transparent !important;}
*{font-family: "Arial"!important}
*{-moz-border-radius: 15px 0px 15px 0px !important;}

table, fullWidth, td, form, img, .message {color:#000 !important; text-shadow: #000 1px 1px 4px !important; background-color: rgba(0,0,0,.8) !important}

body, html, table {background: rgba(0,0,0,.05)url(http://funkyimg.com/i/2ss88.jpg) left center fixed repeat !important; background-size:100%!important;}

*,a,h2,h3,h1,div,a.div{color:#3399ff !important;}
a:hover,h2:hover,h3:hover,h1:hover{color:#3333ff!important}
    
td#activateTimerText, td#watcherText, td.comment.inited {color:green !important; text-shadow: #000 1px 1px 4px !important; background-color: rgba(0,0,0,.8) !important}

#menutable img {height: 1px;}
    

div#top_menu>table>tbody>tr>td:first-child>a{
display:block !important;
background: url("http://funkyimg.com/i/2vobS.png") no-repeat scroll 0 0 
transparent !important;
width: 280px !important;
height: 76px !important;
}

#torrents > table > tbody > tr  {
    background-color:transparent !important;
}
    
    #tbl_menu a{
color: #fff !important;
padding: 0 5px !important;
height:26px !important;
line-height:26px !important;
display:inline-block !important;
}
#tbl_menu a:hover{
background: #4370A0 !important;
border: 1px solid #335477 !important;
}

#tmd_logo{
display:none !important;
}

    table, fullWidth, td, form, img, .message {
color: #FFF !important; }

