css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pressxtowin.com") {

body {
    background: url(http://i.imgur.com/OeRHwEH.png) center top fixed repeat;
	font-family: "PT Sans", sans-serif !important;
}

/* Верхнее меню и главный контейнер */

.navbar, .content {
    margin: auto;
    width: 1024px;
    -webkit-box-shadow: rgba(0, 0, 0, 0.3) 0 0 4px;
    box-shadow: rgba(0, 0, 0, 0.3) 0 0 4px;
    }
    
.navbar {
    margin-top: -10px;
    }
    
.content {
    margin-top: 10px;
    }

.pull-right {
    /* фикс для социальных кнопок (уехали вправо почему-то) */
    margin-right: 20px; 
    }    

.pull-left {
	font-family: "PT Sans Caption", sans-serif;
	}

/* Отступы от краёв основного блока для главной и страницы поста */
.posts-wrapper, .post-main-data {
    padding: 20px;
    }

/* Фикс для картинки и видео в посте */
.post-cover, .post-main-data .flex-video {
    margin-top: 10px;
    }

/* Непрозрачный фон для блока поста */
.content {
    background: #fff;
    }

/* Контейнер с постом и дискусом */
.show-content {
    margin-left: 10px !important;
    }
    
/* Заголовок под видео */ 
.basic-show-title {  
    font: bold 24px "PT Sans Caption", sans-serif !important;
    padding-bottom: 25px;
    }
    
/* Заголовок на картинке в посте и на картинке на главной */      
.basic-title, .body-title a {
    font: bold 36px "PT Sans Caption", sans-serif !important;
    text-shadow: 1px 1px 3px black !important;
    }
    
/* Удаление какого-то уебанского отступа текста на главной */
.body-content {
    padding-top: 20px !important;
    }

}
