css:
/**
 * Light LOTRO forum Theme for Stylish 0.7
 * WTFPL - wtfpl.net
 * Bits by SNy CopyLeft (C) <SNy@bmx-chemnitz.de>
 * With help from Odlind
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    regexp("https://www\\.lotro\\.com/forums/.*"),
    regexp("https://www\\.lotro\\.com/en/forums/.*")

{

	.body_wrapper {
		border-radius: 0 0 0 0				!important;
	}

    /* links */
	body a {
		color: #394041				!important;
	}

    /* links in posts */
	.postrow a {
		color: #5e6566				!important;
		text-decoration: underline				!important;
	}

    /* footer */
	.blockrow {
		background: #ffffff			!important;
	}
	
    /* titles */
	hgroup > h1 {
		text-shadow: 0 0 20px #ffdf60, 0 0 8px rgba(0, 0, 0, 0.8)			!important;
		color: #ffdf60			!important;
	}

    /* top menu with the pretty font */
	ul.navmenu a {
		color: #ffcf71		!important;
		text-shadow: 0 0 6px #000, 0 0 3px #000			!important;
	}

    /* top menu with the pretty font pop ups */	
	.navmenu > li > ul {
		background: #546363			!important;
		border-width: 0			!important;
	}
	

    /* top bar with the login link text */
	body > #userbar a {
		color: #ffffff				!important;
	}
	
	body > #userbar {
		background: #546363			!important;
		opacity: 1			!important;
	}
	
	.page.vb {
		background: #546363			!important;
	}

    /* top bar with the logo */
	.header-wrap {
		background: #546363			!important;
		box-shadow: 0 0 0 0			!important;
		position: relative;
		width: 100%;
	}
  
    /* website background */
	html {
		background: #bec4c4			!important;
	}

	body {
		color: #394041				!important;
		background: #a9afaf			!important;
	}

    /* some bar above the forum nav */
	#pageWrapper {
		background: #a9afaf			!important;
	}

    /* forum nav */
	.breadcrumb {
		border: 0px					!important;
		background: #a9afaf			!important;
		padding: 0 0 10px 10px      !important;
	}

    /* other nav */
	.breadcrumbs {
		text-shadow: 0 0 3px #ffdf60      !important;
	}

    /* activity stream background */
    .blockbody {
        background: #bec4c4         !important;
    }

    /* activity stream - posts */
    li.activitybit {
        background: #e1e1e1            !important;
        border: 0px                 !important;
        box-shadow: 0 0 0 0         !important;
        color: #000000              !important;
    }
    
    /* activity stream - views */
    li.activitybit .views {
        background: #e1e1e1         !important;
        padding: 3px 0px 3px 3px    !important;
    }

    /* activity stream - time */
    li.activitybit .content .datetime {
        background: #f7f7f7          !important;
    }
    
    /* activity stream - title */
    li.activitybit .content .title {
        background: #f7f7f7         !important;
        color: #989898            !important;
    }

    /* activity stream - except */
    #activitylist li.activitybit .excerpt {
        background: #fff            !important;
        padding: 5px 0px 5px 5px    !important;
        font-size: 13px             !important;
        color: #000             !important;
    }

    li.activitybit .content {
        background: #f7f7f7         !important;
        padding: 5px 0px 5px 5px    !important;
    }


    .blockhead {
        background: #a9afaf             !important;
        border: 0px                     !important;
        border-top-left-radius: 0px     !important;
        border-top-right-radius: 0px    !important;
        padding: 0px 0px                !important;
    }

    .newactivity, .moreactivity {
        background: #a9afaf             !important;
        border: 0px                     !important;
        color: #000                     !important;
        margin-top: 0px                 !important;
    }

    /* thread view - pagecount */
	.above_postlist .pagination_top .pagination, .above_postlist .pagination_top .postpagestats {
		color: #394041				!important;
	}
 
    /* thread view - header */
	.vb .body_wrapper {
		background: #bec4c4			!important;
	}

    /* thread view - header 2 */
	.tcatBar {
		background: #a9afaf			!important;
	}

	.tcatRight {
		background: #a9afaf			!important; 
	}

	.tcatLeft {
		background: #a9afaf			!important;
	}

    /* thread view - title */
	#pagetitle h1 {
		color: #394041				!important;
	}

    /* thread view - post info */
	.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
		background: #7a8a8a			!important;
		border: 0px					!important;
		color: #394041				!important;
	}

    /* thread view - time of posting */
	.postbit .postdate .time {
		color: #394041				!important;
	}

    /* thread view - post content */
    .postbit .postbody {
        background-color: #f7f7f7	!important;
        color: #000000				!important;
    }
    
    /* thread view - post content (font size) */
    .postbit .postrow {
        font-size: 13px				!important;
    }

	/* bit by SNy */
    .postbit .postbody font, .postbit .postbody span {
        color: #000                  !important;
        /* list of families is the forum's default as of 2013-08-02 */
        font-family: Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif     !important;
    }
 
    /* thread view - post title */
	.postbit .posttitle, .postbitlegacy .title {
		border-bottom: 0px			!important;
	} 
   
    /* thread view - borders around everything */
	.postbit, .postbitlegacy, .eventbit {
		border: 1px solid #bec4c4	!important;
	}

    /* thread view - getting rid of more pesky borders */
	.postfoot {
		border: 1px solid #bec4c4	!important;
	}

    /* thread view - quote button */
	.postbitlegacy .postfoot .textcontrols a.newreply, .postbit .postfoot .textcontrols a.newreply {
		background: url("https://www.lotro.com/forums/images/styles/UndergroundStyle/buttons/quote_40b.png") no-repeat scroll left center #bec4c4   !important;
	}

    /* thread view - multiquote button */
	.postbitlegacy .postfoot .textcontrols a.multiquote, .postbit .postfoot .textcontrols a.multiquote {
		background: url("https://www.lotro.com/forums/images/styles/UndergroundStyle/buttons/multiquote-back_40b.png") no-repeat scroll left center #bec4c4   !important;
	}

    /* thread view - report button */
	.postbitlegacy .postfoot .textcontrols a.report, .postbit .postfoot .textcontrols a.report {
		background: url("https://www.lotro.com/forums/images/styles/UndergroundStyle/buttons/report-40b.png") no-repeat scroll left center #bec4c4   !important;
	}
	
   /* thread view - edit button */
	.postbitlegacy .postfoot .textcontrols a.editpost, .postbit .postfoot .textcontrols a.editpost, .eventbit .eventfoot .textcontrols a.editevent {
		background: url("https://www.lotro.com/forums/images/styles/UndergroundStyle/buttons/edit_40b.png") no-repeat scroll left center #bec4c4   !important;
	}

	/* thread view - code box */
	.bbcode_container div.bbcode_code, .bbcode_container pre.bbcode_code {
		background: #e7e7e7 !important;
	}

    /* thread view - quoted posts */
    .postbit .postrow .bbcode_quote {
        background-color: #e1e1e1	!important;
        color: #000000				!important;
        font-size: 12px 			!important;
    }
    
    /* thread view - extra padding */
    .postbit .postbody .content {
        padding: 15px 10px 0px 10px     !important;
    }

    /* thread view - signature area */
    .postbit .postbody .signature {
        font-size: 12px				!important;
        padding: 10px 10px 0px 10px	!important;
        border-top-color: #bccacc	!important;
        color: #6e7e7e;
	}

   /* thread view - report & quote bar */
	.postbit .postfoot .textcontrols {
		background: #bec4c4			!important;
	}

    /* thread view - poster info area */
    .postbit .userinfo, .userinfo_noavatar {
        background-color: #e1e1e1			!important;
        border-bottom: 1px solid #bccacc	!important;
		color: #394041						!important;
    }
    
    /* thread view - title position fix */
    .usertitle, .rank {
		text-align: left                    !important;
		padding: 5px 0 0 0        !important;
    }
  
    /* thread view - mod name position fix */
    .username_container {
		text-align: left                    !important;
    }

    /* thread view - member info op-up (that's not just in thread view but do you really expect me to reorganize all this stuff)*/
	.memberaction_body.popupbody {
		background: #f7f7f7					!important;
	}

    /* thread view - member info op-up mouseover */	
	.popupbody li > a:hover, .popupbody li > label:hover {
		background: #e1e1e1				!important;
	}
    
    /* forum index */
	.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
		background: #f7f7f7					!important;
		border-bottom: 1px solid #bccacc	!important;
		border-left: 1px solid #bccacc		!important;
		border-right: 1px solid #bccacc		!important;
	}

    /* forum index - extra bar thing with 'last post' */
	.thead_bar .theadrow {
		background: #a9afaf					!important;
	}

    /* forum index - navbar background */
	.navtabs {
		background: #7a8a8a	!important;
	}
	
    /* forum index - navbar text */
	.navtabs li a {
		color: #f7f7f7						!important;
	}
	
    /* forum index - separators */
	.navtabs li {
		background: #8f9d9d !important;  
	}

    /* forum index - navbar lvl 2 */
	.navtabs li li {
		background: #546363 !important;
		margin: 0 !important;
	}

	.navtabs .popupbody {
		background: #7a8a8a !important; 
	}

	.navbar {
		background: #7a8a8a	!important;
		border-radius: 0	!important;
		padding: 3px 0 0 0 !important;
	}

	.navbarLeft {
		background: #7a8a8a	!important;
	}
	
	.navbarRight {
		background: #7a8a8a	!important;
		padding: 0 20px	!important;
	}

    /* forum index - moving the search box down a bit */
	#searchBox .textbox {
		margin: 10px 0 0 0 !important;
	}

    /* forum index - moving the search button down a bit */
	#searchBox .searchBox_button {
		margin: 11px 0 0 0 !important;
	}

    /* forum index - navbar buttons (What's New, etc.) */
	.navtabs li a.navtab {
		background: #7a8a8a !important;
	}

    /* forum index - navbar buttons mouseover */
	.navtabs li a.navtab:hover {
		color: #ffa648						!important;
	}

    /* forum index - search text */
	#searchBox .searchBox_advanced_search a:link, #searchBox .searchBox_advanced_search a:visited {
		color: #e0e4e4						!important;
	}
	
    /* thread list - padding fix */
    .threadmeta {
    padding: 5px 0 0 0    !important;
    }
 
    /* thread list - views fix */
    .threadbit .threadstats {
        line-height: 120%;
    }

    /* thread list - stickies */
	.threadbit .sticky {
		background: #e6e8e8							!important;
		border-color: transparent #bccacc #bccacc	!important;
		border-right: 1px solid #bccacc				!important;
	}
	
    /* separating stickies from normal threads [thank you Odlind!]*/
    ol.stickies:after {
        content:"-";
        color: #bec4c4;
    }

    /* thread list - non-stickies */
	.threadbit .nonsticky {
		background: #f7f7f7							!important;
		border-color: transparent #bccacc #bccacc   !important;
		border-right: 1px solid #bccacc				!important;
	}

    /* thread list - extra bar thing with 'last post' */
	#threadlist .threadlisthead {
		background: #a9afaf				!important;
		border-top: 1px solid #7d8989	!important;
		border-left: 0px				!important;
		border-right: 0px				!important;
		border-bottom: 0px				!important;
	}

    /* thread list - page numbers */
	.pagination span a {
		background: #f7f7f7				!important;
		border: 0px						!important;
		color: #7aa9a7					!important;
	}

    /* reply - background */
	.wysiwyg_block, .wysiwyg_block iframe.textbox, .wysiwyg {
		background-color: #f7f7f7      !important;
		color: #000000                 !important;
	}

    /* reply - more background */
	.formcontrols .blockrow {
		background-color: #f7f7f7      !important;
	}

    /* reply - title box */
	.textbox, textarea, select {
		background-color: #f7f7f7      !important;
		color: #000000                 !important;
	}

    /* reply - buttons bar */
	.blockfoot, .blocksubfoot {
		background-color: #bec4c4 		!important;
	}

    /* reply - fix quote box */
	.bbcode_container div.bbcode_quote {
        background-color: #e1e1e1		!important;
        color: #000000					!important;
	}

    /* reply - imageinsert pop-up fix */	
    .cke_skin_kama .cke_dialog_body .cke_dialog_contents {
        background: #f7f7f7		!important;
    }

    /* reply - imageinsert pop-up fix */	
    .cke_skin_kama .cke_dialog_contents .cke_dialog_footer {
        background: #f7f7f7		!important;
        border: 0		!important;
    }

    /* footer - boxes font */
	.forum_info {
		color: #394041                      !important;
	}

    /* footer random details until further notice */
	.forum_info .blockhead {
		background: #a9afaf     !important;
		border: 0px             !important;
		padding: 3px 0 5px 5px  !important;
		margin-top: 10px        !important;
	}

	.forum_info .blockbody {
		background: #f7f7f7     !important;
		border: 0               !important;
		box-shadow: 0           !important;
		padding: 0 0 3px 0      !important;
	}

	.thread_info h4 {
		padding: 3px 0 3px 8px      !important;
	}

	.thread_info {
		color: #000     !important;
	}

	.vb .wgo_subblock {
		border-bottom: 0 !important;
	}

	.wgo_block .blockbody {
		background: #f7f7f7 !important;
		border: 0 !important;
	}

	.popupbody li {
		background: #f7f7f7      !important;
		color: #000 !important;
	}

	.navpopupbody li {
		background: #f7f7f7      !important;
		color: #000 !important;
	}

	.navlinks {
		background: #a9afaf !important;
		border-top: 0 !important;
		color: #000 !important;
	}

	.footer-wrap {
		background: #bec4c4			!important;
		padding: 50px			!important;
	}

	.footerNav {
		background: #a9afaf			!important;
	}

	.footerNavLeft {
		background: #a9afaf			!important;
	}

	.footerNavRight {
		background: #a9afaf			!important;
	}

	#footerBar {
		background: #a9afaf			!important;
		border: 0			!important;
	}

	#footerMain {
		background: #7a8a8a			!important;
		border: 0			!important;
	}

    /* that's not really a footer detail. eh */
	.time {
		color: #ff8e1e			!important;
		font-weight: bold 			!important;
	}
	
    /* dev tracker - can't remember what that is */
	#posttracker .trackerlisthead {
		background: #a9afaf 			!important;
		border: 0 			!important;
	}

    /* dev tracker - post list */
	#posttracker .trackerbit {
		background: #f7f7f7 			!important;
		border-bottom: 1px solid #bccacc			!important;
		border-top: 1px solid #bccacc			!important;
		padding: 6px 10px;
	}

    /* community guidelines */
	.black-box, .node-page, #character-info, .menu-media .content > .media, .slide-content.image aside.caption-box, body.menu-articles a.article-item, body.page-taxonomy-term article, body.menu-articles article, .node-news {
		background: #f7f7f7 			!important;
	}

    /* user name & title */
    .vb .postdetails a:hover, .vb .postdetails a:link, .vb .postdetails a:visited, .vb .userinfo a:hover, .vb .userinfo a:link, .vb .userinfo a:visited, .vb .postbody .postrow a:hover, .vb .postbody .postrow a:visited, .vb .activitybit a:hover, .vb .activitybit a:link, .vb .activitybit a:visited {
        text-decoration: none 			!important;
    }

    /* user profile */
	#sidebar_container.member_summary {
		background: #f7f7f7 			!important;
		border-color: #bccacc 			!important;
		color: #394041 			!important;
	}

    /* user profile boxes border */
	#sidebar_container .blockbody {
		background: #f7f7f7 			!important;
		border: 0 			!important;
		border-radius: 0 			!important;
	}

    /* user profile boxes */
	#sidebar_container .userprof_content .blockrow {
		border: 0			!important;
		background: #e1e1e1 			!important;
		border-radius: 0 			!important;
		color: #394041 			!important;
	}

	.floatcontainer, .formcontrols .blockrow, dl.stats {
		font-size: 11px 			!important;
		color: #394041 			!important;
	}

	.member_summary dl.stats dt {
		font-size: 11px 			!important;
		color: #394041 			!important;
	}

	#sidebar_container .blocksubhead {
		color: #394041 			!important;
		border: 0 			!important;
	}

    /* user profile shadow */
	#sidebar_container .underblock {
		background: transparent 			!important;
	}

    /* user profile top bars */
	.memberprofiletabunder {
		background: #bec4c4 			!important;
		height: 0px 			!important;
	}

	#activity_tab_container > div {
		background: #a9afaf 			!important;
	}

    /* user profile top bars - selected tab */
	.activitystream_block dd.selected, dd.userprof_module {
		background: #a9afaf 			!important;
		border-color: #868f8f 			!important;
		border-radius: 0                !important;
	}

    /* user profile top bars - inactive tab */
	.activitystream_block dd, dd.userprof_moduleinactive {
		background: #7a8a8a 			!important;
		border-color: #4c5c5c 			!important;
		border-radius: 0                !important;
	}
	
    /* user profile top bars - text */
    #profile_tabs a, #profile_tabs a:hover {
		background: none 			!important;
        border: 0 			!important;
    }
    
    .moduleinactive_bg {
		background: #f7f7f7 			!important;
    }

    /* calendar - today */
	.calendar .events.today, .calendar .today {
		background: #fff 			!important;
		border: 2px solid #ff8e1e 			!important;
	}

    /* calendar - day box */
	.calendar .events {
		background: #f7f7f7 			!important;
		border: 1px solid #8d9898			!important;
	}

    /* calendar - days from last/next month */
	.month .othermonth {
		background: #e1e1e1 			!important;
	}

    /* calendar - header */
	.blockhead {
		background: #7a8a8a 			!important;
		border: 0 			!important;
		border-radius: 0 			!important;
		padding: 5px 0px 5px 5px 			!important;
	}

    /* calendar - days of the week */
	.blocksubhead {
		background: #a9afaf			!important;
		border-top: 1px solid #5a6969			!important;
		border-bottom: 1px solid #8d9898			!important;
		color: #394041			!important;
		font-weight: bold			!important;
	}

    /* calendar - right column inactive days */
	.mini .othermonth a {
		background: #bec4c4 		!important;
		border: 0			!important;
		border-radius: 0			!important;
		color: #919999			!important;
	}

    /* calendar - top tabs */
	dl.tabslight dd a {
		background: #bec4c4 		!important;
		border: 1px solid #8d9898			!important;
	}

	dl.tabslight {
		border-bottom: 1px solid #8d9898			!important;
	}
	
    /* calendar - event page */
	.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
		background: #bec4c4		!important;
	}

	.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
		background: #f7f7f7			!important;
		border: 0			!important;
	}

	.postbitlegacy .postdetails, .eventbit .eventdetails {
		background: #e1e1e1			!important;
		float: left;
		width: 100%;
	}

	.postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a, .postbitlegacy .postfoot .textcontrols span.mobile, .postbit .postfoot .textcontrols span.mobile {
		background: #7a8a8a			!important;
	}

    /* PMs - reply button */
	.postbitlegacy .postfoot .textcontrols a.quickreply, .postbit .postfoot .textcontrols a.quickreply, .postbitlegacy .postfoot .textcontrols a.forwardpost, .postbit .postfoot .textcontrols a.forwardpost {
		background: url("https://www.lotro.com/forums/images/styles/UndergroundStyle/buttons/reply_40b.png") no-repeat scroll left center #bec4c4 		!important;
	}

    /* User CP - active category */
	#usercp_nav .blockrow .active {
		background: #a9afaf			!important;
		border: 1px solid #ff8e1e 			!important;
	}

    /* User CP - category mouseover */
	#usercp_nav .blockrow * a:hover {
		background-color: #e1e1e1			!important;
	}

    /* User CP - background */
	#usercp_nav .blockbody {
		background: #fff			!important;
		border: 0			!important;
	}
	
    /* User CP - fixin' sum background */
    .vb .formcontrols .section, .vb .formcontrols.blockbody {
		background: #f7f7f7			!important;
    }

    /* No more horrid button shadows */
    .button-appearance, .button, input[type="submit"], .more-link a, .slide-content .action, .gallery .more, .fancybox.alternates a, .page-taxonomy-term .news-teaser .news.content > a, .read-more a, .pager a {
        box-shadow: 0 0  !important;
    }

    /* Announcements */
    .announcements .announcerow {
        background: #ff8e46			!important;
    }

    .announcerow .announceinfo, .announcerow .announcement {
        background: #ff8e46			!important;
    }

}
