css:
@-moz-document
	domain('www.tumblr.com')
{
/* header */
DIV.l-header-container--refresh {
  position: absolute;
  background-color: transparent;
  }

/* blue fix */
div.search_form_row {background-color: rgba(50,0,0,0.5)}
div.l-header .selection_nipple {border-bottom-color: rgba(50,0,0,0.5)}

div.search_form_row {background-color:  rgba(100,100,100,0.8) !important}
#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
body {
  background: url('https://40.media.tumblr.com/c2c9c21ebfb3832ad1c417d496788ca8/tumblr_n7jevzrrtM1rxghfko1_1280.jpg')
    right bottom rgb(42,10,83) no-repeat fixed !important;
  background-size: cover !important;
  }
	ol#posts li blockquote,body.mceContentBody blockquote {
		margin-left:0 !important;
		margin-right:0 !important;
		padding-left:10px !important;
		border-width:4px !important;
		border-color:#a8bccf !important;
	}
	ol#posts li blockquote blockquote,body.mceContentBody blockquote blockquote {
		border-color:#839aaf !important;
	}
	ol#posts li blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote {
		border-color:#6b7d8f !important;
	}
	ol#posts li blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote {
		border-color:#4c5e6f !important;
	}
	ol#posts li blockquote blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
		border-color:#36434f !important;
	}
	div.l-content.l-content.l-content.l-content {
		background:rgba(50,0,0,0.5);
		border-radius: 20px;
		/*margin:auto;*/
		/*padding: 20px;*/
	}
	h1 {
		color:#333333;
		text-shadow:0 0 3px #ffffff;
	}
	.no_posts_found,h1.dashboard_header {
		color:#666666;
	}
	img#content_top,img#content_bottom {
		display:none;
	}
	#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
		background:none;
	}
	#right_column .dashboard_nav_item {
		background:none;
	}
	#right_column .dashboard_nav_item #hide_radar {
		background:transparent;
	}
	#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo,#right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset,#right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
		background:rgba(0,0,0,0.5);
	}
	#nav .nav_item.active {
		background:rgba(50,0,0,0.5) !important;
	}
	#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
		background:rgba(50,0,0,0.5) !important;
	}
	ol#posts li.notification.alt {
		background-color:rgba(0,0,0,0.5);
	}
	ol#posts li.notification.first_notification {
		background-color:rgba(0,0,0,0.8);
	}
	ol#posts li.notification {
		background-color:rgba(0,0,0,0.8);
		border-bottom:1px solid #000000;
	}
	ol#posts li.notification.last_notification {
		border-bottom:1px solid #000000;
	}
	ol#posts li.post .permalink {
		background:rgba(50,0,0,0.9);
	}
	#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
		background:rgba(0,0,0,0.8);
		border:solid #000000;
	}
	ol#posts li.post .post_avatar {
		border-bottom:0px solid #0A2939;
	}
	#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
		border-top:1px solid #000000;
		border-bottom:1px solid #000000;
	}
	#right_column a#dashboard_switch_blog_menu_current:hover {
		background:none;
	}
	#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
		background:none;
	}
	form.dashboard_options_form {
		background-color:rgba(0,0,0,0.7);
		border-bottom:1px solid #000000;
		border-radius:10px;
		color:#cccccc;
		display:block;
		font-size:13px;
		margin-bottom:25px;
		padding:15px 22px;
	}
	form.dashboard_options_form .option_container {
		border-top:1px solid #000000;
	}
	.no_posts_found,h1.dashboard_header {
		color: #333333;
	}
}
