css:
@-moz-document url-prefix("http://pressxtowin.com/") {
body { background: #ffffff !important; }
.top-post-title { background: rgba(0,0,0,0.4); }
.top-post-title:hover { background: rgba(0,0,0,0.0); }
.vertical-line { border: 0; }
.post-title a { color: #000000; }
.dsq-widget-item { border-bottom: 1px dashed #cccccc; padding-bottom: 10px; }
.border-bottom { border-bottom: 1px dashed #cccccc; }
#popularthreads { margin-bottom: 30px; }
.navbar-pxtw { background: #EEE; border-bottom: 1px dashed #cccccc; }
.navbar-pxtw .navbar-inner { max-width: 1280px; margin: 0 auto; background: none; }


.navbar-fixed-top .navbar-inner, .navbar-static-top .navbar-inner { 
webkit-box-shadow: none;
-moz-box-shadow: none;
box-shadow: none; 
border: 0; 
}



}
