css:
@-moz-document url-prefix(https://forums.pokemmo.eu){
#ipboard_body{
background-color: #ffd0d0 !important;
}

body{
color:red !important;
}

img[src*="https://forums.pokemmo.eu/public/style_images/master/logo.png"]{
width:0;
height:63px;
padding-right:358px;
background:transparent url("http://i.imgur.com/KbL3a0J.png") bottom no-repeat !important;
}

img[src*="https://forums.pokemmo.eu/public/style_images/master/f_icon.png"]{
width:0;
height:27px;
padding-right:33px;
background:transparent url("http://i.imgur.com/SHVttdG.png") no-repeat !important;
}

img[src*="https://forums.pokemmo.eu/public/style_images/master/f_icon_read.png"]{
width:0;
height:27px;
padding-right:33px;
background:transparent url("http://i.imgur.com/GuSTxxa.png") no-repeat !important;
}

img[src*="https://forums.pokemmo.eu/public/style_images/master/t_unread.png"]{
width:0;
height:17px;
padding-right:33px;
background:transparent url("http://i.imgur.com/YMFbtBP.png") no-repeat !important;
}

#branding{
background:transparent url("http://i.imgur.com/tKjzFNa.png") repeat-x !important;
border-bottom:solid 2px maroon !important;
}

#primary_nav{
background-color:#740606 !important;
}

li#nav_home.left{
background-color: maroon !important;
}

h3.maintitle{
background:transparent url("http://i.imgur.com/4pkAI6T.png") repeat-x !important;
border-color:maroon !important;
box-shadow:0px 1px 0px maroon inset !important;
}

td.col_c_forum{
color:orange;
}

h4{
color:orange;

}

.ipsFilterbar li.active a{
background-color:#880000 !important;
}

.unread .altrow{
background-color:pink !important;
}

.ipsBox{
background-color:#ffe8e8 !important;
}

div.ipsBox.table_wrap{
color:maroon;
}

a {
color:red !important;
}

DIV.ipsFilterbar.maintitle{
background: transparent url("http://i.imgur.com/4pkAI6T.png") repeat-x !important;
border-color:maroon !important;
box-shadow: 0px 1px 0px maroon !important;
}

h3.row2{
background-color:pink !important;
}

div.maintitle.clear.clearfix{
background:transparent url("http://i.imgur.com/4pkAI6T.png") repeat-x !important;
border-color:maroon !important;
box-shadow:0px 1px 0px maroon !important;
}

.maintitle a{
color:#d6d6d6 !important;
}

#community_app_menu > li > a {
background-color:#3d0000 !important;
}

#community_app_menu > li > a:hover{
background-color:#570000 !important;
}

a:hover{
color:#ff5c5c !important;
}

.maintitle a:hover{
color:white !important;
}
