css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {

::-webkit-scrollbar-thumb{
background-color: #000;
border: 2px solid #f9f9f9;
height:auto;
}
 
::-webkit-scrollbar {
height:9px;
width:9px;
background-color: #000;
border: 4px solid #f9f9f9;
}
   
::selection {
background:#A6FF9B;
}


body{
background:#260510 url("http://media.tumblr.com/09c32090e9d372796868788c418a7ac1/tumblr_inline_mr0kkzA4sz1qz4rgp.gif") bottom right fixed no-repeat !important;
}

.login.sign_up,
A[href="/forgot_password"],
#login_form SPAN,
.footer_links {
display: none !important; 
}

#logo {
width: 0px !important;
height: 0px !important;
padding-left: 196px !important; 
padding-top: 44px !important; 
background-image: url("https://secure.assets.tumblr.com/images/logo/logo.png") !important; 
background-repeat: no-repeat !important;
}



#new_post{
background: #fff !important;  
opacity:50%;
}

.post.is_mine.with_avatar.new_post {
  background: rgba(88,35,45, 0.50) !important; 
}


.new_post_label{
  color: rgba(0,0,0, 1.0) !important; 
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(229, 229, 228, 0.50) !important; 
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(236,191,201, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(236,191,201, 0.50)  !important;
}




.mceContentBody {
  background: url("http://static.tumblr.com/kuehu0n/U5Gmqa2f7/img_2459.jpg") !important; 
  background-size: 100% !important; 
  color: rgba(255,255,255, 1.0) !important; 
}


#content_top {
  width: 0px !important;
  height: 0px !important;




}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 20px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}
#footer {
  display: none !important; 
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes  {
  color: rgba(255,255,255, 0.50) !important; 
  background: rgba(69,57,62, 1.0) !important; 
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -1px !important; 
}

}
