css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com"), domain("reddit.com") {
    ul.flat-list.buttons {
        margin-top: 1px;
        margin-bottom: 2px;
    }

    .link .save-button {
        margin-left: -4px;
    }

    .link form.state-button.hide-button a {
        color: #6f6b6b;
        margin-right: 5px;
        background-color: #c4c4c4;
        padding: 0 2px 0 2px;
        border: 1px solid transparent;
        border-radius: 2px;
        transition: color .5s, border 1.5s, opacity 2s;
        -webkit-transition: color .5s, border 2s, opacity 2s;
    }
        .link form.state-button.hide-button a:hover {
            text-decoration: none;
            color: #595656;
            padding: 0 2px 0 2px;
            border: 1px solid #B4B4B4;
        }
        .link form.state-button.hide-button a:active {
            padding: 0 2px 0 2px;
            border: 1px solid transparent;
            filter: alpha(opacity=70);    
            opacity: 0.70;
        }

    /*Remove 'share' button*/
    .link span.share-button {
        display: none;
        margin: 0;
    }
}
