css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://bbs.saraba1st.com/") {
div.pop_show_mini
{
width: 550px !important;
}
.editor_content img
{
vertical-align: bottom !important;
}
.main_wrap,.header,.footer_wrap
{
width: 90% !important;
}
.main_sidebar,.nav_weak
{
display: none;
}
.main_content
{
margin-right: 0 !important;
}
.floor_page {
margin-left: 45% !important;
}
.forum_group .ct table {
table-layout: inherit !important;
}
.forum_group .ct tr:first-child td,
.forum_group .ct tr:first-child th {
border-top-width: 1px !important;
}
.forum_group .ct tr:last-child td,
.forum_group .ct tr:last-child th {
border-bottom-width: 0 !important;
}
.forum_group .ct tr td,
.forum_group .ct tr th {
border-width: 0 0 1px !important;
border-color: #022C80 !important;
border-style: solid !important;
}
.thread_posts_list tr:last-child td {
border-bottom-width: 0 !important;
}
.nav ul {
padding: 12px 0 0 !important;
}
.header_search {
display: none !important;
}
.header_login_later {
padding-top: 24px !important;
}
.header_login_btn {
margin: 19px 0 0 10px !important;
}
.pop_select_forum {
margin-left: auto !important;
right: 5% !important;
}
.reply_toolbar_wrap {
width: auto !important;
overflow: hidden !important;
}
.floor_reply textarea {
width: 100% !important;
}
.thread_posts_list .author {
width: 15px !important;
padding-left: 0 !important;
}
.thread_posts_list .author a {
display: none !important;
}
}
