css:
@-moz-document url-prefix("http://weibo.com"),
url-prefix("http://www.weibo.com")
{
body{
background-image:url(http://ww4.sinaimg.cn/mw690/9f4d3ff0gw1e7cx97yzqsj21ao0t6n09.jpg)!important;
background-attachment:fixed !important;
background-size:cover!important;
}
/*强制样式*/
*{border-color:rgba(255,255,255,.6) !important;font-family:"微软雅黑" !important;}

body, .S_txt1, .SW_fun .S_func1, .current .S_func1:hover,.S_txt2, .SW_fun2:hover .S_txt3 ,address, caption, cite, code, dfn, em, i, th, var, b ,.S_func3,.noborder ,.S_txt3{color:#000 !important;}
a, .S_link1, .S_func1:hover, .S_bg5 .S_func1:hover, .SW_fun:hover .S_func1, .SW_fun2:hover .S_func3, .SW_fun2:hover .S_func4 {color: rgb(43, 50, 255) !important;}
.WB_feed_spec{background:#fff !important;}

.W_miniblog{padding-top:60px !important;}
.W_btn_b,a.W_gotop{background:rgba(255,255,255,0.2) !important;}
/*hide*/
div[feedtype="ad"],/*微博推广*/
.W_no_border,/*好友动态*/
.layer_tips,
.key,
.S_bg4_c,.S_bg1_c,.S_line1_c,div.gn_title:nth-child(4) > a:nth-child(1) > i:nth-child(1),div.gn_title:nth-child(3) > a:nth-child(1) > i:nth-child(1),div.gn_title:nth-child(2) > a:nth-child(1) > i:nth-child(1),.arrow,.WB_right_module,.W_rightModule,.tips_wrapper,.footer_adv,.global_footer,.W_tabarrow_big.S_bg4{display:none !important;}

li.W_no_border{display:block !important;}

/*代码优化*/
.S_bg1,#Pl_Core_RightPicMulti__8 > div:nth-child(1) > div:nth-child(1) > h4:nth-child(1),#Pl_Core_RightUserGrid__7 > div:nth-child(1) > div:nth-child(1) > h4:nth-child(1),.W_miniblog,.W_miniblog_fb,.W_main_a,.WB_left_nav,.S_txt1,.WB_left_nav a.lev_curr, .WB_left_nav a.lev_curr:hover,#plc_main,#Box_centent,.input,.WB_global_nav,.gn_bg,a.W_btn_c:nth-child(2) > span:nth-child(1),.profile_top,.S_bg4,div.W_btn_c > span:nth-child(1),a.W_btn_c > span:nth-child(1)
{background:none !important;}
.lev:hover,.lev2:hover,.WB_feed_together,.PRF_title_a,.pftb_lk:hover,.page
{background:rgba(255,255,255,.6) !important;}

/*home page*/
.W_main{width:980px !important;background:rgba(255,255,255,0.25) !important;position:relative !important;box-shadow:0px 0px 2px rgba(20,15,10,0.6) !important;min-height:1200px !important;}
.W_main_a{width:980px !important;}

/*left side*/
.W_main_l{border:none !important;width:0 !important;}
.WB_left_nav{background:rgba(255,255,255,.1) !important;
box-shadow: -1px -1px 2px rgba(20,15,55,0.6) !important;
-moz-transition: all .25s linear;
width:50px !important;overflow:hidden !important;
position:absolute !important;
right:0px !important;top:280px !important;
border:1px white solid !important;
border-radius:5px !important;
padding-bottom:10px;
z-index:3 !important;}
.WB_left_nav:hover{width:150px !important;-moz-transition: all .25s linear;}

.lev2 a{padding-left:22px !important;}
.lev2 i{margin-right:15px !important;}

#pl_leftnav_common a{display:block !important;width:150px !important;height:30px !important;overflow:hidden !important;}

.lev3{width:150px !important;}
.W_iconlev_s{margin-right:15px !important;}
.W_new_count,.W_new_count i{background:orange !important;}
form.left_nav_line:nth-child(2) > fieldset:nth-child(1) > legend:nth-child(1){display:block !important;width:50px !important;}
.tit{width:150px;color:#fff !important;}
.lev_curr,.lev,.levmore,.lev2{width:150px;}
/*xuanfu*/
.WB_left_nav .FIXED{
width:50px !important;
height:auto !important;
top:80px !important;
border:#fff solid 1px !important;
border-radius:5px !important;
padding-bottom:10px;
background:rgba(255,255,255,.1) !important;
box-shadow: -1px -1px 2px rgba(20,15,55,0.6) !important;
-moz-transition: all .25s linear;}
.WB_left_nav .FIXED:hover{width:150px !important;-moz-transition: all .25s linear;}


/*right side*/
#plc_main{
width:980px !important;}
#Box_centent{border:2px blue solid !important;}
.W_main_c{width:700px !important;}
.W_follow_bg{width:81% !important;}

/*input*/
.title_area{padding-top:6px !important;}
div.input:nth-child(2){box-shadow:inset 1px 1px 2px rgba(20,15,55,0.6) !important;}
.send_btn{border:1px rgba(255,255,255,0) solid !important;}
.send_btn{width:20px !important;height:15px !important;-moz-transform: rotate(133deg);z-index:1;position:relative !important;
border-radius:0px 50px 0px  70px!important;}
 .send_btn:after{border-color:rgba(255,255,255,0) !important;
 content:"";display:block;width:30px;height:40px;position:absolute;-moz-transform: rotate(133deg);top:8px;right:5px;z-index:2;
border-radius:0px 50px 0px  70px!important;-moz-transition:all .25s linear;}
.send_btn,.send_btn:after{background:rgba(100,125,25,.8) !important;}
.send_btn:hover,.send_btn:hover:after{border-color:rgba(255,255,255,0) !important;background:rgba(100,155,25,.8) !important;-moz-transform: rotate(113deg) scale(1.3);-moz-transition:all .25s linear;}

/*top bar*/
.gn_name:hover,.gn_tab:hover,div.gn_title:nth-child(1) > a:nth-child(1) > i:nth-child(1){background:-moz-linear-gradient(top,rgba(75,200,0,0.3) 10%,rgba(30,150,0,0.3) 70%,rgba(60,100,0,0.3) 100%)!important;}
.WB_feed_type{position:relative !important;border:1px rgba(255,255,255,.6) solid !important;padding:10px !important;margin-top:10px !important;box-shadow:1px 1px 2px rgba(16,55,22,.6) !important;background:rgba(255,255,255,.4)}
.WB_screen{float:right !important;position:absolute !important;right:0px !important;}
.input_detail{width:98% !important;}
#Box_right{z-index:1!important;width:250px !important;}
.gn_search{background:rgba(255,255,255,.2) !important;border:1px rgba(255,255,255,.6) solid !important;border-radius:30px !important;box-shadow:inset 1px 1px 2px rgba(20,15,10,0.6) !important;}
.gn_topmenulist,.layer_menu_list{background:rgba(255,255,255,.7) !important;box-shadow: 1px 1px 2px rgba(20,15,10,0.6) !important;}
.gn_input{color:rgba(255,255,255,.9) !important;}
.enter{float:right !important;}

.gn_name,div.gn_title:nth-child(1) > a:nth-child(1) > i:nth-child(1),div.gn_title:nth-child(2) > a:nth-child(1) > i:nth-child(1),
div.gn_title:nth-child(3) > a:nth-child(1) > i:nth-child(1),div.gn_title:nth-child(4) > a:nth-child(1) > i:nth-child(1),
div.gn_title:nth-child(2) > a:nth-child(1) > i:nth-child(1) > span:nth-child(1) > em:nth-child(1),
div.gn_title:nth-child(3) > a:nth-child(1) > i:nth-child(1) > span:nth-child(1) > em:nth-child(1),
div.gn_title:nth-child(4) > a:nth-child(1) > i:nth-child(1) > span:nth-child(1) > em:nth-child(1)
{color:#fff !important;}

/*wrap*/
.title,.cm_arr_box{background:none !important;}
.WB_msg_type:hover{background:rgba(255,255,255,.2) !important;}
.notes,.W_tips,.t_lk,.W_btn_c,.PRF_feed_list_more,.p_tit_bar{background:-moz-linear-gradient(top,rgba(255,255,255,.5)10%,rgba(255,225,255,.1)80%,rgba(255,255,255,.3)100%) !important;}
.t_lk{margin:2px !important;text-decoration:none !important;}
.notes:hover,.W_tips:hover,.t_lk:hover,.W_btn_c:hover,.PRF_feed_list_more:hover{background:-moz-linear-gradient(top,rgba(255,255,255,.5)10%,rgba(255,125,255,.2)80%,rgba(255,255,255,.3)100%) !important;}
.WB_media_expand,.WB_feed_togethe{background:rgba(255,255,255,0.3) !important;}
.WB_media_expand{box-shadow:0px 0px 2px rgba(20,15,55,0.6) !important;}

/*input*/
.W_input{margin:2px !important;background:none !important;box-shadow:inset 1px 1px 2px rgba(20,15,55,0.6) !important;}
.input_search,.W_btn_round2{background:none !important;border:1px white solid !important;border-radius:30px !important;box-shadow:inset 1px 1px 2px rgba(20,15,55,0.6) !important;}
.inner,.W_btn_round2 > span:nth-child(1),.group_read_layer{background:none !important;}
.group_read_layer_advance{background:rgba(255,255,255,0.3) !important;box-shadow:0px 0px 2px rgba(20,15,55,0.6) !important;}
.WB_feed_together{border:1px rgba(255,255,255,0.6) solid !important;box-shadow:0px 0px 2px rgba(20,15,55,0.6) !important;}
#pl_content_publisherTop{width:94.5% !important;background:rgba(255,255,255,0.1) !important;border:1px rgba(255,255,255,.6) solid !important;box-shadow: 1px 1px 2px rgba(20,15,55,0.6) !important;margin-left:18px !important;}

/*face photo*/
/*my face*/
img.S_line5{-moz-transition:all .5s linear;position:absolute !important;}
img.S_line5:hover{-moz-transform:rotate(980deg) scale(.01)/*skew(300deg,0deg)*/;-moz-transition:all 1s linear;}
.W_person_info > dt:nth-child(1) > a:nth-child(1){display:block !important;height:100%;background:rgba(22,25,255,.1);-moz-transition:all 1s linear;}
.W_person_info > dt:nth-child(1) > a:nth-child(1):hover{display:block !important;height:100%;background:rgba(220,255,0,.1);-moz-transform:rotate(-720deg);-moz-transition:all 1.5s linear;}
#pl_rightmod_myinfo{background:rgba(255,255,255,.1) !important;border:1px rgba(255,255,255,.6) solid;box-shadow: inset 1px 1px 2px rgba(16,55,25,.6) !important;margin:0 18px 0 2px !important;padding-top:18px !important;}
/*#pl_rightmod_myinfo:hover{box-shadow:-2px -2px 2px rgba(16,55,25,.7) !important;}*/
.W_person_info > dt:nth-child(1){-moz-transition:all 1s linear;border:1px rgba(255,255,255,.6) solid;box-shadow: 1px 1px 2px rgba(16,55,25,.6) !important;padding:0px !important;}
.W_person_info > dt:nth-child(1):hover{border-radius:50px;-moz-transition:all 1s linear;}
#Box_right{top:0 !important;padding-top:28px !important;}

/*others' face*/
.WB_face,.W_face_radius,.WB_face:before,.WB_face:after
{
content:"";
position:absolute !important;
-moz-transition:all 0.25s linear; 
-moz-transform-origin:left!important;
display:block !important;
height:50px !important;
width:50px !important;
background: rgb(255, 355, 255);
}
.W_face_radius,.WB_face:before,.WB_face:after{left:0;top:0;}
.WB_face{z-index:1; }
.W_face_radius{z-index:6;line-height:50px !important;}
.WB_face:before{z-index:3;}
.WB_face:after{z-index:2;}

.WB_face:hover,.WB_face:hover:after,.WB_face:hover:before,.W_face_radius:hover{-moz-transition:all 0.25s linear; box-shadow:0px 0px 2px rgba(20,15,10,0.6);}
.WB_face:hover{-moz-transform: rotate(-3deg);}
.WB_face:hover:after{-moz-transform: rotate(-23deg) translate(-8px, 8px);}  
.WB_face:hover:before{-moz-transform: rotate(-13deg) translate(-4px, 4px);}
.W_face_radius:hover{-moz-transform: rotate(-3deg); }

/*little face*/
.WB_detail{margin-left:60px !important;}
.W_face_radius img:nth-child(1){width:50px !important;height:50px !important;}


/*my page*/
/*top*/
.profile_pic_top:after{content:"";width:980px;height:300px;position:absolute;z-index:2;}
.pf_head,.pf_badge_icon,.enter{position:absolute !important;z-index:3 !important;}
.layer_menulist_tags{background:rgba(255,255,255,0.9) !important;}
.profile_pic_top:hover:after{background:rgba(255,255,255,.1) !important;}
.W_main_2r{position:relative !important;width:250px !important;}

/*nav*/
#Pl_Core_Nav__2{background:-moz-linear-gradient(left,rgba(255,255,255,0.3) 30%,rgba(255,255,255,0.2) 60%,rgba(253,255,248,0.2) 100%)!important;}
#Pl_Core_Nav__2,.pftb_ul,.pftb_itm{height:41px !important;}
.pftb_lk{ border:none !important;height:30px !important;line-height:20px !important;margin-right:2px !important;background:-moz-linear-gradient(top,rgba(255,255,255,.1)10%,rgba(195,125,255,.1)60%,rgba(255,255,255,.1)100%) !important;}

#Pl_Core_RightUserGrid__7 > div:nth-child(1),#Pl_Core_RightPicMulti__8 > div:nth-child(1){box-shadow:0px 0px 2px rgba(20,15,55,.6) !important;background:rgba(255,255,255,0.3) !important;}

/*go to top*/
#base_scrollToTop{background:rgba(255,255,255,.4) !important;border:none !important;}
/*page number*/


.W_main_bg,.form_follow_wrp{background:none !important;}
/*附加*/
.bagde_list{width:220px !important;}
.PRF_W_wrapr{position:absolute !important;top:27px !important;width:97% !important;}
.group_read .sort{border:none !important;}
.W_btn_v{overflow:visible !important;}
.send_btn span{display:none !important;}
    #pl_leftnav_app{display:none !important;}
}
