css:
@-moz-document regexp("^https?:\\/\\/(.*?)dobreprogramy.pl/?(#(.*?))?$"), regexp("https?:\\/\\/(.*?)dobreprogramy.pl(.*?)News(.*?)"), regexp("https?:\\/\\/(.*?)dobreprogramy.pl/Aktualnosci(.*?)?") {
    .main {
        width: 1263px !important;
    }

    .c-small .main {
        width: 1024px !important;
    }

    .actionbar {
        background: inherit !important;
    }

    #sidebar,#phContent_sidebar {
        padding: 10px !important;
    }

    #page-content .span-8 {
        width: 796px !important;
        float: right !important;
        margin-right: 40px !important;
        padding: 10px !important;
        margin-top: -10px;
        padding-left: 0 !important;
    }

    .c-small #page-content .span-8 {
        width: 590px !important;
        margin-right: 0px !important;
    }

    #komentarze {
        float: right !important;
    }

    #sidebar {
        width: 212px !important;
        background: url(http://i.imgur.com/2ibRItH.png) !important;
        margin-left: 2px !important;
        font-size: 1em!important;
        border-top: #a8bbca 1px solid !important;
        border-radius: 7px !important;
        margin-top: -37px;
        margin-top: 2px;
    }

    #sidebar .list-item {
        line-height: 1 !important;
        font-size: 0.9em !important;
    }

    #sidebar .nav-tabs li a {
        font-size: 0.7em !important;
    }

    #sidebar .nav-tabs li {
        font-size: 0.9em !important;
        margin-left: 2px !important;
    }

    #sidebar .span-3 {
        width: 140px !important;
    }

    #sidebar .span-3 h4 {
        font-size: 0.8em !important;
    }

    #sidebar span.table-row a.text-h4 {
        font-size: 0.8em !important;
    }

    #sidebar .span-3 .span-1.split-info {
        display: none !important;
    }

    #sidebar h3 {
        font-size: 1.3em !important;
    }

    #sidebar-2 {
        margin-top: -110px;
        width: 203px !important;
        background: url(http://i.imgur.com/0RfYhWF.png) !important;
        float: left !important;
        position: relative !important;
        border-radius: 7px !important;
        border-bottom: #a8bbca 1px solid !important;
    }

    #sidebar-2:before, .c-t {
        content: "OSTATNIO NA FORUM" !important;
        display: block !important;
        margin-top: -20px;
        height: 22px !important;
        background: url(http://i.imgur.com/aUHYyjz.gif) !important;
        padding-top: 3px !important;
        text-align: center !important;
    }

    #sidebar-2 ul {
        width: 179px !important;
        list-style-image: url(http://i.imgur.com/izj0x6e.png) !important;
    }

    #sidebar-2 ul li a {
        font-size: 11px !important;
        color: #1c6000 !important;
    }

    #sidebar-2 ul li {
        line-height: 1.25 !important;
        margin-bottom: 8px !important;
    }

    #page-content .span-8 .clearfix .span-6 {
        width: 800px !important;
        text-align: justify !important;
    }

    .c-small #page-content .span-8 .clearfix .span-6 {
        width: 554px !important;
    }

    body {
        overflow-x: hidden;
    }

    .page-title span {
        font-size: 22px !important;
        font-family: Verdana,Arial,Helvetica,sans-serif !important;
    }

    #subs {
        background: url('http://i.imgur.com/Z7ZoQsg.gif') !important;
        padding-left: 20px !important;
        padding-top: 86px !important;
        height: 140px !important;
    }

    #subs a {
        background-image: url('http://i.imgur.com/EZIYgwI.jpg') !important;
        display: block !important;
        float: left !important;
        height: 43px !important;
        width: 38px !important;
    }

    #subs a.rss {
        background-position: -5px -7px !important;
    }

    #subs a.fb {
        background-position: -50px -7px !important;
    }

    #subs a.twitter {
        background-position: -93px -6px !important;
    }

    #subs a.newsletter {
        background-position: -132px -3px !important;
    }

    h4 a {
        font-size: 22px !important;
        font-family: Verdana,Arial,Helvetica,sans-serif !important;
        color: #e4680d!important;
    }

    .clearfix.gallery-group {
        margin: 10px 10px 10px 0 !important;
    }

    .clearfix.gallery-group img {
        width: auto !important;
    }

    body {
        padding-top: 0 !important;
    }

    #c-top .top_1 {
        height: 22px !important;
        background: url(http://i.imgur.com/5ga0VQY.png) !important;
        text-align: right !important;
    }

    #c-top .top_1 a {
        color: darkgreen !important;
        display: inline-block !important;
        margin-right: 10px !important;
    }

    #c-top .top_2 {
        height: 112px !important;
        background: white !important;
    }

    #c-top .top_2 .logo {
        background: url(http://i.imgur.com/rSB91j2.png) !important;
        height: 112px !important;
        width: 137px !important;
        float: left !important;
    }

    #c-top .top_2 .space {
        background: url(http://i.imgur.com/NGsUge1.png) !important;
        height: 112px !important;
        width: 758px !important;
        float: left !important;
    }

    #c-top .top_2 .rightSpace {
        background: url(http://i.imgur.com/JZ4CK2I.png) -244px -141px !important;
        height: 112px !important;
        width: 7px !important;
        float: right !important;
    }

    #c-top .top_3 {
        clear: both !important;
        background: url(http://i.imgur.com/yiUlCAJ.png) !important;
        height: 44px !important;
        width: 100% !important;
    }

    #c-top .top_3 .logo {
        width: 160px !important;
        float: left !important;
        height: 44px !important;
        background: url(http://i.imgur.com/ELXvy7A.png) !important;
    }

    #c-top .top_3 .dp {
        float: left !important;
        width: 159px !important;
        height: 44px !important;
        background: url(http://i.imgur.com/TOD3W3W.png) !important;
    }

    #c-top .top_3 .belka {
        float: left !important;
        width: 879px !important;
        height: 44px !important;
        background: no-repeat url(http://i.imgur.com/n2QRBYi.png) !important;
    }

    #c-top .top_3 .belka #scroll,#c-top .top_3 .belka input {
        background: #FFF !important;
        box-shadow: inset 0 1px 5px 2px silver !important;
        border-radius: 10px !important;
        width: 450px !important;
        height: 23px !important;
        border: 1px solid #227c22 !important;
        margin-left: 100px !important;
        margin-top: 10px !important;
        white-space: nowrap !important;
        overflow: hidden !important;
    }

    #c-top .top_3 .belka input {
        margin-left: 170px !important;
        width: 150px !important;
        height: 19px !important;
    }

    #c-top .top_3 .belka #scroll a {
        color: #2a6a04 !important;
        font-size: 10px !important;
        margin-right: 20px !important;
        display: inline-block !important;
    }

    #c-top .top_4 {
        height: 35px !important;
        background: url(http://wayback.archive.org/web/20130102122521/http://static.dpcdn.pl/css/Green/Images/menu_bkg_long.png) !important;
        width: 100% !important;
    }

    #c-top .top_4 .logo {
        float: left !important;
        background: url(http://i.imgur.com/Gqi8Oh2.png) !important;
        width: 203px !important;
        height: 35px !important;
    }

    #c-top .top_4 .menu {
        float: left !important;
        background: url(http://i.imgur.com/aqxJ5WK.png) no-repeat !important;
        height: 35px !important;
        width: 1044px !important;
    }

    #c-top .top_4 .sep {
        display: block !important;
        background: url(http://i.imgur.com/WTiOHyu.png) !important;
        width: 9px !important;
        height: 35px !important;
        float: left !important;
    }

    #c-top .top_4 .end {
        display: block !important;
        background: url(http://i.imgur.com/depP9HB.png) !important;
        width: 7px !important;
        height: 35px !important;
        float: right !important;
    }

    #hoz-menu {
        list-style-type: none !important;
        margin-top: 6px !important;
    }

    #hoz-menu li {
        float: left !important;
        margin-right: 80px !important;
    }

    #hoz-menu a {
        font-size: 1.2em !important;
        color: #747a99 !important;
    }

    #hoz-menu a:hover {
        color: #f59b45 !important;
        text-decoration: none !important;
    }

    #c-top .top_5 {
        width: 100% !important;
        height: 124px !important;
        padding-left: 200px !important;
    }

    .top_5 .c-box {
        background: url(http://i.imgur.com/VUGccxm.png) !important;
        width: 212px !important;
        float: left !important;
        height: 124px !important;
        position: relative !important;
        overflow: hidden !important;
        border-radius: 7px !important;
    }

    .c-box h6 {
        position: absolute !important;
        color: #abf741 !important;
        font-size: 1.1em !important;
        font-family: Arial, Calibri, Tahoma, sans-serif !important;
        left: 10px !important;
        top: 7px !important;
        text-shadow: 1px 1px 1px rgb(3, 71, 30) !important;
    }

    .c-box img {
        background: #FFF !important;
        border-radius: 10px !important;
        top: 40px !important;
        position: absolute !important;
        box-shadow: inset 0 3px 5px 2px silver !important;
        right: 10px !important;
        border: 1px solid #71d371 !important;
        padding: 10px !important;
    }

    .c-box p {
        left: 10px !important;
        position: absolute !important;
        top: 23px !important;
        width: 120px;
        line-height: 1 !important;
    }

    .c-box p.c-no1 {
        border-top: 1px solid green !important;
        top: 60px !important;
    }

    .c-box p a {
        color: #FFF !important;
        font-size: .75em !important;
    }

    .c-box p a:hover {
        text-decoration: underline !important;
    }

    .c-box .przewijanie {
        position: absolute !important;
        left: 0;
        bottom: 0 !important;
    }

    .c-box .przewijanie div {
        float: left !important;
        cursor: pointer !important;
        color: #FFF !important;
    }

    .c-box .przewijanie div.disabled {
        cursor: auto !important;
        color: green !important;
    }

    .c-box .c-scroll {
        overflow: hidden !important;
        position: absolute !important;
        top: 30px !important;
        width: 1200px !important;
        left: 0;
        -webkit-transition: all .3s ease-in !important;
        -moz-transition: all .3s ease-in !important;
        transition: all .3s ease-in !important;
    }

    .c-box .c-scroll .strona {
        position: relative !important;
        width: 190px;
        margin-left: 10px !important;
        margin-right: 10px !important;
        height: 80px !important;
        float: left !important;
    }

    .c-box .c-scroll .strona img {
        top: 6px !important;
        width: 70px;
        padding: 0 !important;
    }

    .c-box .c-scroll .strona p {
        top: -7px !important;
        left: 0 !important;
        width: 100px;
        line-height: 1em !important;
    }

    .c-box .c-scroll .strona p small {
        display: block !important;
        color: #abf741 !important;
        font-size: .6em !important;
    }

    .go-down {
        background: url(http://i.imgur.com/l0L4yCr.png) !important;
        width: 17px;
        height: 34px !important;
        float: left !important;
        margin-top: 4px !important;
        margin-right: 10px !important;
    }

    header h1.font-serif {
        margin-top: -20px !important;
        line-height: 0.6 !important;
    }

    header#c-top {
        width: 1263px !important;
        margin: auto !important;
    }

    .c-small header#c-top {
        width: 1024px !important;
    }

    .c-small #bestprog {
        display: none;
    }

    .c-small #c-top .belka {
        width: 704px !important;
    }

    .c-small #c-top .belka input {
        width: 125px !important;
        margin-left: 8px !important;
    }

    .c-small #c-top .top_4 .menu {
        width: 804px !important;
    }

    .c-small #hoz-menu li {
        margin-right: 57px !important;
    }

    .c-small .top_5 .c-box {
        width: 203px !important;
    }

    #phContent_HomeShowcaseControl_topdiv,#news_ticker,.nested-grid .span-2,#header {
        display: none !important;
    }

    #c-top .top_3 .belka form,#c-top .top_3 .belka #scroll,#c-top .top_3 .belka form {
        float: left !important;
    }

    #phContent_divMetaBody p {
        text-align: justify !important;
    }

<!    -- gallery fix -->
div.gallery-group {
        float: none !important;
        margin: inherit;
        display: inherit;
    }

    div.gallery-group div.image-border {
        padding: 0 !important;
        border: 0 !important;
        background: none !important;
    }

    div.gallery-group div.image-border a {
        max-width: 358px;
        margin: auto;
        background: #f1f1f1;
        padding: 11px;
        border: 1px solid lightgray;
    }

<!    -- -->
section.block ins {
        height: 130px !important;
        width: 187px !important;
        overflow: hidden;
    }

    section.block ins  #aswift_0_anchor {
        height: 130px  !important;
        width: 187px !important;
    };
}
