css:
@-moz-document domain("youtube.com") {
#yt-hitchhiker-feedback, #sb-button-notify, .guide-module-toggle-label, #yt-masthead-user-displayname, .yt-masthead-user-icon, #masthead-upload-button-group, #watch-description-toggle, .related-video-featured {
    display             : none !important;
}
.yt-subscription-button-subscriber-count-unbranded-vertical, .yt-subscription-button, #yt-admin-content, #yt-admin-content :not(.yt-uix-form-input-select-arrow):not(img), #watch7-content :not(.yt-uix-button), #guide-main *, #masthead-search *, #masthead-upload-button-group > *, #footer-hh-container, #footer-hh-container *, #insight #contentTable, #insight #contentTable *, .guide-context-item.context-playing a, .guide-context-item a:hover, .context-data-container *, .home.branded-page-v2-detached-top .branded-page-v2-primary-col {
    border-color        : rgba(155, 155, 155, 0.1) !important;
}

a, a b, .comment a, .video-list-item .title, #watch-description a {
color: #66CD00 !important;
}

.subscription-item.odd, #subscription-manager, .placeholder.channel-module h3, .sorterbar th, .m_highlight, .message.m_highlight td, .album-row-odd, #guide-reminders ul, #masthead-subnav ul, .channel-horizontal-menu, .yt-uix-button, .tile, #watch-actions-area, .feed-item-title, .starting-box-inner, .google-checkout, .vm-video-list li.selected, #playlist-editor-wide-input-section, .context-body, .context-link, .yt-tile-static, .video-list-item a:hover, .menu-item a:hover, .playlist-video-item a:hover, .comment, .yt-tile-visible, .yt-tile-default, .tile, .context-back-link, #yt-admin-sidebar a, .watch-actions-stats .stats-box, tr.ab-users:nth-child(2n), .dashboard-widget, .yt-subscription-button-subscriber-count-unbranded-vertical, .yt-subscription-button-subscriber-count-branded-vertical, .yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal, #masthead-expanded-container, .search-field-container, #subscription-manager-container .even td, .lohp-shelf-cell-container:hover {
background-color: rgba(204, 204, 204, 0.02) !important;
background-image: none !important;
}

#feed {
background: linear-gradient(90deg, black, black 650px) !important;
border-right: black 0px solid !important;
border-left: rgba(155, 155, 155, 0.1) 1px solid !important;
}

.feed-header {
margin-top: 10px;
padding: 0 20px;
border-bottom: 0px solid #E2E2E2;
}


a, abbr, acronym, address, applet, b, big, blockquote, button, canvas, caption, center, cite, code, dd, del, dfn, div, dl, dt, em, embed, fieldset, font, form, h1, h2, h3, h4, h5, h6, hr, html, i, iframe, img, ins, kbd, label, legend, li, menu, object, ol, p, pre, q, s, samp, small, span, strike, strong, sub, sup, table, tbody, td, tfoot, th, thead, tr, tt, u, ul, var  {
font-size: 99%;
}

.yt-tile-default h3 a:visited, .yt-tile-visible h3 a:visited, a:visited, a:visited b, a:visited font, a:visited span {
color: #A8A8A8 !important;
}

.yt-tile-default h3 a:visited, .yt-tile-visible h3 a:visited, a:visited, a:visited b, a:visited font, a:visited span {
color: #71834C !important;
}

.epic-nav-dropdown-group:hover, body a.yt-uix-button-epic-nav-item:hover, body a.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item-heading {
height: 29px;
line-height: 29px;
vertical-align: bottom;
color: #333;
border-bottom: 3px solid;
border-color: #66CD00;
padding-bottom: 0;
display: inline-block;
}

.guide-channels-content ::-webkit-scrollbar, .guide-module-content ::-webkit-scrollbar {
width: 1px;
height: 9px;
}

.primary-col {
width: 990px;
background-color: rgba(87, 87, 87, 0.1);
border: 0px solid #E2E2E2;
border-top: 0px solid #E2E2E2;
border-bottom: 0px solid #E2E2E2;
-moz-border-radius: 0 0 3px 3px;
-webkit-border-radius: 0 0 3px 3px;
border-radius: 0 0 3px 3px;
display: inline-block;
}

.yt-lockup2:hover {
background: rgba(173, 173, 173, 0.14);
}

#filter-dropdown {
background-color: rgba(236, 236, 236, 0.08);
}

.yt-uix-expander-head {
letter-spacing: -0.04em;
cursor: pointer;
font-size: 16px;
}

#watch-description-expand, #watch-description-collapse, .yt-uix-expander-head {
border-top: 0px solid #555 !important;
}

#watch7-video-container, .watch7-playlist {
padding-top: 0px;
}

.exp-new-site-width #yt-masthead {
width: 960px;
}

#yt-masthead #logo-container {
position: relative;
margin-left: 9px;
margin-right: 15px;
margin-top: 1px;
}

.watch-view-count {
font-size: 18px;
line-height: 24px;
}

#watch7-secondary-actions {
margin-top: 18px;
}

#watch-headline-title {
padding-top: 0px !important;
}

body #footer-hh-container {
background-color: none!important;
border-top: 1px solid #E2E2E2;
}

#watch7-secondary-actions {
margin-top: -35px!important;
}

#watch7-secondary-actions .yt-uix-button.yt-uix-button-toggled {
    border-bottom-color: #66CD00!important;
    }
#watch7-secondary-actions .yt-uix-button:hover {
border-bottom-color: #408100!important;
}

#yt-masthead-dropdown {
top: 10px!important;
}
#masthead-search {
width: 70%!important;
}
#yt-masthead-content {
bottom: 50px !important;
max-width: 840px;
}

#yt-masthead-container {
padding: 1px 150px 1px 100px !important;
}

.ytcenter-site-watch.ytcenter-site-center #player {
top: -50px !important;
}

.ytcenter-site-watch.ytcenter-site-center.sidebar-expanded .watch-wide #watch7-sidebar, .ytcenter-site-watch.ytcenter-site-center .watch-playlist #watch7-sidebar, .ytcenter-site-watch.ytcenter-site-center.sidebar-collapsed .watch-playlist #watch7-sidebar, .ytcenter-site-watch.ytcenter-site-center .watch-branded #watch7-sidebar {
    padding-top: 14px !important;
}

.yt-uix-button-default, .yt-uix-button-subscription, .yt-uix-button-panel:hover .yt-uix-button-text, body .yt-uix-button-default[disabled] {
border-color: rgba(111, 111, 111, 0.2)!important;
}
.yt-uix-button-context-light .yt-uix-button-subscribed-branded, .yt-uix-button-context-light .yt-uix-button-subscribed-unbranded {
    border-color: rgba(225, 225, 225,0.2)!important;
    }
.yt-uix-button-subscribe-branded, .yt-uix-button-subscribed-branded {
    height: 24px;
    border: 1px solid rgba(204, 204, 204,0.2)!important;
    }
body {
background: url("http://static.tumblr.com/c343e5e9653508facd207de9eb902bbe/jyrhahv/Niymjeomn/tumblr_static_grainpattern222.jpg") !important;
}

#channel-subscription-promo-in-feed {
display:none !important;
}

.yt-lockup2 .yt-lockup2-content {
    overflow: visible !important;
}

.primary-col {
    background-color: rgba(255, 255, 255, 0.0) !important;
    border-color: -moz-use-text-color rgba(226, 226, 226, 0.1) rgba(226, 226, 226, 0.1) !important;
    }
    
    .search-header {
    border-bottom: 1px solid rgba(226, 226, 226, 0.1) !important;
}
    
    .yt-lockup2.has-hover-effects:hover {
    background: none repeat scroll 0% 0% rgba(246, 246, 246, 0.01) !important;
}

.result-list .result-item-padding {
    padding: 0px 0px !important;
}

======#watch7-sidebar {
border-bottom: 1px solid rgba(226, 226, 226, 0.1) !important;
border-right: 1px solid rgba(226, 226, 226, 0.1) !important;
}

.video-list-item a {
    padding: 0px 0px !important;
    }
    
    .video-list-item {
    margin-bottom: 5px !important;
}

#watch-description {
    line-height: 16px !important;
    }
#footer-logo img {
display:none!important;
}

#footer-main {
    border-bottom: 1px solid rgba(226, 226, 226, 0.1) !important;
    }
    
    #page.watch, #page.watch #content, .exp-new-site-width #page.watch, .exp-new-site-width #page.watch #guide + #content {
    padding-bottom: 0px !important;
}

.feed-list li:first-child .feed-list-item, .feed-list li.feed-list-item:first-child {
    padding-top: 0px !important;
}
.feed-item-container .feed-item-main {
    overflow: visible !important;
    }

.action-panel-trigger 
{
border-width: 0px !important; height: 30px !important;
border-radius: 2px !important; border: 1px solid rgba(255,255,255,0.02) !important; 
background-image: -moz-linear-gradient(center top , #333333, #000000) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#333333), to(#000000)) !important;
}
.watch7-playlist {
    padding-left: 0px !important;
}
#yt-masthead #logo-container {
    margin-left: 58px !important;
    margin-right: 99px !important;
    margin-top: 2px !important;
}
#yt-masthead #logo {
margin-top: 0px !important;
width: 71px !important;
height: 27px !important;
}
#eow-title {
font-size: 20px !important;
font-weight: normal !important;
color: #66cd00 !important;
cursor: default !important;
}

#guide-main .guide-module-toggle:hover .guide-module-toggle-icon img {
    background: url(data:image/png;base64,)!important;
    width: 18px;
    height: 18px;
}}

.share-group {
display:none!important;
}

#watch7-secondary-actions {
margin-top: 3px !important;
}
