css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbs.themex.net") {

body {
	background:#e7e7e7 !important;
}
a,a:visited, body_avisited,a:link, body_alink{
  	color:#666 !important;
}
a:hover{
  	color:#1aad9f !important;
}
#shad-t-r,#shad-t-l,#shad-t {
	display:none !important;
}
#shad-th-r {
	padding:0px !important;
	background:none !important;
}
#shad-th-l {
	padding:0px !important;
	min-height:40px !important;
	background:none !important;
}
#header-tb-r,#header-tb-l {
	display:none !important;
}
#header-tb {
	margin:0px !important;
	height:44px !important;
	background:#33373b !important;
}
#widget-r,#widget-l {
	display:none !important;
}

#tb-caption {
	display:none !important;
}
#tb-nav {
	margin:0 auto !important;
	width:1200px !important;
	height:40px !important;
}
#tb-nav #tb-icons li {
	line-height:40px !important;
    margin: 0 0 0 -1px !important;;
}
#tb-nav #tb-icons li a {
	padding: 2px 10px !important;
    color: #999 !important;
    line-height: 40px !important;
    font-weight: bold !important;
}
#tb-nav #tb-icons li a:hover {
	color:#1aad9f !important;
}
#tb-nav #tb-icons li a img {
	display:none !important;
}
#tb-nav #tb-icons li a br {
	display:none !important;
}
#tb-nav #tb-icons li#tb-logo {
	padding: 0 0 0 4px !important;;
    background: url(data:image/png;base64,) no-repeat 8px 4px !important;
}

ul#tb-icons #tb-logo a{
	width: 137px !important;
	height: 40px !important;
}

#tb-register{
	width:72px !important;
	height: 40px !important;
	border-right: 1px dotted #555 !important;}

#tb-profile{
	width:72px !important;
	height: 40px !important;
	border-right: 1px dotted #555 !important;}

#tb-faq{
	width:72px !important;
	height: 40px !important;
	border-right: 1px dotted #555 !important;}

#tb-community{
	width:72px !important;
	height: 40px !important;
	border-right: 1px dotted #555 !important;}

#tb-calendar{
	width:72px !important;
	height: 40px !important;
	border-right: 1px dotted #555 !important;}

#tb-newposts{
	width:72px !important;
	height: 40px !important;
	border-right: 1px dotted #555 !important;}

#tb-search{
	width:72px !important;
	height: 40px !important;
	border-right: 1px dotted #555 !important;}

#tb-quicklinks{
	width:72px !important;
	height: 40px !important;
	border-right: 1px dotted #555 !important;}

#tb-logout{
	width:72px !important;
	height:40px !important;}

#tb-searchdiv {
	padding:2px !important;
	width:280px !important;
    font-weight: bold !important;
}
#tb-searchbox {
	float:left !important;
	padding:0px !important;
	width:194px !important;
	height:40px !important;
	background:none !important;
}
#tb-searchbox input {
    float: left !important;
    margin: 7px 0px 0px 20px !important;
    padding: 4px 7px !important;
    width: 160px !important;
    height: 17px !important;
    outline: 0 !important;
    border: none !important;
    border-radius: 2px 0px 0px 2px !important;
    color: #999 !important;
}
#tb-searchdiv a {
	float: left !important;
    margin-top: 7px !important;
    padding: 0 10px !important;
    height: 25px !important;
    min-width: 36px !important;
    border: none !important;
    border-radius: 0 2px 2px 0 !important;
    background: #1aad9f !important;
    color: #FFF !important;
    text-align: center !important;
    font-weight: normal !important;
    line-height: 24px !important;
    cursor: pointer !important;
    _overflow: hidden !important;
}

#tb-searchdiv a:hover{
	background: #33bfb2 !important;}


#page-body {
	border-top:none !important;
  	background:#dcdcdc !important;
  	width: 1200px;
  	margin: 0px auto;
}


#breadcrumb table {
	float: right;
	margin:0px auto !important;
	width:300px !important;
}
#page-body>table {
	margin:0px auto !important;
	width:1200px !important;
	border:1px solid #e7e7e7 !important;
	border-top:none !important;
  	border-bottom:none !important;
}

.bleft {
	border-left:1px solid #e7e7e7 !important;
	color: #666;
}

.tool_search img{
	margin-top: 10px !important;
	width: 25px !important;
	height: 13px !important;  
	background: url(data:image/png;base64,) no-repeat 0 -15px !important;
}
.tool_forum img{
	margin-top: 10px !important;
	width: 25px  !important;
	height: 13px !important; 
	background: url(data:image/png;base64,) no-repeat 0 0 !important;
}

.tool_rate img{
	margin-top: 10px !important;
	width: 25px  !important;
	height: 13px !important; 
	background: url(data:image/png;base64,) no-repeat 0 -30px !important;
}

.tcat a:first-child {
	margin-top:5px !important;
	padding-left:2px !important;
	background:#1aad9f !important;
}

.tcat {
	border-bottom:1px solid #e7e7e7 !important;
    border-top:5px solid #e7e7e7 !important;
	color:#666 !important;
	text-shadow:none !important;
	line-height:25px !important;
	background: #eee !important;
	text-align: left !important;
}

.aquag {
	display:none !important;
	background:none !important;
}

.tcat a {
	color:#555 !important;
	font-weight:bold !important;
	font-size:12px !important;
	line-height:25px !important;
}


#shad-b-r,#shad-b-l,#shad-b {
	display:none !important;
}

#page-body table > tbody:nth-child(n+20) {
	display:none !important;
}
#page-body > .pad4sides {
	display:none !important;
}
.alt2 {
	background:#FAFAFA !important;
}
#page-body > form{
	width:1200px !important;
	margin:0px auto !important;
	background: #FBFBFB !important;
	color:#999 !important;
}
#page-body > form >.pad4sides{
  	padding: 30px 5px 10px 5px !important;
	background: #333 !important;
	margin-top: 5px  !important;
	border: 1px solid #dcdcdc !important;
}
#fb-nav{
  	display:none !important;
}

#breadcrumb {
	height: 38px !important;
	padding:10px 0px !important;
	border-bottom:1px solid #e7e7e7 !important;
	background:#FBFBFB !important;
}



#breadcrumb_self,#breadcrumb_start{
  	background: url(data:image/png;base64,) no-repeat !important;
	padding: 0px 0 0px 26px !important;
	margin: 3px 160px 0 10px !important;
	line-height: 14px !important;
	width: 800px !important;
}

#breadcrumb_end{
  	background:url(data:image/png;base64,) no-repeat  !important;
  	margin: 5px 0px 0px 30px !important;
  	padding: 2px 0 2px 34px !important;
}

.pad4all{
  	border:1px solid #e7e7e7 !important;
  	background:#FCFCFC !important;
  	margin-top: -1px !important;
}
.bbot{
  	border-top:none !important;
  	border-bottom: 1px solid #e7e7e7;
  	height: 40px !important;
}


#page-body > div.pad4all{
  	width:1198px !important;
  	margin:0px auto !important;
  	padding:0px !important;
  	border-top:none !important;
}
#page-body > div.pad4all table{
  	padding:4px !important;
}
#posts{
  	width:1198px !important;
  	margin:0px auto !important;
  	border: 1px solid #e7e7e7 !important;
	border-top: none !important;
	border-bottom: none !important;
}
.sig{
    display:none !important;
}
#posts .alt1{
 	padding:0px  !important;
}
.bbot2{
  	padding:8px 8px 7px 10px  !important;
  	border-color:#e7e7e7 !important;
    background:#FBFBFB !important;
    color: #666 !important;
}
.post{
    padding:10px !important;
}

font[color*="blue"]{
    color: #1aad9f !important;
}
.postbit.bright{
  	background:#FFF !important;
    padding:0px !important;
    border-color:#e7e7e7 !important;
    position: relative !important;
}
.postbit.bright > div:last-child
{
position: absolute !important;
top: 0px !important;
left: 175px !important;
background: #FFFDFD !important;
width: 172px !important;
box-shadow: 2px 2px 5px #e7e7e7;
display: none;
}

.postbit.bright > div:first-child{
  	border-bottom: 0px solid #e7e7e7 !important;
	height: 29px !important;
	padding: 0px  !important;
	line-height: 29px !important;
  	background:#FFF !important;
}
.postbit.bright > div.avatar{
    margin:14px 0px !important;
  }


.avatar img{border: 1px solid #E7E7E7;
border-radius: 100px;
width: 100px;
height: 100px;
box-shadow: 0px 2px 5px #e7e7e7;
padding: 5px;
background: #ffffff;}


.postbit.bright:hover > div:last-child{
       display:block;
}
.postsep.btop.bbot{
	width: 1196px !important;
	padding: 0px !important;
	height: 1px !important;
	background: #E7E7E7 !important;
	border-bottom: 5px solid #DCDCDC !important;
}
.postsep.btop.bbot img{
 	display:none !important;
}
div[id*="post_thanks_box"] table tr td > div:last-child{
	padding:10px  !important;
}
#page-body > form[name="vbform"]{
  	width:1198px !important;
  	border:1px solid #e7e7e7 !important;
  	margin:-1px auto 0px auto  !important;
  	background:#FFF !important;
}
a.bnewthread,a.bnewreply{
	display: inline-block !important;
	border: 1px solid #CCC !important;
	border-radius: 2px !important;
	font-weight: bold !important;
	background-repeat: no-repeat !important;
	background: #F7F7F7 url(data:image/png;base64,) no-repeat  -47px -121px !important;
	color: #666 !important;
	width:110px !important;
	height:auto !important;
	padding-top: 3px  !important;
}
a.bnewreply{
  	width:75px !important;
}

.button{
color: #fff !important;
background: #1aad9f !important;
width: 120px !important;
height: 30px !important;
border: 0px solid #ccc !important;
margin: 5px !important;
border-radius:2px !important;
}

.button:hover{
background: #33bfb2 !important;
}

a.bnewthread span,a.bnewreply span{
  	padding: 3px 10px !important;
	margin-left: 25px !important;
	display: block !important;
	background: #F7F7F7 !important;
  	font-size:13px !important;
}
.tborder{
	background: #F7F7F7 !important;
	color: #666 !important;
	border: none !important;
	border-radius: 2px !important;
}
.tborder tr td{
	border:1px solid #e7e7e7 !important;
	border-right:none !important;
	padding:6px 10px !important;
}
.tborder tr td:last-child{
	border-right:1px solid #e7e7e7 !important;
}
.tborder tr td.vbmenu_control{
	background:#1aad9f !important;
	border:none !important;
	border-radius:2px 0px 0px 2px !important;
}
.alt3 {
	background: #ffe1e1 !important;
	width: 1198px !important;
	margin: 0px auto !important;
	border: 1px solid #e7e7e7 !important;
	padding: 6px 0px  !important;
	margin-top: -1px !important;
}
.dropdown{
  	border-color:#e7e7e7 !important;
    background:#FFF !important;
    border-radius:3px !important;
}
.dropdown dl{
  padding:0px !important;
}
.dropdown dl dt{
  	border-bottom: 1px solid #e7e7e7 !important;
	background: url(data:image/png;base64,) 0px -20px repeat-x !important;
	color: #444 !important;
	text-shadow: none !important;
    border-radius:3px 3px 0px 0px !important;
}
.dropdown dd a:hover{
  	background: #EFEFEF !important;
  	color: #545454 !important;
}
#threadsearch{
  	display:none !important;
}
#posts .alt1>div.btop2{
	height: 30px;
	line-height: 30px;
	padding-top: 0px !important;
}

#posts .alt1>div.btop2>table tr td>a{
	display: inline-block  ;
	height: 30px  ;
	line-height: 30px  ;
	border-top: 0px  ;
	border-bottom: 0px  ;
	margin-top: 0px  ;
}
#posts .alt1>div.btop2>table tr td>a img{
  width:0px ;
  height:0px  ;
  background: url(data:image/png;base64,) no-repeat 0px -1px;
  padding:15px 0px  ;
  -webkit-transition: all 0.1s linear;
}
#posts .alt1>div.btop2>table tr td>a img[src*=quote]{
  background-position:0px -1px ;
  padding:15px 22px 15px 23px ;
  margin-left: -4px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=multiquote]{
  background-position:-45px -1px  ;
  padding:15px 22px 15px 23px  ;
  margin-left: -4px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=multiquote_on]{
  background-position:-45px -32px  ;
  padding: 15px 22px 15px 23px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=quickreply]{
  background-position: -90px -1px;
  padding: 15px 22px 15px 23px;
  margin-left: -4px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=thanks]{
  background-position:-135px -1px  ;
  padding: 15px 22px 15px 23px;
  margin-left: -4px;
}
#posts .alt1>div.btop2>table tr td>a img[src*=edit]{
  background-position:-180px -1px;
  padding: 15px 22px 15px 23px;
}
#posts .alt1>div.btop2>table tr td>a:hover img{
  background-position-y: -32px;
  -webkit-transition: all 0.2s linear;
}
#threadslist .alt3{
  display:none ;
}



#usercp-nav {
background:#fff  !important;}


#usercp-nav dl dt {
margin: 0;
padding: 10px 0px 6px 20px;
color: #666;
font-size: 12px;
font-weight: bold;
text-transform: uppercase;
line-height: 16px;
}


#shad-r,#shad-l {
	padding:0px !important;
	background-image: none;
background-color: #e7e7e7;
}


.thead {
background: #eee;
color: #666 !important;
font-size: 12px;
border-bottom:1px solid #e7e7e7 !important;
    border-top:5px solid #DCDCDC !important;
	text-shadow:none !important;
	line-height:25px !important;
	text-align: left !important;
}

.action_links{
width:80px;
padding:5px 0px 10px 20px }

#page-body > div.pad4all table.btop.bright.bbot.bleft{
	display:none;
}

#page-body > div.pad8all table.btop.bright.bbot.bleft{
	display:none;
}

a[href*="rss"]{
  	display:none !important;
}


#page-body table > tbody[id*=collapseobj_forumbit]>tr>td[valign*=top] {
        display:none !important;
}

.alt2 td[align*="left"] div.smallfont,.alt1 td[align*="left"] div.smallfont{
  	display:none !important;
}

.alt2 td[align*="left"] span.smallfont,.alt1 td[align*="left"] span.smallfont{
	display:none !important;
}


#page-body table > tbody[id*=collapseobj_forumbit]>tr>td {
	border-bottom:1px solid #EAEAEA !important;
	padding:10px 5px 10px 8px  !important;
}

.pad8sides{color: #999;}

.time{color: #999}


#page-body table > tbody>tr>td[valign*=top] img{
  display:none;
}


#posts table[id*=post]{
	position: relative;
}

}
