css:
@-moz-document domain("steamgifts.com") {
p, .discussions .details .title, .discussions .details .title a {
    color: rgb(234, 234, 235) !important;
}
html, body, ul, ol, li, img, h1, h2, h3, h4, h5, h6, form, fieldset {
    background: transparent;
    color: #EAEAEB !important;
    outline: none;
    text-decoration: none;
    font-style: none;
    text-shadow: none !important;
}
body {
    font-family: Helvetica, Arial, sans-serif;
}
hr {
    color: #292a2e;
}
#navigation {
    /*  background: #242424 !important; */
    background-color: transparent;
    color: rgb(234, 234, 235);
}
.sub_navigation {
    background: #222;
    color: #fff;
    border: 1px solid #444;
}
.post {
    border: 1px solid #292a2e !important;
    border-bottom: 1px solid #292a2e !important;
    background: #212121 !important;
}
.pagination {
    background-color: #2a2a2a !important;
    background-image:none !important;
    color: #fff;
    border: 1px solid #444 !important;
}
#navigation .left .search .input input, #navigation .left .search, #navigation .left .search.disabled, a.sale.left, a.sale.right {
    background: #444;
    color: #fff;
    border: none;
    border-radius: 15px 15px 15px 15px;
}
#navigation .left ol li a, #nav .left ol li a {
    color: #fff;
}
.wrapper {
    /*  background: #1c1c1c !important; */
    background-color: #1A1A1A !important;
    background-image: url(http://i.imgur.com/OPdh3Yj.png) !important;
    color: #fff;
}
.post .left .description, .post .left .description .time_remaining, .post .left .description .created_by, .post .left .description .created_by a, .discussions .details {
    color: rgba(0, 194, 255, 0.66);
    padding: 2px 0 2px 2px !important;
    background-image: none !important;
    border: 1px solid transparent !important;
}
.post .left .title, .post .left .title a {
    color: #EAEAEB !important;
    outline: none;
    font-family: Helvetica, Arial, Times New Roman;
    text-shadow: none !important;
}
.post .left .entries a {
    color: #EAEAEB !important;
    font-size: 100%;
}
.recent_winners .row .details, .top_contributors .row .details {
    background: transparent;
    color: #EAEAEB !important;
}
.recent_winners .row.first, .top_contributors .row.first {
    background: #313237;
    color: #EAEAEB !important;
    border: 1px solid #292a2e !important; 
}
.recent_winners .row, .top_contributors .row {
    background: #212225;
    color: #EAEAEB !important;
    border: 1px solid #292a2e !important;
}
.top_contributors .row .gift_value {
    background: transparent;
    color: #EAEAEA !important;
    text-decoration: none;
    font-style: normal;
    outline: none;
    text-shadow: none !important;
    letter-spacing: 1px;
    width: 108px;
    margin-right: 10px;
}
.top_contributors .row .details, .recent_winners a, .top_contributors a, .recent_winners .row .title a, p.title {
    color: rgba(0, 194, 255, 0.66);
}
.gift_count strong {
    color: #EAEAEB !important;
}
.footer {
    background: #111 !important;
    border-radius: 15px 15px 15px 15px;
}
.footer, .footer .footer_column {
    background: transparent;
}
.footer .footer_column h4, .footer .footer_column ul li, .footer .footer_column ul li a {
    text-decoration: none;
    outline: none;
    text-shadow: none;
    color: #fff;
}
.footer .footer_column ul {
    border: none;
}
.featured, .featured .left .featured_img, .featured .left .featured_img, .featured .left .private_img {
    background: transparent;
}
.bg_gradient {
    background: none !important;
    background-image: url(data:image/svg+xml;base64,) !important;
    background-image: -webkit-gradient(radial, 50% 0%, 0, 50% 0%, 196, color-stop(0%, rgb(48, 104, 148)), color-stop(100%, #242424)) !important;
    background-image: -webkit-radial-gradient(center top, farthest-side, #347DB5 -100%, #1a1a1a 100%) !important;
    background-image: -moz-radial-gradient(center top, farthest-side, #347DB5 -100%, #1a1a1a 100%) !important;
    background-image: -ms-radial-gradient(center top, farthest-side, #347DB5 -100%, #1a1a1a 100%) !important;
    background-image: -o-radial-gradient(center top, farthest-side, #347DB5 -100%, #1a1a1a 100%) !important;
    background-image: radial-gradient(farthest-side at center top, #347DB5 -100%, #1a1a1a 100%) !important;
    background-repeat: no-repeat !important;
    background-position: center top !important;
}
.pagination .numbers a.selected {
    color: #f00;
    text-decoration: none;
}
.pagination .numbers a {
    color: #aaf;
    text-decoration: none;
}
#navigation .left ol li a, #navigation .left ol li a {
    -ms-filter:"progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";
    filter: alpha(opacity=100);
    opacity: 1.0 !important;
}
.discussions .body, .comment.child .child_container {
    background: #222 !important;
    color: #ff7 !important;
    border: 1px solid #444 !important;
}
.comment_body textarea {
    background: #444;
    color: #EAEAEB;
}
.discussions .details .title, .discussions .details .title a {
    text-decoration: none;
    text-shadow: none;
    outline: none;
}
.discussions .row {
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #444 !important;
    border-left: 1px solid #444 !important;
    border-right: 1px solid #444 !important;
    background: #222 !important;
    padding-bottom: 12px !important;
}
.discussions a {
    color: #11b2ff !important;
}
.sub_navigation a.button {
    background: #000 !important;
    color: #09F !important;
}
.discussions .body, .comment.child .child_container {
    border-left: 1px solid #444;
    border-right: 1px solid #444;
}
.register .input input {
    background: #444;
    color: #ff7;
}
.invites .row .code, .invites .heading {
    text-decoration: none;
    outline: none;
    text-shadow: none !important;
    color: #fff;
}
.register .input .notification {
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #444 !important;
    border-left: none !important;
    border-right: none !important;
}
.register .input .verify.success {
    background: #333;
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
    border-left: none;
    border-right: none;
    text-decoration: none;
    outline: none;
    text-shadow: none;
}
.date_description strong {
    color: #ff7;
}
.html, html {
    background: #282A34;
}
.create_giveaway .divider {
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #444 !important;
}
.post.fade {
    -ms-filter:"progid:DXImageTransform.Microsoft.Alpha(Opacity=50)";
    filter: alpha(opacity=50);
    opacity: 0.5;
}
.post .right {
    background: #333 !important; 
    border: 1px solid #444 !important;
    border-radius: 5px 5px 5px 5px;
}
div#visualization {
    background: #333 !important;
}
#stats hr {
    border: 1px solid #292a2e;
}
div#gafPopup {
    background: #333 !important;
    border: 1px solid #444 !important;
    color: #ff7 !important;
}
input, textarea, keygen, select, button, isindex {
    background: #444;
    color: #ff7;
}
a {
    color: #EAEAEB;
}
.register .important, .invites .row.first, .invites .divider, .invites .row {
    background: #333;
    text-shadow: none;
    outline: none;
    color: #fff;
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
}
.ui-slider-horizontal {
    background: #333;
    border: 1px solid #444;
}
.ui-slider-horizontal .ui-slider-handle {
    background: #666;
    border: 1px solid #555;
}
.markdown p, p, a {
    text-shadow: none;
    outline: none;
}
.featured, .profile {
    background: #222;
    border: 1px solid #444;
    border-radius: 5px;
}
.comment.parent .body_container .comment_reply, .comment.parent .body_container .comment_reply a {
    color: #f66;
    outline: none;
    text-shadow: none;
}
.comment.child .body_container .comment_reply a {
    color: #F66;
}
.discussions .body, .comment.child .child_container {
    border:none !important;
    border-radius: 1px;
}
.comment.parent {
    background: #222;
}
.comment_container, .parent_container {
    border:none !important;
}
.discussions .row.borderless {
    border: 1px solid #444;
    border-radius: 15px 15px 15px 15px;
}
.sgpRowPage {
    background-color: #2a2a2a !important;
    background-image: none !important;
    color: rgba(0, 194, 255, 0.7) !important;
    font-size: 1em;
    font-weight: bold;
    text-shadow: none;
    border-top: 1px solid #2a2a2a !important;
    border-bottom: 1px solid #2a2a2a !important;
    border-left: 1px solid #2a2a2a !important;
    border-right: 1px solid #2a2a2a !important;
}
#sgpBE_Heading {
    color: #00B8FF !important;
}
.sgpMarkdown a {
    color: #00B8FF !important;
}
.author_container a {
    color: #BDBDBD !important;
}
input[type="submit"] {
    color: #FFF !important;
}
.comment_container .author_name a {
    color: #31c2e1 !important;
}
.comment_container .author_date {
    color: #FFF !important;
}
.comment_container {
    text-shadow: 1px 1px #000 !important;
}
.border_container {
    background-image: url(http://i.imgur.com/OPdh3Yj.png) !important;
    background-color: #1A1A1A;
    border: #1A1A1A !important;
}
.discussions .row.borderless {
    padding-top: 10px !important;
    padding-left: 10px !important;
    border: none;
    margin-bottom: 10px !important;
}
.row.borderless {
    padding-top: 10px !important;
    padding-left: 10px !important;
    border: none;
    margin-bottom: 10px !important;
}
.sgpTileViewInfoRelative {
    background-color: #444 !important;
    color: #00EBFF !important;
    border-left: 1px solid #000 !important;
    border-bottom: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
}
.sgpTileViewInfoRelative a {
    color: #FFF !important;
}
.sgpCheckbox {
    color: #B3B3B3 !important;
}
.sgpFilterControls {
    background-color: #444 !important;
    border: 1px solid rgb(102, 102, 102) !important;
    color: #D5D5D5 !important;
}
.sgpCheckbox.checked {
    color: #00ADFF !important;
}
.contributor_only {
    text-shadow: 1px 1px #000 !important;
    -webkit-box-shadow: 0 0 0 #000 !important;
    box-shadow: 0 0 0 #000 !important;
}
div[style] {
    text-shadow: 1px 1px none !important;
}
.mod_container .username {
    text-shadow: 1px 1px #000 !important;
    color: #007AFF !important;
}
.status {
    color: #FF0000 !important;
}
.mod_container .review_body {
    background: #1A1A1A;
    margin-top: 7px;
    margin-bottom: 7px;
    padding-left: 5px;
}
.submit_form {
    background-image: -webkit-linear-gradient(rgba(0, 85, 151, 1) 0%, rgb(0, 27, 95) 100%) !important;
    background-image: -moz-linear-gradient(rgba(0, 85, 151, 1) 0%, rgb(0, 27, 95) 100%) !important;
    background-image: -o-linear-gradient(rgba(0, 85, 151, 1) 0%, rgb(0, 27, 95) 100%) !important;
    background-image: -ms-linear-gradient(rgba(0, 85, 151, 1) 0%, rgb(0, 27, 95) 100%) !important;
    background-image: linear-gradient(rgba(0, 85, 151, 1) 0%, rgb(0, 27, 95) 100%) !important;
    text-shadow: none !important;
}
.mod_container .review_container textarea {
    background-color: #2e2e2e !important;
}
.no_results, .content .create_giveaway .notification, .create_giveaway .input select, .create_giveaway .input input, .date_description, .create_giveaway .input select, .create_giveaway .input input, .create_giveaway .input textarea {
    background: #333;
    color: #EAEAEB;
    border: 1px solid #444;
    text-decoration: none;
    outline: none;
    text-shadow: none;
}
.or {
    text-shadow: 1px 1px #000 !important;
    color: #00AAFF !important;
}
.featured, .profile {
    padding-right: 5px;
    padding-left: 5px;
}
.profile .row {
    text-shadow: -1px -1px #000 !important;
}
.post .left .description {
    background-color: #292929 !important;
}
.title span[style] {
    color: rgba(0, 153, 255, 0.69) !important;
}
.user_body div[style] {
    background-color: #000 !important;
}
.success strong {
    color: #00ADFF !important;
}
.invites {
    text-shadow: 1px 1px #000 !important;
}
.register .input .title {
    color: rgba(0, 184, 255, 0.65) !important;
}
.invites .row .code, .invites .heading {
    color: #089CFF !important;
    padding-left: 5px;
}
.register .important, .invites .row.first, .invites .divider, .invites .row {
    padding-left: 5px;
}
.content_divider {
    border-bottom: 1px solid #292a2e !important;
    border-top: 1px dotted #000 !important;
}
.sub_navigation {
    border-radius: 5px 5px 0 0;
    background-image:none !important;
    background-color: #1A1A1A !important;
    border-color: #313137 !important;
}
.featured {
    background-color: #212225;
}
.description {
    width: 704px !important;
    margin-left: 10px !important;
}
.entries {
    width: auto;
    margin-left: 10px !important;
}
.title {
    width: auto;
    margin-left: 10px;
}
.rounded .entries {
    width:auto !important;
    margin-left: 0px;
}
.discussions .reply {
    margin-left: 0px !important;
}
.discussions .author_avatar {
    margin-left: 12px;
}
.discussions .reply_avatar {
    margin-right: 13px;
}
.details .title {
    margin-left: 0px;
}
.post .avatar_overlay {
    background-image: url(http://i.imgur.com/u7CxnJy.png) !important;
}
.comment.parent .avatar_overlay {
    background-image: url(http://i.imgur.com/P6JbZRR.png) !important;
}
.row:not(.first) .avatar_overlay, .discussions .row .author_avatar_overlay {
    background-image: url(http://i.imgur.com/JLGzyXp.png) !important;
}
.row .details, .row .first .details {
    margin-left: 0px;
}
.row .avatar, .row .first .avatar {
    margin-left: 10px;
}
.first .avatar_overlay {
    background-image: url(http://i.imgur.com/tVd2Tm8.png) !important;
}
.row .reply_avatar_overlay, .author_container .avatar_overlay {
    background-image: url(http://i.imgur.com/0R0Ip8G.png) !important;
}
.sub_navigation a.button {
    color: rgba(0, 194, 255, 0.66);
    background-color: #222 !important;
}
.sgpFloatDownMenu {
    background-image: none !important;
    background-color: #242424 !important;
}
.child_container div span {
    text-shadow: none !important;
}
label[for=body], label[for=title], label[for=contributor], label[for=private], label[for=date], label[for=copies], label[for=game_id], span .private, .row .title {
    text-shadow: none !important;
    color: #EAEAEB !important;
}
.post { margin-right: 0px !important;}
}
.post .center { margin-right: 6px !important;}
}
