css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.purepc.pl") {

.node p, p { /* wyrownanie tekstu w artykulach */
    text-align:justify;
    margin-top:1em !important; /* aby nie najezdzaly wykresy */
    font-size: 1em; /* tryb dla slepych */
}

div,dt,dl { /* tryb dla slepych */
    font-size: 20px !important; 
}

dt,dl { /* elementy w naglowku newsa */
    font-size: 16px !important;
}

h3 { /* naglowek newsa */
    font-size: 20px !important;
}

select,option {
    font-size: 20px !important;
}

.wykres_nazwa, .wykres_nazwa b { /* wielkosc tesktu na wykresach, niestety czasem moze sie rozjezdzac */
    font-size: 16px !important;
}

.dbar {
    vertical-align:bottom !important;
}

.dline { /* lamanie linii w opisie na wykresie */
    height:200% !important;
}

.wykres_wynik { /* wykres slupkow */
    padding-top:8px !important;
    padding-bottom:8px !important;
}

.skala { /* skala wykresu */
    padding-top:5% !important;

}

.lbl { /* napisy na skali */
    font-size: 12px !important;
}


#comments li /* komentarz z cielmym tlem i linia na lewej stronie */
{
    background-color:#eeeeee !important;
    list-style-type: none !important;
    list-style-position:inside !important;
    border-left:1px black solid;
    text-align: justify;
}

.comment_reply { /* usuwamy czarny border bo inaczej wyglada ... */
    border-left: none !important;
}

h4 { /* uzywnay w komentarzach*/
    padding-left:1em;
}

.pager li { /* zmiana stron na dole */
    padding: 4px !important;
}

.pager a, .pager-current a { /* zmiana stron na dole */
    padding:0.5em;
    white-space:nowrap;
    line-height:2em;
}

.pager-current { /* dajmy ciemniejsze tlo dla aktualnej strony */
    background-color:#eeeeee !important;
}

/* naglowek, belka na gorze */

.lixx{ /* nie rysujmy pionowych kresek. nie ma miejsca */
    display:none !important;
}

.mainmenus ul, .mainmenus li {
    font-size: 18px !important;
}

}
