css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("http(|s)://(|www.)2(|-)ch\.(so|ru|hk|ec|pm|re|tf|wf|yt).*") {
	
	html, body {
		background: #151515 !important;
		color: #dcdcdc !important;
		font-family: sans-serif !important;
	}
	
	/* Ссылка и заголовок раздела */
	a, .logo, .header {
		color: #808080 !important;
	}
	
	/* hovered ссылка, заголовок треда */
	a:hover, .filetitle, .replytitle {
		color: white !important;
	}
	
	/* Контейнер поста, новости на главной etc */
	.reply, .theader, .hdescription, .postscontainer, .box1, .txt-box11, .topmenu li a.mitem:link, .topmenu li a.mitem:visited, .addban, .floatforms, .settings_wrap {
		color: inherit !important;
		background: #313131 !important;
		border: 1px solid black !important;
		border-radius: 5px !important;
		font-family: sans-serif !important;
	}
	
	/* Спойлер */
	span.spoiler {
		background-color: #999 !important;
		color: #222 !important;
	}
	
	/* Цитата */
	.unkfunc {
		color: #8b9965 !important;
	}
	
	/* Комментарий слишком длинный. >>Полный текст<<.
	 * Пропущено X ответов, из них Y с изображениями[...]
	 */
	.omittedposts, .abbrev {
		color: #bbb !important;
	}
	
	/* Моноширинный текст */
	code {
		border: 1px dotted #999 !important;
		background: #464646 !important;
	}
	
	/* Разделительные линии */
	hr, .bline .togglediv, .footer {
		border-top: 1px solid white !important;
	}
	
	#fixed {
		background-image: none !important;
		border-top: 1px solid white !important;
		border-bottom: 1px solid white !important;
	}
	
	.newsline {
		border-bottom: 1px solid white !important;
	}
	
	/* Форма настроек */
	.form-tbl, .form-tbl p, blockquote, .adminbar, .rmenu {
		background-color: inherit !important;
		color: inherit !important;
	}
	
	/* Подчеркивание под номером поста в новостях на главной */
	.postscontainer a {
		text-decoration: underline !important;
	}
	/* Показана уменьшена копия, оригинал по клику */
	.thumbnailmsg {
	   display: none !important;
	}
	
	/* Ответы: */
	.de-refmap, .ABU_refmap {
		font-size: .8em !important;
		font-style: normal !important;
	}
	
	/* Служебные надписи */
	.settings_wrap, .settings_wrap div.title, .rowelems label, .postername, .passvalid, .welcome, .newsdescription, .bline .btitle, .postauthor, .posttime {
		color: inherit !important;
	}
	
	/* Всякие заголовки */
	h3, h5 {
		background: inherit !important;
		color: inherit !important;
		border: none !important;
	}
	
	/* Для фреймов */
	dl.menu dt {
		color: inherit !important;
		background-color: inherit !important;
		font-weight: bold !important;
	}
	
	hr {
		height: 0px !important;
	}
	
	.oppost, .reply, .theader {
		margin: 0px !important;
	}
	
	/* Поле ввода */
	textarea, .bline .btitle .btoggle {
		background-image: none !important;
	}
}
