css:
@namespace url(http://www.w3.org/1999/xhtml);
/* v0.06 */

@-moz-document url-prefix("http://backpack.tf/classifieds"), url-prefix("https://backpack.tf/classifieds") {	
  div.quote-box { padding:0px!important; font-size:90%;border:none;box-shadow: none;background:#fff } 
  li.media {  margin:0px!important;  }  
  .listing {line-height: normal!important;}
  .text-muted { line-height:8px!important; font-size:80% }
  div.media-body > h5 { margin:1px!important; border:none!important } 
  i.fa-quote-right.pull-right.fa-large.text-muted { display:none!important }
  li.media.listing:nth-child(10n+1) {counter-increment: pageno;border-top: 1px solid #aaa}
  
li.media.listing:nth-child(10n) h5::after ,
li.media.listing:nth-child(10n+1) h5::after { content:counter(pageno);float: right;border-radius: 6px;padding: 2px;position:static}
  
  li.media.listing:nth-child(10n) h5::after {  background: #ccc; color:white}
  li.media.listing:nth-child(10n+1) h5::after  {  background: #999; color:white}
  
  .listing h5 {padding-bottom: 0!important;font-size: 90%;font-weight: bold}
  .listing h5 > small { color:black!important;background:#ccc }
.search-crumb { margin-top:1px!important; margin-bottom:1px!important; padding:6px 3px!important;border-radius:8px!important}
a.btn { padding:2px 5px!important}
.panel-main > .panel-body, .well {margin-bottom:0px!important; padding:4px!important;border:none!important}
#simple-text-search {padding:2px!important;height:auto!important;font-size:16px!important}
#page-content { padding-top:50px!important }
.well.classifieds-user-controls { background:#aaa!important }
p.listing-counter {color:#fff!important}
#open-search-modal, #submit-search {height:auto!important}
  #offers-url-form label,
  .charsRemaining {display:none}
  .offers-control, .form-control, #offers-url {height: auto;padding: 2px}
  #button_save {height: 22px;padding: 1px}
  #classifieds-form .row, #classifieds-form .form-group {margin-bottom: 2px}
  #details {height: 40px}
  i.listing-intent-sell {display: none}
}
