css:
@namespace url(http://www.w3.org/1999/xhtml);
/*贴吧悬浮回复框by defpt at 2013.08.06*/

 @-moz-document domain(tieba.baidu.com){
    /*[[positionsetting]]*/

}

style_setting_options.value:
/*底部触发*/
#tb_rich_poster_container{
	position:fixed !important;
        border:1px solid #51AFF7 !important;
	background: #F1F1F1!important;
	padding:0 !important;
	margin:0 !important;
	bottom:0 !important;
	left:auto !important;
	top: 99% !important; /*设置触发高度：100%-top值*/
	width: auto!important;
	z-index:999 !important;
	opacity: /*[[youropacity]]*/ /*触发区透明度可设0~1之间数值，1为不透明，0为透明*/
}
#tb_rich_poster_container:hover{
	top: auto !important;
	opacity: 1 !important;
}

/*左下角圆点触发*/
#tb_rich_poster_container{
	position:fixed !important;
	background: #F1F1F1!important;
	bottom:8% !important;/*触发区到底部的距离，此值自己按需设置*/
	left:9.26% !important;/*触发区到左边的距离，此值自己按需设置*/
	width: 0 !important;
	height: 0 !important;
	padding:0 !important;
	margin:0 !important;
	overflow:hidden !important;
	border: 8px solid #51AFF7 !important;
	border-radius: 8px !important;
	z-index:999 !important;
}
#tb_rich_poster_container:hover{
        overflow:auto !important;
	bottom:0 !important;/*回复框置底，此值需自己设置*/
	height: auto !important;/*此值最好自己设置  px 以便符合自己屏幕*/
	width: auto!important;/*此值最好自己设置  px 以便符合自己屏幕*/
        border: 1px solid #51AFF7 !important;
	border-radius: 2px !important;
}
