@-moz-document domain("permalink.gmane.org"), domain("article.gmane.org") {
div.blogbody { 
         margin-left : -2.9em  !important;
   }
}

@-moz-document domain("thread.gmane.org") {
body.article pre {
            font-size : medium !important;
          font-family : "Anonymous Pro", monospace !important;
   }
}

@-moz-document domain("permalink.gmane.org"), domain("article.gmane.org"), domain("comments.gmane.org") {

  body
  {
    background-color : rgb(0, 102, 0) !important;
  }


img[border="0"] {
             margin-right : 20px  !Important;
   }

 
   div.face + pre,
   div.face + h3 + pre,
    td.bodytd { 
                 border-color : rgb(24,98,255);
                        border: thin solid;
             background-color : rgb(212,245,248)  !important;
                    overflow  : auto !important;
   }

   div.face + H3 + p > div > div,
   div.headers + pre,
   div.headers + H3 + pre,
    td.bodytd , div.blogbody {
            font-size : 18pt   !important;
          font-family : "Anonymous Pro", monospace !important;
         padding-left : 0.2ex  !important;
   }
 
 

   body > div#content > div.blog {
          padding-right : 0px  !important;
   }

   div.blog > div.posting > div.entry * pre,
   body > div#content > div.blog .blogbody pre {
                padding : 9px 0px 20px 6px;
           white-space  : pre-line !important; 
              font-size : 15pt !important;
   }

 
   body.main {
            margin-right : -3.7ex  !important;
   }
   body.main table,
   body.main table td {
            margin-right : -1.6ex   !important;
                 padding : 4px  !important;
   }
   body.main td.listid {
               max-width : 35px !important;
   }
   body.main td.bodytd {
           padding-right : 2px  !important;
            padding-left : 2px  !important;
   }

 td.bodytd a,
  .blogbody a {
         font-family : "envy code r", monospace   !important;
         font-size   : 13px     !important;
        white-space  : pre-wrap !important; 
            color    : #5CADFF  !important;
      
   }
   div.posted form   {
         font-family : "Anonymous Pro", monospace   !important;
            color    : #CC9933  !important;
   }
   div.posted form a {
         font-family : "Anonymous Pro", monospace   !important;
            color    : #993300  !important;
   }

   div.face + h3 + p > div > div,
   div.blogbody > p,
   div.face + h3,
    td.bodytd,
   div.blogbody {
            background-color : rgb(24,01,11) !important;
                       color : rgb(129,255,255)  !important;
          
   }

   h3 + div > p pre, 
   div.blogbody > pre + pre,
   div.blogbody > pre + pre > a
                {
              background-color: rgb(255,255,255) !important;
                         color: rgb(255,255,255)  !important;
                display : none !important;
   }

   div.blogbody .tosig, div.blogbody .signature {
              background-color: rgb(0,0,0) !important;
                         color: rgb(8,4,12)  !important;
   }
   div.blogbody .tosig > a, div.blogbody .signature >a {
                         color: #143D66  !important;
   }

   div.face + H3.title,
   div.face + H2.title  {    
         font-family :
          "Palatino Linotype", "Book Antiqua", Palatino, serif  !important; 
         font-size  : xx-large !Important;
          margin-top : -10px   !Important;
       margin-bottom : -2ex    !Important;
         margin-left : -75px   !Important;
         padding-top : 0.3ex   !Important;
      padding-bottom : 1.66ex  !Important;
     vertical-align : 30%      !important;   
       white-space  : pre-line !important;
              color : #EAC7FF  !important; 
   background-color : rgba(1,1,1,45)   !important;
 
   }
}

@-moz-document domain("blog.gmane.org"), domain("comments.gmane.org") {
/* The sidebox (with default CSS, it appears to the left) */

/* body background color should be set in a shared CSS section */
 body { background-color : rgb(0, 102, 0) !important; }

 div#sidebox /* padding-top: 15px; width: 1583.5333251953125px; COMPUTED */
 {
         padding-top : 0.1ex !important;
       padding-right : 0.2ex !important;
        margin-right : 0px   !important;
       padding-left  : 0.2ex !important;
      padding-right  : 0.1ex !important;
           max-width : 236px !important;
           min-width : 235px !important;
    background-color : rgb(0, 102, 0)  !important;
 }

  div#sidebox > *
    {
            max-width : inherit !important;
    }


/* Sidetitle sections:  Options 7, Recent 8 */

 .sidetitle
   {
 -webkit-background-clip : text;
    -moz-background-clip : text;
      background-clip : text;
          margin-top : 1em  !important;
            font-size : medium          !important;
                color : transparent     !important;
     background-color : rgba(1,1,1,0.7) !important;
      /* rgb(34, 61, 34) !important */
          text-shadow : rgba(255, 255, 255, 0.8) 1px 1px 1px;
   }

 #sidebox .side
   {
     background-color : DarkOliveGreen  !important;
                color : LemonChiffon    !important;
        margin-bottom : 3.8ex           !important;
         padding-left : 1ex             !important;
   }

 #sidebox .side a
   {
                color : rgb(202, 225, 255) !important;
   }
 
 #sidebox .sidetitle:first-child + div.side /* trailing "." is site flaw */
   {
                color : transparent      !important;
      /* make the same as search box beneath :  height: 64px */
           min-height : 64px;
          font-weight : 700              !important;
            font-size : small            !important;
       vertical-align : middle           !important;
          padding-top : 1.5ex            !important;
           text-align : justify          !important;
   }
 #sidebox .sidetitle:first-child + div.side a
   {
      /* test shadow 3D emboss effect  */
          text-shadow : 2px  8px  6px rgba(0,0,0,0.2),
                        0px -4px 10px rgba(255,255,255,0.3);
   }

   #sidebox .sidetitle:first-child + div.side a:link,
   #sidebox .sidetitle:first-child + div.side a:visited
     {
       color : rgb(210, 190, 0)  !important;
     }


/* The advert that has been appearing. I have no wish to harm the ability of Gmane
 * to support their operations with advertising revenues. But the advert intrudes
 * layout-wise on the content. So I remove it. */
 #sidebox .sidetitle:nth-child(0n+3),
 #sidebox .side:nth-child(0n+4)
 {
     display : none !important;
 }

 #sidebox .side:nth-child(0n+12) ,
 #sidebox .side:nth-child(0n+14)
   {
       font-size : small       !important;

   }

.side:nth-child(0n+6)
  {
      max-height : 64px        !important;
  }
.side:nth-child(0n+6) form input
  {
     margin-left : -0.25em     !important;
  }
   .side:nth-child(0n+6) form input[name="query"]
     {
           color : red         !important;
background-color : black       !important;
     font-family : "dejavu sans", sans-serif;
       font-size : medium      !important;
     line-height : 1.9         !important;
  vertical-align : middle      !important;
       min-width : 4em;
    padding-left :  2px;
     }

   div.calendar * {
           font-size : xx-small  !important;
    background-color :  rgb(122, 122, 85) !important;
   }

   table.months  tbody tr th {
          visibility : hidden !important;
    background-color :  rgb(122, 122, 85) !important;
   }
   table.months  tbody tr td {
    background-color :  rgb(122, 122, 85) !important;
             padding : 2px 0px 1px 2px !important;
   }

  
   table.months * tr,
   table.months * div,
   #sidebar div.side:nth-of-type(0n+5)  {
           font-size : xx-small  !important;
   }



/* The main content (is in div#content) */
   div#content
     {
          min-width : 75%    !important;
          max-width : auto   !important;
     }
   #content div.blog
     {
   background-color : rgb( 24,  01, 11) !important;
              color : rgb( 85, 107, 47) !important;
    /*    margin-left : -11.8em       !important; */
        margin-left : 0px               !important;
              width : auto              !important;
     padding-right  : 4ex               !important;
       padding-left : 1.8ex             !important;
     }
   div.blog * { color : rgb( 85, 107, 47) !important; }

   div.posting * div.posting
     { 
        margin-left : 0em   !important;
     }

   div.face       {   float : left   !important; }
   div.face > br  {   display : none !important; }
   div.face + h3 /* common to permalink, article, comments WITH OTHER SELECTORS */ 
     {
            font-size : 18pt         !important;
                color : inherit      !important;
          font-family : "Anonymous Pro", monospace !important;
         padding-left : 0.4em        !important;
       vertical-align : baseline     !important;
          line-height : 2.3          !important;
     }
 
   div.blog > div.entry,
   div.blog > div.posting * div.entry {
   background-color : rgb(24, 1, 24)      !important;
          overflow  : auto                !important;
    /*        margin  : 0px 4px 3ex 0px     !important; */
      padding-right : 2ex              !important;
   }

   div.blog > div.entry > div.date,
   div.blog > div.posting * div.entry > div.date, 
   div.blog > div.entry > div.date * {
   background-color : rgb(24, 1, 24)     !important;
              color : rgb(153, 253, 253)  !important;
   }
   div.blog > div.entry > div.blogbody,
   div.blog > div.posting > * div.entry  {
   background-color : rgb(24, 1, 24)     !important;
              color : rgb(153, 253, 253)  !important;
       padding-left : 2.2ex    !important;
         margin-top : 0px !important;
   }
/* 
            line-height : 95% !important;
 */

   div.entry .blogbody
    {
      padding-right : 1ex      !important;
        font-family : "Anonymous Pro", monospace !important;
    }

 .blogbody pre
  {
   background-color : rgb(24,01,19) !important;
    /*  margin-left : -3.1em   !important; */
        margin-left : -2.1em   !important;
       margin-right : 0px      !important;
        padding-top : 0ex      !important;
       white-space  : pre-line !important; 
          overflow  : auto     !important;
          font-size : inherit  !important;
        font-family : inherit  !important;
          font-size : large    !important;
        font-weight : bolder   !important;
              color : #EAC7FF  !important;
        line-height : 122%     !important;
   }

   div + br[clear="all"],
   div + br[clear="all"] + *
     {
   background-color : rgb(102, 102, 0)  !important;
     }
}

@-moz-document domain("permalink.gmane.org") {

/* The advert that has been appearing. I have no wish to harm the ability of Gmane
 * to support their operations with advertising revenues. But the advert intrudes
 * layout-wise on the content. So I remove it.
 */
 #sidebox .sidetitle:nth-child(0n+5),
 #sidebox .side:nth-child(0n+6)
 {
     display : none !important;
 }
}
