css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("oper.ru") {

body {background-color:#fff !important;background-image:none !important;}
body * {max-width:1000px !important;font-family:'Verdana' !important;}

* {font-weight:inherit !important;}
  
div#wrapper {background-image:none !important;max-width:1000px !important;min-width:960px !important;margin:0 auto;}
div#wrapper>a {display:none;}

a,div#middle dt>a,div#middle h1 a,table.comment a {font-weight:inherit !important;}
a {color:#666 !important;border-bottom:1px solid #ddd;}
a:hover {border-bottom:1px solid #33c;}
div#middle h1 a,table.comment a {color:#33a !important;border: none;}

p.meta {color:#000 !important;}    
p.meta a,span.text13 a,td.text13 a { color:#999 !important;border-bottom: 1px solid #ddd !important;}
p.meta a:hover,span.text13 a:hover,td.text13 a:hover {color:#000 !important;border-bottom: 1px solid #aaa !important;}

div.body a {color:#666 !important;border-bottom: 1px dotted #aaa !important;}
div.body a:hover {color:#000 !important;border-bottom: 1px solid #aaa !important;}
    
div#middle dt>a,div#middle h1 a {color:#000 !important;border-bottom: none !important;}
div#middle dt>a:hover,div#middle h1 a:hover {color:#666 !important;border-bottom: none !important;}
    
a:hover,table.comment a:hover {color:#33c !important;}
table.comment a:hover {border-bottom: 1px solid #33c !important;}

p.categories a {color:#333 !important;border-bottom: 1px dotted #aaa !important;}
p.categories a:hover {color:#33c !important;border-bottom: 1px solid #66f !important;}
    
body {color:#777 !important;}
div>font b {color:#888 !important;}

/* * {background-color:#fff;} */
ul.tablist {background-color:#fff;border:1px solid #fff;height: inherit;}
ul.tablist li.current > a {
    background-color:#fff;
    color:#000 !important;
    text-shadow: none !important;
    border-bottom:3px solid #fff;
}

ul.tablist li.current > a:hover {background-color:#fff;}
ul.tablist li {margin-right: 30px;margin-left: 10px;}
ul.tablist li > a {
    background-color:#fff;
    color:#000 !important;
    text-shadow: none !important;
    padding: 0;
    border-bottom:3px solid #fff;
}
ul.tablist li > a:hover {background-color:#fff;color:#666 !important;border-bottom-color: #ddd !important;}
ul.tablist li > a img {display: none !important;}
    
li.search {display: none;}

#container {background-color:#fff !important;padding:0 !important;}
#container>div {height:1px !important;}
#container table td {background-color:#FFF !important;}
    
#headermenut {background-image:none !important; height:30px !important;}
#headermenut a img {width:125px !important; height:35px !important;}
div#headermenul, div#headermenul * {background-color:#fff !important;}
#headermenul a {color:#844 !important;}

    
div.body {color:#000;padding:0 !important;margin:0 !important;}

div#middle dt,div#middle h1 {
    padding: 5px 0;
    border-bottom-width: 1px;
    border-color:#fff #fff #ccc #fff !important;
    color:#444 !important;
    background-color:#fff !important;
}

div#middle dd {
    padding: 0 !important;
}
div#middle dl p.meta {font-size: 16px;}
div#middle dl p.meta a font {color:inherit !important;}
    
div#middle {color:#666 !important;background-color:#fff;font-size: 16px !important;  margin-top: 15px;}

div#middle h1 a {text-shadow: none !important;}
div#middle dl {margin-bottom: 20px;}
div#middle blockquote {color:#333 !important;background-color:#eee !important;padding:20px 30px 20px 50px;margin:10px 0;}

    
/* Комментарии */
table.comment {background-color:#ddd !important;color:#000;
    border-collapse: collapse;border-spacing: 0px;}

table.comment {border:1px solid #e3e3e3;}
table.comment td.text13 b {background-color:#333 !important;padding:3px 7px;}
table.comment .quoted {color: #555 !important;}
span.masked {color: #a00 !important;}

/* Forum */
div#middle table.forum td.opened  {background-color:#333 !important; color:#aaa;}
table.forum {border:1px solid #ccc;color:#333;}
table.forum td.text13>b {background-color:#333;padding:3px 7px;}
span.quoted {color:#666;}


span.text13, span.posted {font-size: 14px !important;}
font.verdana > div {font-size: 16px !important;}
    
.block{border-color:#fff !important;}
#right {background-color:#fff !important;}
#right .block a {color:#666 !important;}
#right .block a:hover {color:#33C !important;}
#right .block h3 a {color:#94870a !important; display: none;}


div#i font {color:#555 !important;}

.bottomp a {color:#555 !important;border-bottom:1px dotted #aaa !important;}
.bottomp a:hover {color:#33c !important;border-bottom:1px solid #33c !important;}
.bottomh {color:#888 !important;}
.bottomh a {color:#444 !important;}


/* other */

hr {color:#ccc !important;}
   

/* Удалить */
li.special {display: none;}
div.mega_block {display:none;}
#soc {display:none !important;}
#container > div:first-child {display:none !important;}
div#topbanner {display:none !important;}
td[width="120"][rowspan="2"] {display:none !important;}
iframe.twitter-timeline-rendered {display:none !important;} 
div#right>iframe {display:none !important;}
div.fb_iframe_widget {display:none !important;}
.banner {display:none !important;}
yatag {display:none !important;}

/* v.1.8.1 */

}
