css:
@-moz-document domain(tomshardware.com)
{

#shareBarFloating, #topPromo, aside.rightCol, div.zonepub, .breadcrumb, nav.mainNav, div.headerLink, div.headerSearch, .OUTBRAIN {display:none;}
body.THA, #mainSection, .topContainer, .mainHeader {background:none!important;}
.headerContent {margin-top:-1%;}
.infos {margin-top: 0px!important;}
article.leftCol {width: 100%!important;}
h1.title {font-size: 18px!important;}
section.header {font-family:ABeeZee;font-size:20px;}
#news-content {margin: 0%; font-family:ABeeZee;font-size:20px;}
#news-content p {font-family:ABeeZee;font-size:20px;}
div.box {border:1px solid #B0B0B0; margin:1% 3% 3% 3%;box-shadow: 0 0 15px 10px #B0B0B0;}
section.inner15 h1 {text-align:center;font-family:ABeeZee;font-size: 24px!important;}
#modUsrNotifications {top:10px!important;}
#news-content table.prices {display:none;}
