css:
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{

/* blue fix */
div.search_form_row {background-color: rgba(100,100,100,0.8)}
div.l-header .selection_nipple {border-bottom-color: rgba(100,100,100,0.8)}

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

.blog.has_follow_button:before,
#right_column:after {display: none}

BODY:not(.mceContentBody), #new_post.editing {
	background: url('http://i29.tinypic.com/2wmjrrq.jpg') fixed no-repeat !important;
	background-size: cover !important;
}

.new_post .title, .new_post .tag_editor {
	background-color: rgba(255,255,255,1) !important;
}

.l-content {
	background: rgba(100,100,100,0.8) !important;
}

#dashboard_edit_post {
	background-color: #ffffff !important;
}

#right_column .dashboard_nav_item {
	border-bottom: 0px solid #eee !important;
	background-image: none  !important;
}

#right_column .dashboard_nav_item .dashboard_nav_title,
#right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,
#right_column .dashboard_nav_item ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {
	color: #fff !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
	color: #888 !important;
}

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
	background: none !important;
}

#left_column #posts .notification .avatar {
	border-color: transparent !important;
	background-color: #fbfbfb !important;
	opacity: 0.5 !important;
}

#left_column #posts .notification .avatar:hover {
	opacity: 1 !important;
}

.dashboard_switch_blog_menu_header {
	background-color: #fbfbfb !important;
	color: #666 !important;
}

.dashboard_switch_blog_menu_item {
	background-color: #fbfbfb !important;
	color: #666 !important;
	border: 0px !important;
}

.dashboard_switch_blog_menu_footer {
	background-color: #fbfbfb !important;
	color: #666 !important;
}

.dashboard_switch_blog_menu_wrapper {
	background-color: #fbfbfb !important;
}

#new_post_notice_container {
	color: #fff !important;
}

#new_post_notice_container a {
	color: #fff !important;
}

#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current {
	background-image: none !important;
}

.new_post_label/*_icon*/ > I {
	color: transparent !important;
	background-image: url('http://i28.tinypic.com/2u7lv6t.png') !important;
	z-index: -1;
}
/* Big planets, please: */
/* saturn is 50x50; icons 64hx513w (nominal 75w)*/
/* x1.4 --> 70x70 icons in 89.6h x 718.2w strip */
.new_post_label/*_icon*/ > I {
	background-size: auto 90px !important;
	transition: background-position .8s linear !important;
}


.new_post_label:nth-of-type(1) > I {background-position: /*-moz-calc(1.4 * -8px) -moz-calc(1.4 * 8px)*/-11.2px 11.2px !important}
.new_post_label:nth-of-type(2) > I {background-position: /*-moz-calc(1.4 * -81px) -moz-calc(1.4 * 8px)*/-113.4px 11.2px !important}
.new_post_label:nth-of-type(3) > I {background-position: /*-moz-calc(1.4 * -156px) -moz-calc(1.4 * 8px)*/-218.4px 11.2px !important}
.new_post_label:nth-of-type(4) > I {background-position: /*-moz-calc(1.4 * -230px) -moz-calc(1.4 * 8px)*/-322px 11.2px !important}
.new_post_label:nth-of-type(5) > I {background-position: /*-moz-calc(1.4 * -303px) -moz-calc(1.4 * 8px)*/-424.2px 11.2px !important}
.new_post_label:nth-of-type(6) > I {background-position: /*-moz-calc(1.4 * -379px) -moz-calc(1.4 * 8px)*/-530.6px 11.2px !important}
.new_post_label:nth-of-type(7) > I {background-position: /*-moz-calc(1.4 * -453px) -moz-calc(1.4 * 8px)*/-634.2px 11.2px !important}

.new_post_label:nth-of-type(1):hover > I {background-position: /*-moz-calc(1.4 * -8px) -moz-calc(1.4 * -7px)*/-11.2px -9.8px !important}
.new_post_label:nth-of-type(2):hover > I {background-position: /*-moz-calc(1.4 * -81px) -moz-calc(1.4 * -7px)*/-113.4px -9.8px !important}
.new_post_label:nth-of-type(3):hover > I {background-position: /*-moz-calc(1.4 * -156px) -moz-calc(1.4 * -7px)*/-218.4px -9.8px !important}
.new_post_label:nth-of-type(4):hover > I {background-position: /*-moz-calc(1.4 * -230px) -moz-calc(1.4 * -7px)*/-322px -9.8px !important}
.new_post_label:nth-of-type(5):hover > I {background-position: /*-moz-calc(1.4 * -303px) -moz-calc(1.4 * -7px)*/-424.2px -9.8px !important}
.new_post_label:nth-of-type(6):hover > I {background-position: /*-moz-calc(1.4 * -379px) -moz-calc(1.4 * -7px)*/-530.6px -9.8px !important}
.new_post_label:nth-of-type(7):hover > I {background-position: /*-moz-calc(1.4 * -453px) -moz-calc(1.4 * -7px)*/-634.2px -9.8px !important}

.new_post_label {
	font-size: 14px !important;
	padding-top: 10px !important;
	font-family: Georgia, Times New Roman, sans-serif !important;
	font-style: italic !important;
	text-transform: lowercase !important;
	color: #222 !important;
	letter-spacing: 1px !important;
}

#left_column #posts .notification {
	background-color: #fbfbfb !important;
	color: #666 !important;
	border-bottom-color: #eee !important;
}

#left_column #posts .notification .block {
	color: #333 !important;
}

#left_column #posts .notification.alt {
	background-color: #f7f7f7 !important;
}

#left_column #posts .notification.last_notification,
#left_column #posts .notification.single_notification {
	border-bottom-color: #ececec !important;
}

#left_column #posts .notification a {
	color: #565656 !important;
	border-bottom: 0px !important;
}

#left_column #posts .notification blockquote {
	border-left-color: #eee !important;
}

#left_column #posts .notification .avatar {
	border-color: transparent !important;
	background-color: #fbfbfb !important;
	opacity: 0.5 !important;
}

#left_column #posts .notification .avatar:hover {
	opacity: 1 !important;
}

.notification_type_icon {
	display: none !important;
}

}
