css:
body , #playerContainerWrapper, #bottomContentTabContainer.outline #outline {
  background: #353634;
}

#siteHeader {
  background-color: #2B2B2B;
}

#siteHeader *, #siteHeader *:hover {
  background: #2b2b2b !important;
}

#siteHeader a {
  color: #acacac !important;
  transition: .15s linear
}

#siteHeader a:hover {
  color: #d2d2d2 !important;
  text-decoration: underline !important;
}

#siteHeader #siteHeaderInner ul li .siteHeaderNicopo.siteHeaderTest3 a.siteHeaderNicopoBalance {
  text-shadow: 0 0 3px #057ac2 !important;
}

#siteHeader #siteHeaderInner .siteHeaderGlovalNavigation li ul li.menuServiceList {
  border-top: 1px solid #353634 !important;
}

body, #videoHeaderDetail h2, #videoTagContainer .tagInner #videoHeaderTagList li a, .videoPostedAt, .postNameSubmit, .postNameBroadCast, #videoDetailInformation, #videoInfo #videoStats li span, #videoInfo .cct, #videoInfo .parentVideoInfo .parentVideoPostedAt, #videoInfo .contentsTree, .extraVideoInfo .supplementary li, #videoComment h4, #videoInfo #videoStats li {
  color: #d2d2d2;
}

#videoInfo .videoMainInfoContainer, #nicommend {
  border-bottom: 1px solid #2b2b2b;
}

#ichibaMain #ichibaMainFooter * {
  color: #d2d2d2 !important;
}

#ichibaMain #ichibaMainFooter {
  width: 96%;
}

#ichibaMain #ichibaMainLogo a, #ichibaMain #ichibaMainHeader a, #ichibaMain .action, #ichibaMain #ichibaMainLogo, #ichibaMain .price {
  color: #d2d2d2 !important;
}

.click, #ichibaMain .goIchiba a {
  color: #56bcfb !important;
}

#videoInfo .contentsTree {
  background: #d2d2d2 !important;
  border: 1px solid #2b2b2b !important;
}

#videoInfo .cct a {
  color: #353634;
}

#videoInfo .contentsTree:hover {
  color: #353634;
  text-decoration: underline;
}

#videoMenuTopList li.videoMenuList a {
  color: #d2d2d2;
}

a:not(#siteHeader a) {
  color: #057ac2 !important;
}

#videoHeader .shortVideoInfo {
  display: none;
}

.searchContainer .searchText a {
  border-color: #acacac;
}

.searchContainer .searchText input {
  color: #d2d2d2;
  text-indent: .3em;
}

.searchContainer .searchText, .toggleTagEdit, #videoReview, #videoHeader.infoActive .toggleDetailExpand .close, #videoInfo, .stream .inner, #nicommendContainer, #nicoIchiba {
  background: #5b5d5a !important;
  box-shadow: none;
  border-top: 1px solid #2d2d2d;
}
    
.stream .inner {
  border-bottom: 1px solid #2b2b2b;
}
    
#videoHeader .toggleDetailExpand .open, #videoHeader .toggleDetailExpand .close {
  display: none;    
}

#videoTagContainer.default {
  height: 13em !important;
  position: relative;
  top: 1em;
  width: 71%;
  min-width: 71%;
  z-index: 500 !important;
}

#videoTagContainer .tagInner #videoHeaderTagList {
  padding: 3em 0 0;
  line-height: 2;
}

#videoTagContainer .tagInner #videoHeaderTagList .toggleTagEdit {
  width: 13em;
  font-size: 1em;
  top: 0;
  height: 1em !important;
}

#videoTagContainer.default .tagInner {
  height: 10.5em;
}

#videoTagContainer .tagInner #tagEditContainer {
  position: absolute;
  top: 0;
  left: 6em;
}

#videoTagContainer .tagInner #videoTagContainerPin.active, .moreTagsExist {
  display: none !important;
}

#videoTagContainer .tagInner #videoHeaderTagList .toggleTagEdit .toggleTagEditInner {
  position: relative;
  top: 0;
  text-indent: -4em;
  color: #d2d2d2;
}

#videoTagContainer.default .tagInner #videoHeaderTagList .toggleTagEdit .toggleTagEditInner .tagEditStart {
  position: relative;
  top: -1.2em;
  height: 0;
  left: 4em; 
  color: #56bcfb;
}

#videoTagContainer.isEdit .tagInner #videoHeaderTagList .toggleTagEdit .toggleTagEditInner .tagEditFinish {
  position: relative;
  top: -1.2em;
  height: 0;
  left: 5em;
  color: #56bcfb;
}

.videoMenuToggle.videoMenuToggleButton {
  display: none;
}

#videoMenuWrapper {
  height: 10.7em !important;
  top: -11.8em;
  margin-bottom: -13.5em !important;
}

#videoHeader.menuOpened #videoMenuWrapper {
  position: relative;
}

#videoMenuWrapper {
  overflow: auto;
}

body #videoMenuTopList {
  width: 23%;
  padding: 0;
  margin: 0;
  right: 0;
}

#videoInfo {
  box-sizing: border-box;
  padding: 1em;
  margin-bottom: 2em;
}

#videoInfoHead .infoHeadOuter p, #videoShareLinks ul.socialLinks, .extraVideoInfo .supplementary {
  width: 96%;
}

#outline.commonsTreeExists #videoInfo .parentVideoInfo, #userProfile {
  width: 100%;
}

#videoInfoHead .infoHeadOuter p, #videoInfoHead, #videoShareLinks ul.socialLinks, #outline.commonsTreeExists #videoInfo .parentVideoInfo, #userProfile, .extraVideoInfo .supplementary {
  border-bottom: 1px solid #2b2b2b;
}

#videoComment h4 {
  font-weight: bold;
  font-size: 2em;
  width: 96%;
  text-indent: .5em;
  border-left: 8px solid #56bcfb;
}

.videoDescription {
  font-size: 1.2em;
  width: 96%;
  line-height: 1.5;
}

.videoMenuList {
  display: block !important;
}

#videoInfoHead {
  padding-bottom: 2em;
}

#videoShareLinks ul.socialLinks, #outline.commonsTreeExists #videoInfo .parentVideoInfo, #userProfile, .extraVideoInfo .supplementary {
  padding-bottom: 1em;
}

#userProfile .profile {
  width: 86%;
}

.sidebar {
  position: relative;
  bottom: 1.1em;
}

#nicommendContainer, #nicoIchiba {
  box-sizing: border-box;
  padding: 1em;
}

#nicoIchiba {
  margin-top: 2em;
}

#ichibaMain #ichibaMainLogo a {
  font-weight: bold;
  font-size: 1.3em;
}

#ichibaMain #ichibaMainHeader {
  width: 94%;
  margin-bottom: .5em;
}

#ichibaMain dl {
  margin: 0 8px 30px !important;
}

#videoExplorerExpand {
  background: #353634;
  border: none;
  border-bottom: 2px solid #2b2b2b;
  height: 2em;
  margin-bottom: 3em;
  text-align: center;
}

#ichibaMain .itemname a {
  color: #56bcfb !important;
}

#videoExplorerExpand .expandButton {
  position: relative;
  top: 3.1em;
  height: 2em;
  line-height: 2;
  width: auto;
  padding: 0 1em;
  background-color: #353634;
  transition: .15s linear;
}

#videoExplorerExpand .expandButton:hover {
  background-color: #2b2b2b;  
}

#videoExplorerExpand .expandButton {
  line-height: 2;
  color: #d2d2d2;
  font-weight: bold;
  text-shadow: none;
}

.newVideoReview {
  background-color: #acacac;
}

#videoReview h3, .videoReviewAside .videoReviewContrl h4 {
  color: #d2d2d2 !important;
}

#bottomVideoDetailInformation .trigger {
  display: none;
}

#playerTabContainer .playerTabHeader .playerTabItem.active, #playerTabContainer, #playerCommentPanel .section .commentTable .commentTableContainer {
  background-color: #d2d2d2;
}

#playerTabContainer .playerTabHeader .playerTabItem {
  background-color: #acacac;
}

#playerCommentPanel .section .commentTable .commentTableHeader span.sortable {
  color: #acacac;
}

#playerCommentPanel .section .commentTable .commentTableHeaderWrapper {
  background: #353634;
}

#playlist #playlistContainerInner {
  background: #acacac;
}

#playlist .playlistInformation {
  box-shadow: none;
  background: #353634;
  border-top: 2px solid #2b2b2b;
  height: 2em;
}
