css:
@-moz-document url("http://www.tumblr.com/"), url("http://www.tumblr.com/dashboard"), url("http://www.tumblr.com/likes"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/blog/mondoswaifu/followers"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/spotlight/culture"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/tagged/") , url("https://www.tumblr.com/"), url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/blog/mondoswaifu/followers"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/spotlight/culture"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/") {

body {

   background:url("http://oi43.tinypic.com/1zqqxih.jpg")no-repeat fixed  !important;

   background-color: #E4A1FF !important;

   background-position: 0% 60% !important;

   background-size: 100% 100%  !important;

 }



div.l-header .tab.iconic a{

  background-color: rgba(255,0,153,1.0) !important;

  border-radius: 60px !important;  

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  

   font-family: Arial; color: #ff0099;

   background: rgba(255,0,153, 0.65) !important;



 }





.notification  {

  	background: #AA86BF !important; border: none !important; 

	color: #ff0099 !important; font-size: 11px !important;

  }



.notification :link, .notification :active, .notification :visited {

  	color: #ff0099 !important;

    text-decoration: none !important;

    border: none !important;

  }



.new_post_label .new_post_label_icon {

background-image: url('http://i40.tinypic.com/15fq3au.png') !important;

background-size: 520px 100px !important;

margin-top: 0px !important;

margin-right: 15px !important;

opacity: 1 !important;

}



div.l-content #new_post .new_post_label {

color: rgba(255,255,255,.9) !important;

line-height: 1.4em;

font-weight: bold !important;

font-size: .9em !important;



}



#right_column .controls_section li a,



#right_column .controls_section li a .count {



  color: #ffffff !important; 

}



#logo, #logo a {

  position: relative;

  z-index: 1;

  width: 270px !important;

  height: 71px !important;

  background-repeat: no-repeat;

  background: url("http://i43.tinypic.com/veb4pg.jpg")!important;

}



.controls_section li .posts:before {



  position: absolute !important;

  height: 30px !important;

  line-height: 28px !important;

  width: 100px !important;

  font: inherit !important;

}



a.posts  div.hide_overflow{

  visibility: hidden !important;

}



.controls_section li .posts:before {

  content: "Posts" !important;

}.controls_section li .followers:before {



  position: absolute !important;

  height: 30px !important;

  line-height: 28px !important;

  width: 100px !important;

  font: inherit !important;

}



a.followers  div.hide_overflow{

  visibility: hidden !important;

}



.controls_section li .followers:before {

  content: "DARK LORDS" !important;

}.controls_section li .drafts:before {



  position: absolute !important;

  height: 30px !important;

  line-height: 28px !important;

  width: 100px !important;

  font: inherit !important;

}



a.drafts  div.hide_overflow{

  visibility: hidden !important;

}



.controls_section li .drafts:before {

  content: "Draft" !important;

}.controls_section li .queue:before {



  position: absolute !important;

  height: 30px !important;

  line-height: 28px !important;

  width: 100px !important;

  font: inherit !important;

}



a.queue  div.hide_overflow{

  visibility: hidden !important;

}



.controls_section li .queue:before {

  content: "Queue" !important;

}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {



  background: none !important;



  width: 0px !important;



  height: 0px !important;



  margin-top: 0px !important;



  border-bottom: solid 11px rgba(255, 0, 153, 0.65) !important;



  border-right: 11px solid transparent !important;



  border-left: 11px solid transparent !important;



}



.return_to_top_icon{

background: url('http://i42.tinypic.com/2dui69v.png') no-repeat transparent!important;

width:128px!important;

height:128px!important}

}
