css:
@-moz-document domain("twitch.tv") {
/* Color List

Author: Kyle Briody

*/

/* Viewer List */
h3.drag_handle{
	color: #ffffff !important;
}

li.viewer a { 
  	color: #ba95ff !important;
}

/* Scrollbar */
.tse-scrollbar .drag-handle {
    background: none repeat scroll 0% 0% #ffffff !important;
}

/* Buttons & Glyph Icons */
.content .bottom #controls #control_buttons #chat_viewers_dropmenu_button span, 
.content .bottom #controls #control_buttons #chat_settings_dropmenu_button span,
.content #stats_and_actions #channel_actions #button_options span{
    background-image: url('http://i.imgur.com/qRXel8G.png') !important;
}

.dropdown_glyph, .dropdown_static {
    background-image: url('http://i.imgur.com/ieMg8f3.png')!important;
}

/* Chat Box / Lines etc */
#right_col .content { 
	background: #1e1e1e !important; 
}

/* Chat / video Tabs */
.segmented_tabs { 
  	background: #242424 !important; 
}

.segmented_tabs li a {
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.5)!important;
    color: rgb(221, 221, 221)!important;
    background:none!important;
    background-color: rgb(25,25,25)!important;
    box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.15), 0px 1px 0px rgba(255, 255, 255, 0.05) inset!important;
    margin-right:-1px!important;
}

.segmented_tabs li a:hover{
    text-shadow: 0px 1px 0px rgba(190, 190, 190, 0.5)!important;
    color: rgb(255, 255, 255)!important;
    background:none!important;
    background-color: rgb(45, 45, 45)!important;
    box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.15), 0px 1px 0px rgba(255, 255, 255, 0.05) inset!important;
    margin-right:-1px!important;
}

.segmented_tabs li a.selected, .segmented_tabs li a:active {
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.5)!important;
    color: rgb(221, 221, 221)!important;
    border: 1px solid rgb(0, 0, 0)!important;
    background-color: rgb(16,16,16)!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.05), 0px 1px 0px rgba(0, 0, 0, 0.15) inset!important;
}

/* Broadcaster & Mod Icon BG */
.broadcaster, .mod { 
	background-color: #6441a5 !important; 
}

/*Moderation Icon Change */
    A[class="mod_button ban"] {margin-right:2px!important;padding-top:1px!important;background-repeat:no-repeat!important;background-position:center center!important;background-image:url('http://i.imgur.com/oXZkbwW.png') !important;}
    IMG[src*="g18_ban-00000080"] {visibility:hidden!important;}
    A[class="mod_button unban"] {margin-right:2px!important;padding-top:1px!important;background-repeat:no-repeat!important;background-position:center center!important;background-image:url('http://i.imgur.com/xjezRVT.png') !important;}
    IMG[src*="g18_unban-00000080"] {visibility:hidden!important;}
    A[class="mod_button timeout"] {padding-top:1px!important;background-repeat:no-repeat!important;background-position:center center!important;background-image:url('http://i.imgur.com/3wwcgOk.png') !important;}
    IMG[src*="g18_clock-00000080"] {visibility:hidden!important;}

#controls { background: #3e3e3e !important; color: #fff !important; }
#control_input { border: none !important; }
#controls .text { background: #242424 !important; color: #fff !important; }

#chat_lines { background: #1e1e1e !important; }
#chat_line_list { background: #1e1e1e !important; border: none !important; }
#chat_line_list .fromjtv { color: #e5e5e5 !important; }
#chat_line_list li { background: #242424 !important; color: #fff !important; margin-top: 5px !important; padding: 3px 0 !important; line-height: 20px !important; }
#chat_line_list li:nth-child(odd) { background: #1e1e1e !important; }
#chat_line_list p { margin-top: 5px !important; padding-left: 5px !important; }
#chat_line_list a { color: #ba95ff !important; }
#chat_line_list .nick { color: #c9c9c9 !important; padding: 3px !important; background: #181818 !important; border-radius: 3px !important; }
#chat_line_list .chat_line { padding-left: 5px !important }
#chat_line_list .emoticon { padding-top: 5px !important }

div#controls{
    background: none repeat scroll 0% 0% #242424!important;
    background-color: #242424!important;
    border-bottom: 1px solid rgb(0, 0, 0)!important;
    border-top: 1px solid rgb(16,16,16)!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.05) inset!important;
}
textarea#chat_text_input.text{
    border: 1px solid rgb(0, 0, 0)!important;
    background-color: rgb(16,16,16)!important;
    color: #fff!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.05), 0px 1px 0px rgba(0, 0, 0, 0.15) inset!important;
}

#speak.bottom{
    border:none!important;
}

#speak_cover{
    display:none!important;
}

/* Videos */
div.js-archive_list{
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.04) inset;
    background-color: #242424!important;
}

div.archive.video.clearfix{
    border-bottom: 1px solid rgb(0, 0, 0)!important;
}

a.thumb{
    border:none!important;
    background-color: #242424 !important;
    color:rgb(230,230,230)!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.04) inset!important;
}

a.thumb:hover{
    border-top:none!important;
    border-color:#000!important;
    box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.04) inset!important;
    background-color:rgb(25,25,25)!important;
    color:rgb(230,230,230)!important;
}

#right_col .content #archives {
 	background: #242424; 
}
#right_col .content #archives .video a .title {
    color: #ffffff !important;
}

#right_col .content #archives .tse-scroll-content {
    margin-bottom: 59px!important;
}

#right_col .content #archives .video:first-child a{
    box-shadow:none!important;
}

#right_col .content #archives .more_archives {
    border:none!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.04) inset!important;
    background-color:rgb(16,16,16)!important;
    height:60px!important;
  	color: #ba95ff;
}

#right_col .content #archives .more_archives:hover {
    border:none!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.04) inset!important;
    background-color:rgb(25,25,25)!important;
    height:60px!important;
}

/* Main Column */
#main_col .content .tse-content {
    background: #242424!important;
    color: #ffffff !important;
}
#main_col .content .tse-content .description h2, #main_col .content .tse-content .description ul {
	color: #ffffff !important; 
}

/* Stream Title & Now Playing/Team */
span.real_title {
  color: #ffffff !important;
}
div#channel_panels li.panel h3.title, div#channel_panels ol {
  color: #ffffff !important;  
}
div#channel_panels li.panel h1 {
  color: #ffffff !important;
  padding-bottom: 3px;
  border-bottom: 1px solid #1e1e1e !important;  
}
.channel span { color: #ececec !important; }
.channel a { color: #ba95ff !important;}
.profile-photo { border: 4px solid #171717 !important; }
.description p { color: #ececec !important; }
.description a { color: #ba95ff !important; }

#channel_stats span.stat { color: #ececec !important; }
#channel_actions span#channel_following { color: #ba95ff !important; }

div.archive_info p {
 color: #ffffff !important; 
}

/* Buttons */
/* Follow Button */
a#channel_follow,a#subscribe_action,a#subscribe_price.button.leftround.normal_button,#subscribe_action.button.rightround.primary_button{
    background: #242424 !important;
    color:#ccc!important;
    border-color:#1e1e1e!important;
    border-radius:0px!important;
    box-shadow: inset 0 0 1px 0 #000!important;
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.5)!important;
}

/* Sub Buttons */
a#subscribe_price{
    border-right:none!important;
}

a#subscribe_action{
    border-left:none!important;
}

#button_options.dropdown_glyph.action{
    border-radius:0px!important;
    border-color:#1e1e1e!important;
    box-shadow: inset 0 0 1px 0 #000!important;
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.5)!important;
    background-color: #242424!important;
}

a#subscribe_price,a#button_bookmark{
    background: #242424!important;
    color:#ccc!important;
    border-color:#1e1e1e!important;
    border-radius:0px!important;
    box-shadow: inset 0 0 1px 0 #000!important;
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.5)!important;
}


#chat_speak.primary_button{
    border-radius:0px!important;
}

#button_options.dropdown_glyph.action{
    border-radius:0px!important;
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.5)!important;
    background-color: #242424!important;
}
/*Block buttons */
div#chat{
    top:50px!important;
}

/*
div#sub-button{
    display:none!important;
}
*/

/* Hides Watch on Xbox Button */
a#xbox_link,a.button.xbox_button{
    display:none!important;
}

/* generic buttons */
#main_col .content #stats_and_actions #channel_actions .action {
    background-color:#242424!important;
}

a.js-share,#right_col .content .bottom #controls #control_buttons .dropdown_glyph, #button_share.dropdown_static.action{
    background-color: #242424!important;
    color:#ccc!important;
    border-color:#1e1e1e!important;
    border-radius:0px!important;
    box-shadow: inset 0 0 1px 0 #000!important;
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.5)!important;
}


/* Dashboard Styles */
#container {
 	background: #242424 !important; 
}
#dash_main {
 	background: #242424 !important; 
}
#dash_main #chat {
	margin-bottom: 50px !important;
}
#dash_main h1#dashboard_title {
	color: #ffffff !important;  
}
#dash_nav li a {
 	color: #ba95ff !important;
}
#dash_nav li.selected a {
 	color: #ffffff !important; 
}
div#setup_link.old a {
 	color: #ba95ff !important; 
}
div#controls_column #vod_form {
 	border: none !important;
  	background: #242424 !important;
}
div#controls_column #vod_form h3 {
 	color: #ffffff !important;
}
div#controls_column #vod_form textarea, #gameselector_input {
    border: 1px solid rgb(0, 0, 0)!important;
    background-color: rgb(16,16,16)!important;
    color: #fff!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.05), 0px 1px 0px rgba(0, 0, 0, 0.15) inset!important;
}
#custom_filter #gameselector_input {
 width: 185px !important; 
}
div#controls_column #vod_form .normal_button, div#player_overlay a.normal_button, div#controls_column #vod_form .dropdown {
    border-radius:0px!important;
    border-color:#1e1e1e!important;
    box-shadow: inset 0 0 1px 0 #000!important;
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.5)!important;
    background: #242424 !important;
  	color: #ffffff !important;
}
div#controls_column #vod_form .dropdown {
  
}
div#container #stats {
 	border: none !important; 
}
div#container #stats .stat {
 	color: #ffffff !important;
  	opacity: 1 !important;
}
div#twitch_chat.chat_box input#chat_text_input.text {
    border: 1px solid rgb(0, 0, 0)!important;
    background-color: rgb(16,16,16)!important;
    color: #fff!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.05), 0px 1px 0px rgba(0, 0, 0, 0.15) inset!important;
}


/* Hide Kabam Game Filter */
ul.game_filters li.game_filter[data-name="kabam"]{
	display: none !important;
}

/* New subscriber */
ul#chat_line_list li.line.fromjtv:not(:first-child) {
    background: #ba95ff !important;
  	color: #ffffff !important;
  	text-shadow: 1px 1px 1px #000 !important;
}
ul#chat_line_list li.line.fromjtv div.indicator {
 	display: none !important; 
}
}
