css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https?://(0chan.hk|0chanru.net)/[_0-9a-z]+(/.*)?$") {
	html, body {
		background-image: none !important;
		background-color: #171717 !important;
		color: #999 !important;
		font-size: 10pt !important;
		font-family: sans-serif !important;
		margin: 0px !important;
		padding: 0px !important;
	}
	
	a {
		color: #006600 !important;
	}
	
	a:hover {
		color: #0066FF !important;
	}
	
	.adminbar {
		clear: both !important;
		float: right !important;
		font-size: .8em !important;
	}
	
	.adminbar a {
		font-weight: bold !important;
	}
	
	.logo {
		clear: both !important;
		text-align: left !important;
		font-size: 2em !important;
		font-weight: bold !important;
		color: #006600 !important;
	}
	
	.replymode, .passvalid {
		background: #181818 !important;
		text-align: center !important;
		padding: 2px !important;
		color: #2266AA !important;
		clear: both !important;
		font-weight: bold !important;
		margin-bottom: .5em !important;
		border: solid 1px #575757 !important;
		-moz-border-radius: 5px !important;
		-webkit-border-bottom-left-radius: 5px !important;
		-webkit-border-bottom-right-radius: 5px !important;
		-webkit-border-top-left-radius: 5px !important;
		-webkit-border-top-right-radius: 5px !important;
	}
	
	.catalogmode {
		background: #0040E0 !important;
		text-align: center !important;
		padding: 2px !important;
		color: #181818 !important;
		clear: both !important;
		font-weight: bold !important;
		margin-bottom: .5em !important;
		border: solid 1px #575757 !important;
		-moz-border-radius: 5px !important;
		-webkit-border-bottom-left-radius: 5px !important;
		-webkit-border-bottom-right-radius: 5px !important;
		-webkit-border-top-left-radius: 5px !important;
		-webkit-border-top-right-radius: 5px !important;
	}
	
	.rules {
		font-size: 0.7em !important;
	}
	
	.postblock {
		background: transparent !important;
		color: #006600 !important;
		font-weight: bold !important;
	}
	
	.footer {
		text-align: center !important;
		font-size: 12px !important;
		font-family: serif !important;
		margin: 2em 0 0 0 !important;
	}
	
	.dellist {
		font-weight: bold !important;
		text-align: center !important;
	}
	
	.delbuttons {
		text-align: center !important;
		padding-bottom: 4px !important;
	}
	
	.managehead {
		background: #181818 !important;
		color: #006600 !important;
		padding: 0px !important;
	}
	
	.postlists {
		background: white !important;
		width: 100% !important;
		padding: 0px !important;
		color: #800000 !important;
	}
	
	.row1 {
		background: #181818 !important;
		color: #006600 !important;
	}
	
	.row2 {
		background: #575757 !important;
		color: #006600 !important;
	}
	
	.unkfunc {
		background: inherit !important;
		color: #789922 !important;
	}
	
	.reflink {
		font-size: .8em !important;
		font-weight: bold !important;
	}
	
	.filesize {
		text-decoration: none !important;
		color: #666 !important;
		font-size: .8em !important;
	}
	
	.filetitle {
		background: inherit !important;
		font-size: 1.2em !important;
		color: #006600 !important;
		font-weight: bold !important;
	}
	
	.postername {
		color: #004A99 !important;
		font-weight: bold !important;
	}
	
	.postertrip {
		color: #FF3300 !important;
	}
	
	.oldpost {
		color: #CC1105 !important;
		font-weight: bold !important;
	}
	
	.omittedposts {
		color: #666 !important;
	}
	
	.reply {
		background: #181818 !important;
		border: solid 1px #575757 !important;
		padding: 0 !important;
		margin: 0 !important;
		border-radius: 5px !important;
	}
	
	blockquote {
		margin: .5em .5em .5em 1em !important;
	}
	
	blockquote p {
		margin: 0 !important;
		padding: 0 !important;
	}
	
	.reply blockquote {
		margin: .5em !important;
	}
	
	input[type="password"], input[type="file"], input[name="snum"], div form select {
		-webkit-box-sizing: content-box !important;
		-moz-box-sizing: content-box !important;
		box-sizing: content-box !important;
		-webkit-appearance: none !important;
		-moz-appearance: none !important;
		appearance: none !important;
		color: #BBB !important;
		border: 2px solid #545454 !important;
		background-color: #121212 !important;
	}
	
	input[type="file"], input[name="snum"], div form select {
		border-radius: 5px !important;
	}
	
	input[type="text"] {
		-webkit-box-sizing: content-box !important;
		-moz-box-sizing: content-box !important;
		box-sizing: content-box !important;
		-webkit-appearance: none !important;
		-moz-appearance: none !important;
		appearance: none !important;
		background-color: #121212 !important;
		color: white !important;
		border: 2px solid #545454 !important;
		-moz-border-radius: 5px !important;
		border-radius: 5px !important;
	}
	
	input:hover[type="text"], input:focus[type="text"] {
		background-color: #121212 !important;
		color: #AAAAFF !important;
	}
	
	textarea, #path {
		-webkit-box-sizing: content-box !important;
		-moz-box-sizing: content-box !important;
		box-sizing: content-box !important;
		-webkit-appearance: none !important;
		-moz-appearance: none !important;
		appearance: none !important;
		background-color: #121212 !important;
		color: #CCCCCC !important;
		border: 2px solid #545454 !important;
		-webkit-border-radius: 5px !important;
		-moz-border-radius: 5px !important;
		border-radius: 5px !important;
	}
	
	textarea:hover, textarea:focus, #path:hover, #path:focus {
		background-color: #121212 !important;
		color: #EEE !important;
	}
	
	.doubledash {
		display: none !important;
		vertical-align:top !important;
		clear:both !important;
		float:left !important;
	}
	
	.replytitle {
		font-size: 1.2em !important;
		color:#006600 !important;
		font-weight:bold !important;
	}
	
	.commentpostername {
		color:#004A99 !important;
		font-weight:800 !important;
	}
	
	.thumbnailmsg {
			font-size: .8em !important;
			color: #666 !important;
	}
	
	hr {
		border-style: solid none none none !important;
		border-width: 1px !important;
		border-color: #171717 !important;
	}
	
	table {
		border-style: none !important;
	}
	
	table td {
		border-style: none !important;
	}
	
	.nothumb {
		background-color: white !important;
		border-style: dotted !important;
		margin: .3em .5em !important;
	}
	
	.abbrev {
		color: #666 !important;
	}
	
	.highlight {
		background: #161616 !important;
		color: #999 !important;
		border: 2px dashed #727272 !important;
	}
	
	#watchedthreads {
		background-color: #181818 !important;
		border: 1px solid #575757 !important;
		-moz-border-radius: 5px !important;
		-webkit-border-bottom-left-radius: 5px !important;
		-webkit-border-bottom-right-radius: 5px !important;
		-webkit-border-top-left-radius: 5px !important;
		-webkit-border-top-right-radius: 5px !important;
	}
	
	
	.hidethread {
		background:
			transparent url('data:image/png;base64,')
			no-repeat !important;
	}
	
	.unhidethread {
		background:
			transparent url('data:image/png;base64,')
			no-repeat !important;
	}
	
	.watchthread {
		background:
			transparent url('data:image/png;base64,')
			no-repeat !important;
	}
	
	.expandthread {
		background:
			transparent url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAABGdBTUEAALGPC/xhBQAAABh0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjM2qefiJQAAAGhJREFUOE9jZAACAwOD/yCaVHDhwgVGsGZKMF4Dpk2b9h+E8VmA0wCYZkKG0NYLxIQNigvQ/YvN+ehq4AagK8YVBujqqGcAyL8UeYGYAMOmhnbRSJWERCgVgjMhuX6H6QPnYHINAekFACBcogsqf36UAAAAAElFTkSuQmCC')
			no-repeat !important;
	}
	
	.quickreply {
		background:
			transparent url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAABGdBTUEAALGPC/xhBQAAABh0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjM2qefiJQAAAEtJREFUOE9jZAACAwOD/yCaVHDhwgVGsGZK8HA3YNq0af9BGF8Y4QwDmGZChmA1AF0zPkNoYwAp6WKQuoBiL9DXAFh2JsVWmFqQXgC915Ozvjy8qwAAAABJRU5ErkJggg==')
			no-repeat !important;
		margin-right: 1px !important;
	}
	
	.hidewatchedthreads {
		background:
			transparent url('data:image/png;base64,')
			no-repeat !important;
	}
	
	.refreshwatchedthreads {
		background:
			transparent url('data:image/png;base64,')
			no-repeat !important;
	}
	
	.restorewatchedthreads {
		background:
			transparent url('data:image/png;base64,')
			no-repeat !important;
	}
	
	.topbar-left,.topbar-center,.topbar-right,
	.border-left,.border-right,.bottom-left,
	.bottom-right,.bottom-center {
		background-image: none !important;
	}
	
	.content-background {
		background-color: #030303 !important;
	}
	
	.maintable {
		width: 98% !important;
		table-layout: fixed !important;
	}
	
	.ui-button {
		border-style: none !important;
	}
	
	.ui-button-text {
		background-color: #1E1E1E !important;
		color: #999999 !important;
		border: 1px solid #999999 !important;
		border-radius: 4px !important;
	}
	
	h1, h2 {
		background: #222 !important;
		color: #999 !important;
	}
	
	.spoiler {
		background-color: #555 !important;
		color: white !important;
	}
	
	.boardlist_footer {
		color: #727272 !important;
	}
	
	.code_part {
		color: #444 !important;
	}
	
	.shareSite {
		display: none !important;
	}
}
