css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.torrent-invites.com") 
{

/* v1.0 */

.body_wrapper,
#sidebar_container.member_summary,
dd.userprof_moduleinactive,
#profile_tabs a, #profile_tabs a:hover
{
    color: black !important;
    background-color: white !important;  
}

/* active tab */
#profile_tabs dd.userprof_module a,
dd.userprof_module 
{
    background-color: lightgrey !important;  
    background-image: none !important;
}


#sidebar_container .blocksubhead
{
    color: black !important;
}


#sidebar_container .userprof_content .blockrow, dl.stats dt, 
#sidebar_container .blockbody.userprof_content,
#sidebar_container .userprof_content .blockrow
{
    background-color: white !important;
}

.userprof_content, dl.stats dt
{
    color: black !important;
    background-color: rgb(240,240,240) !important;  
}

}
