css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {
/* ==================== Fonts ==================== */
* {
font-family: tahoma!important;
}

.infoBox {
margin: 25px 0 0px 0 !important;
}

#feedlyFrame {
width: 100%!important;
animation: none!important;
transition: none!important;
}

#feedlyPart0 {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
}

.u0Entry {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
border-left-color: transparent!important;
border-right-color: transparent!important;
}

h1 a {
color: #333 !important;
}

.inlineFrame {
margin-top: 0px!important;
background: url(http://i.imgur.com/xOwCK0k.png)!important;
border: 1px #BFBFBC solid !important;
box-shadow: 0px 0px 6px 1px rgba(0, 0, 0, 0.09);
}

.inlineFrame .selectedEntry,.entryHolder {
background-color: transparent!important;
}

.viewerIcon.requiresLogin.tagHolder {
background: none!important;
}

#feedlyProBar {
position: absolute !important;
height: 1px; width: 1px; overflow: hidden; clip: rect(1px 1px 1px 1px);
}
/* ==================== Floating Panel ==================== */
#floatingBar {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
border-bottom: 1.5px solid !important;
text-shadow: 0 0 0.5em black;
}

.pageAction {
opacity: 1!important;
color: lightblue;
}

#floatingBar .pageActionBar {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
box-shadow: 0 0 5px rgba(20,22,18,.5)!important;
border-radius: 3px!important;
}

#floatingTitleBar {
color: #1C1919!important;
font-size: 18px!important;
border-bottom: #D2E4AD!important;
}

#floatingBar span {
color: #000000!important;
}

#feedlyTitleBar {
font-size: 18px!important;
}

#feedlyTitleBar span {
color: #000000!important;
}

/* ==================== Left Sidebar ==================== */
#feedlyTabs .simpleUnreadCount {
opacity: 1!important;
font-size: 12px!important;
text-shadow: 0 0 0.5em black
}

.header.selected,.feedIndex.selected {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
}

.header.selected:hover,.feedIndex.selected:hover {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
}

.header.selected,.feedIndex.selected {
margin-left: -5px!important;
margin-right: -40px!important;
padding-left: 10px!important;
padding-right: 40px!important;
}

.header.selected,.feedIndex.selected {
-webkit-transition: all0.2s;
-moz-transition: all0.2s;
-o-transition: all0.2s;
-ms-transition: all0.2s;
transition: all0.2s;
}

.target:hover {
background-color: hsla(255,100%,100%,0.3)!important;
}

.target:hover {
margin-left: -15px!important;
margin-right: -40px!important;
padding-left: 2px!important;
padding-right: 35px!important;
}

.target:hover {
-webkit-transition: all0.2s;
-moz-transition: all0.2s;
-o-transition: all0.2s;
-ms-transition: all0.2s;
transition: all 0.2s;
}

.target.selected {
border-left: solid 9px hsl(10, 94%, 49%)!important;
color: hsl(10, 94%, 49%)!important;
}

/* ==================== Right Sidebar ==================== */
div#sideArea {
display: none!important;
}

#sideArea .feedIndexTitle {
color: #000000!important;
}

div#sideArea .metadata {
color: #000000!important;
}

h2 {
direction: ltr!important;
text-align: justify!important;
color: #000000!important;
}

/* ==================== Title Only View ==================== */
.u0Entry {
border-top: solid 1px hsl(208,30%,70%)!important;
border-bottom: solid 1px hsl(208,30%,70%)!important;
}

.u0Entry {
padding-left: 0em!important;
padding-right: 0em!important;
}

.u0Entry, .inlineFrame, #fullyLoaded {
margin-bottom: 0.5em!important;
}

.u0Entry .title.unread {
color: #0000FF!important;
font-weight: normal!important;
}

.u0Entry .title.read {
color: #FB0000!important;
font-weight: normal!important;
}

.u0Entry .title:hover {
color: #3AA500!important;
}

.nbrRecommendations {
color: hsl(10, 94%, 49%)!important;
}

.u0Entry:hover {
text-shadow: 0 0 0.8em #3AA500!important;
}

.u0Entry .sourcetitle a {
color: black!important;
text-shadow: 0 0 0.9em black, 0 0 0.5em black!important;
}

.sourceinfo {
left: auto!important;
float: left!important;
direction: ltr!important;
text-align: left!important;
}

div .sourceInfo {
padding-right: 0.5em!important;
padding-left: 0.5em!important;
}

.u0EntryList {
margin-top: 6px!important;
}

.u0Entry .quicklistHandle {
margin-left: -0.3em!important;
margin-right: -0.2em!important;
}

.lastModified {
color: hsl(126, 100%, 32%)!important;
float: left!important;
}

.lessThanAnHour, .preferencesItemLabel {
color: hsl(10, 94%, 49%)!important;
}

.u0Entry .lastModified {
text-align: center!important;
font-weight: bold!important;
}

.u0Summary {
color: black!important;
}

/* ==================== Today Page ==================== */
div#feedlyPage {
width: 100%!important;
}

.u4Entry, .u12Entry {
max-width: none!important;
}

.u4Entry .title.unread, .u12Entry .title.unread {
color: #000000!important;
font-weight: bold!important;
transition: background 0.25s ease 0s,color 0.25s ease 0s!important;
}

.u4Entry .title.read, .u12Entry .title.read {
color: #000000!important;
font-weight: normal!important;
transition: background 0.25s ease 0s,color 0.25s ease 0s!important;
}

.u4Entry .title:hover, .u12Entry .title:hover,.u4Entry .title:active, .u12Entry .title:active, .u4Entry .title:focus, .u12Entry .title:focus {
color: #00CCFF!important;
transition-delay: 0s,0s!important;
transition-duration: 0.25s,0.25s!important;
}

.u4Entry .title:hover, .u12Entry .title:hover,.u4Entry .title:active, .u12Entry .title:active, .u4Entry .title:focus, .u12Entry .title:focus {
transition-property: background,color!important;
transition-timing-function: cubic-bezier(0.25,0.1,0.25,1),cubic-bezier(0.25,0.1,0.25,1)!important;
}

.u4Entry .title:hover, .u12Entry .title:hover,.u4Entry .title:active, .u12Entry .title:active, .u4Entry .title:focus, .u12Entry .title:focus {
-moz-text-blink: none!important;
-moz-text-decoration-color: rgb(0,0,0)!important;
-moz-text-decoration-line: none!important;
-moz-text-decoration-style: solid!important;
}

.u4Entry .summary, .u12Entry .summary {
color: #000000!important;
}

.u4Entry .summary div, .u12Entry .summary div {
text-align: justify!important;
}

.visual {
border-color: #000000!important;
}

.topRecommendedEntry .visual {
border-radius: 6px!important;
}

.topRecommendedEntry .title.unread {
color: #000000!important;
font-weight: bold!important;
transition: background 0.25s ease 0s,color 0.25s ease 0s!important;
}

.topRecommendedEntry .title.read {
color: #000000!important;
font-weight: normal!important;
transition: background 0.25s ease 0s,color 0.25s ease 0s!important;
}

.topRecommendedEntry .title:hover, .topRecommendedEntry .title:active, .topRecommendedEntry .title:focus {
color: hsl(3,90%,30%)!important;
transition-delay: 0s,0s!important;
transition-duration: 0.25s,0.25s!important;
}

.topRecommendedEntry .title:hover, .topRecommendedEntry .title:active, .topRecommendedEntry .title:focus {
transition-property: background,color!important;
transition-timing-function: cubic-bezier(0.25,0.1,0.25,1),cubic-bezier(0.25,0.1,0.25,1)!important;
}

.topRecommendedEntry .title:hover, .topRecommendedEntry .title:active, .topRecommendedEntry .title:focus {
-moz-text-blink: none!important;
-moz-text-decoration-color: rgb(0,0,0)!important;
-moz-text-decoration-line: none!important;
-moz-text-decoration-style: solid!important;
}

#recommentationAreaHeadlines {
background: hsl(230,95%,95%)!important;
padding: 3% 0% 3% 3%!important;
width: 103%!important;
margin-left: -3%!important;
}

#recommentationAreaHeadlines {
box-shadow: 0px 0px 8px 8px hsl(49,14%,45%)!important;
border: solid 3px #000000!important;
border-radius: 9px!important;
}

/* ==================== Cards View ==================== */
.u5Entry {
background: hsl(230,95%,95%)!important;
border-color: #000000!important;
border-radius: 3px!important;
}

.u5Entry .title.unread {
background: rgb(46,51,55)!important;
color: rgb(255,255,255)!important;
font-weight: bold!important;
width: 108%!important;
padding: 5% 4% 5% 4%!important;
margin-left: -8%!important;
}

.u5Entry .title.read {
background: rgb(46,51,55)!important;
color: rgb(255,255,255)!important;
font-weight: normal!important;
width: 108%!important;
padding: 5% 4% 5% 4%!important;
margin-left: -8%!important;
}

.u5Entry .title:hover {
color: rgb(0,255,255)!important;
}

.u5Entry .summary {
color: #000000!important;
width: 100%!important;
padding: 0% 4% 0% 4%!important;
margin-left: -8%!important;
}

/* ==================== Full Article View ==================== */
.u100Entry {
max-width: 100%!important;
}

.u100Entry .title {
color: #028600!important;
padding: 2% 5% 0 5%!important;
max-width: none!important;
margin: 0!important;
}

.u100Entry .title:hover {
color: #008000!important;
padding: 2% 5% 0 5%!important;
max-width: none!important;
margin: 0!important;
}

.entryHeader a {
color: #4B0082!important;
font-size: 100%!important;
font-weight: bold!important;
text-align: justify!important;
}

.entryHeader a:hover {
color: rgb(0, 255, 0)!important;
text-shadow: 0 0 0.5em black;
}

.entryHeader .metadata {
color: #333333!important;
font-size: 104%!important;
background: url(http://i.imgur.com/xOwCK0k.png)!important;
border-top: 2px #383840 solid !important;
border-bottom: 2px #383840 solid !important;
padding: 0 5% 0 5%!important;
}

.entryBody {
padding: 0 5% 5% 5%!important;
max-width: none!important;
}

.entryBody div {
color: #000000!important;
text-align: justify!important;
}

.entryBody a {
color: #F22E07!important;
}

.entryBody a:hover, .entryBody a:active, .entryBody a:focus {
color: rgb(0,204,255)!important;
transition-delay: 0s,0s!important;
transition-duration: 0.25s,0.25s!important;
}

.entryBody a:hover, .entryBody a:active, .entryBody a:focus {
transition-property: background,color!important;
transition-timing-function: cubic-bezier(0.25,0.1,0.25,1),cubic-bezier(0.25,0.1,0.25,1)!important;
}

.entryBody a:hover, .entryBody a:active, .entryBody a:focus {
-moz-text-blink: none!important;
-moz-text-decoration-color: rgb(0,0,0)!important;
-moz-text-decoration-line: none!important;
-moz-text-decoration-style: solid!important;
}

.inlineFrame {
padding: 2em 0 0 0!important;
box-shadow: 0 0 4px 4px hsl(49,14%,45%)!important;
border: solid 2px #6688EE!important;
}

.u100Frame {
padding: 0!important;
box-shadow: 0 0 4px 4px hsl(49,14%,45%)!important;
border: solid 2px #6688EE!important;
}

/* ==================== Icon Layout ==================== */
*.action, .staticValue, .checkboxLabel, .entryBody h3 {
color: navy!important;
}

#quicklookHeader .action {
color: rgb(255,255,255)!important;
}

.topWikiWidget {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
}

.topWikiWidget {
bottom: 0!important;
left: 0!important;
position: absolute!important;
width: 100%!important;
}

.topWikiWidget {
border-bottom: medium none!important;
margin-bottom: 0!important;
padding-bottom: 0!important;
border-top: solid 1px #DEE7FF!important;
}

.bottomWikiWidget .inlineFrame {
display: none!important;
}
.websiteCallForAction {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
border: solid 3px #6688EE!important;
font-size: 18px!important;
color: #00FF00!important;text-shadow: 0 0 0.5em black!important;
margin-top: 0px!important;
margin-bottom: 20px!important;
}
.websiteCallForAction:hover {
background: url(http://i.imgur.com/xOwCK0k.png)!important;
border: solid 3px #6688EE!important;
font-size: 18px!important;
color: #FF4500!important;text-shadow: 0 0 0.5em black, 0 0 0.5em black!important;
margin-top: 0px!important;
margin-bottom: 20px!important;
}
.highlightableIcon {
opacity: 0.5!important;
}

div .metadata {
color: #333333!important;
}

div .metadata a:hover {
color: #8B008B!important;
}
}
