@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
    /* Adjusts site-wide font color */
    body{
        color: #676767 !important;
    }
    /* Adds border around thumbnails, both for art and literature */
    .shadow img, .shadow .thumb.lit{
        border: 2px solid #52473f !important;
    }
    /* Recolors "beta" and "new" tags on sidebar, obsolete as there's nothing new or in beta on the site
    .facet-tag-beta small{
        background-color: #5b6762 !important;
    }
    .facet-tag-beta small:hover{
        background-color: #9ead9e !important;
    }
    .facet-tag-new small{
        background-color: #5b6762 !important;
    }
    .facet-tag-new small:hover{
        background-color: #9ead9e !important;
    }
    */
    /* Overheader styles */
    #overhead {
        max-height: 30px !important;
        background: #52473f!important;
        width:100%!important;
    }
        /* Styles the overhead user menu */
        #oh-menu-deviant, #oh-menu-split, #oh-menu-friends, #oh-menu-collect, #oh-menu-cart, #oh-menu-join, #oh-loginbutton, #overhead .oh-mc-split, #oh-menu-greeting{
            background: #493f38  !important;
            border-left: none !important;
        }
        /* Styles overhead menu icons and links */
        #oh-menu-split>a>.icon, #overhead a{
            color: #FFF !important;
        }
        /* Styles overhead menu item backgrounds */
       .oh-menu{
        background: #29231f  !important;
        }
        /* Styles overhead menu item background while menu is open and button is NOT hovered */
        .mmhover.oh-hashover .oh-l,.mmhover2 .oh-l,.mmhover3 .oh-l,.oh-nohover .oh-l:hover,.oh-hasbutton .active,.oh-m:active, .mmhover>a>span{
            background-color:#29231f !important;
        }
        /* Styles Submit button */
        #oh-menu-submit>a>span{
            background: #29231f !important;
            border-radius: 15px !important;
        }
        /* Replaces CORE logo with new image and hides the old one */ 
        #oh-menu-upgrade>a>img{
            background: url(http://i.imgur.com/EPnzkn1.png) no-repeat !important;
            padding-left: 100px !important;
            height: 14px !important;
            width: 0px !important;
            overflow: hidden !important;
        }
        /* Cleans up extra highlight width on CORE upgrade button*/
        #oh-menu-upgrade>a{
            max-width: 90px !important;
        }
        /* Styles hover event for remaining overhead buttons  */
        /* This includes CORE upgrade, friends, and favorites */
        .oh-l:hover{
            background: #29231f  !important;
        }
    
        /* Styles serachbar background */
        #overhead .gmbutton2f{
            background:#FFF !important;
        }
        /* Styles font color of points balance under Shop item */
        .pointsBalance{
            color: #d4dfd0 !important;
        }
    /* *** END OF OVERHEAD STYLES *** */
    
    /* Modifies background color of subheader & left sidebar */
    #navbar-menu .navbar-menu-inner, #output>.browse-container.newbrowse .browse-left-bar .browse-left-bar-inner{
        background: #52473f !important;
    }
    /* Styles & modifies the hamburger menu on left sidebar */
    #browse-sidemenu .browse-facet.browse-facet-state .button{
        background: #312a25 !important;
        border: none !important;
    }
    /* Replaces default "hamburger" menu icon */
    #browse-sidemenu .browse-facet.browse-facet-state .button span{
        background: url("http://i.imgur.com/in6VjiY.png") no-repeat scroll 0 0 transparent !important;
        background-size: 16px !important;
        background-position: 0px -2px !important;
    }
    /* Styles left sidebar header colors */
    #browse-sidemenu .noncollapseHeader{
        color: #FFF !important;
    }
    /* Styles SELECTED link on left sidebar */
    .browse-facet a.selected{
        background: #333 !important;
    }
    /* Styles HOVERED link on left sidebar */
    .browse-facet a:hover{
        background: #333 !important;
    }
    /* Styles left siderbar menu link color */
    .browse-facet ul a {
        color: #FFF !important;
    }
    /* Modifies mature content filter settings in browse window */
    .browse-link-button {
        color: #52473f !important;
    }
    
    /* Profile styles */
    ul.iconset-gruser li.active.profile, ul.iconset-gruser li.active.profile:hover, #group ul.iconset-gruser li.active, #group ul.iconset-gruser li.active:hover {
        background: #52473f !important;
        border-bottom: 1px solid #52473F !important;
        box-shadow: none !important;
    }
    ul.iconset-gruser li.tab{
        border: none !important;
    }
    /* Changes font color on profile page subheader */
    /* This includes user name and menu tab text */
    body.gruze .catbar a{
        color: #FFF !important;
    }
    /* Styles catbar */
    body.gruze .catbar{
        background: #89776a  !important;
        border-top: none !important;
    }
    /* Styles blues bar font to white */
    .super-secret-why-short > span:nth-child(2){
        color: #FFF !important;
    }
    span.bb:nth-child(2) > a:nth-child(1) > div:nth-child(2){
        color: #FFF !important;
    }
    span.bb:nth-child(2) > a:nth-child(1) > div:nth-child(3){
        color: #FFF !important;
    }
    .tight{
        color: #FFF !important;
    }
    div.blues-bar span.section-links a{
        color: #9FA8A2 !important;
    }
    /* Styles blues bar background */
    .blues-bar{
        background:#52473f !important;
        border-top: none !important;
    }
    .blues-bar-ctrl{
        background:#52473f !important;
        border-bottom: none !important;
    }
    /* Styles frame tabs */
    i.gr3 i, .gr-top .gr{
        background: #52473f !important;
    }
    /* Draws triagles using border trick to replace image */
    i.tri{
        background: transparent !important;
        margin-top: 30px;
        width: 0; 
	    height: 0; 
	    border-left: 10px solid transparent;
	    border-right: 10px solid transparent;
	    border-top: 10px solid #52473f;
    }
    /* Changes frame tab font to white */
    i.gr3 i, .gr-top .gr h2{
        color: #FFF !important;
    }
    /* Styles pale smbutton */
    .smbutton-pale{
        background: #52473f !important;
    }
    .smbutton-pale > span{
        border-top-color: #52473f !important;
    }
    .smbutton-blue{
        background: #52473f !important;
        border-color: #52473f !important;
    }
    .smbutton-blue > span{
        border-top-color: #52473f !important;
    }
    
    /* Friends list full-height */
    .popup2-friends-menu {
        position: fixed !important;
        top: 42px !important;
        left: auto !important;
        right: 0;
        height: calc(100% - 42px) !important;
        box-shadow: 0 0 16px rgba(0, 0, 0, 0.5);
    }
    .friendmachine .edit {
        top: 23px !important;
        left: auto !important;
        right: 3px !important;
        width: 109px !important;
    }
    
    /* Styles site footer and link colors */
    #deviantART-v7 footer#depths .depths-inner{
        background: #52473f !important;
        color: #FFF !important;
    }
    #deviantART-v7 footer#depths div.footer_copyright, #deviantART-v7 footer#depths div.footer_tx_links, #deviantART-v7 footer#depths a{
        color: #FFF !important;
    }
    /* *** END FOOTER STYLES *** */
}
