css:
@-moz-document domain("readytogo.net") {
/*page settings */
.pageWidth {
  	margin-left:auto;
	margin-right:auto;
    max-width:800px;
	}

.pageContent{
	font-family:Arial;
    font-size:8pt;
	}
.logoBlock{
	background-color: #EDE8EA;
	}

/* Subforum nodes */
.sectionMain{
	padding-bottom: 0px;
	padding-left: 0px;
	padding-right: 0px;
	padding-top: 0px;
	margin-top:0px;
	border:none;
	}
.node .nodeText .nodeTitle{
	font-size:10pt;
	}
.nodeIcon{
	display: none;     
	}
.node .nodeLastPost{
	display: none;              
	}
.node .tinyIcon{
	display: none;
	}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo{
	border:solid;
	border-width:1px;
	}
.node .nodeStats{
	display:none;
    }

/*menu bar and outer*/
.discussionListItem .itemPageNav{
  visibility: visible;
    }
.breadcrumb{
	border: none;
	background-color: inherit;
	}
.breadcrumb .crust a.crumb{
    border: none;
	background-color: inherit;
    padding-left: 5px;
	padding-right: 5px;
    }
#content .pageContent{
    padding-left: 3px;
  	padding-right: 3px;
	}
.navTabs{
	border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
  	}
.footer .pageContent{
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    }
.sharePage{
    visibility:hidden;
    height: 1px;
	}
 .Bg{
	visibility:hidden;
    height: 1px;
    }
 .Cg{
    visibility:hidden;
    height: 1px;
    }

/*messages list */
.pollBlock .question .questionText{
	color: red;
    }
.discussionListItem .posterAvatar, .discussionListItem .stats{
  	background-color: inherit;  
    }
.discussionList .posterAvatar{
    visibility:hidden;
    width: 1px;
	height: 1px;
    }
.discussionListItem .posterAvatar .avatar img{
	visibility:hidden;
    width: 1px;
	height: 1px;
    }
.discussionListItem .title{
    font-size: 10pt;
	}
.discussionListItem .stats .major{
    font-size: 10pt;
	}
.discussionListItem .lastPostInfo .username{
    font-size: 10pt;
    }
.sectionMain{
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    }

/* individual messages */
.secondaryContent{
	background-color: inherit;
  	}
#pageDescription{
	visibility:hidden;
    height:0px;
	}
.messageList{
	border:none;
	border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
  	padding-bottom: 5px;
  	}
.messageList .message{
	background-color: inherit;
    border-bottom-style:solid;
    border-top-style:solid;
    border-right-style:solid;
    border-left-style:solid;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    border-bottom-width:1px;
    border-top-width:1px;
	border-top-color: black;
	border-bottom-color: black;
	padding-bottom: 10px;
    padding-top: 10px;
    }
.primaryContent{
  	background-color: white;
	}
.message .signature{
  	background-color: inherit;
    font-size: 8pt;
    }
.messageContent{
    background-color: white;
    padding: 5px;
    }
.messageText{
    font-size: 9pt;
    font-family:verdana;
	}

/*Avatar area*/
.avatar img, .avatar .img, .avatarCropper{
	border:none;
	border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    padding: 0px;
    }
.messageUserBlock{
	background-color: inherit;
    padding: 0px;
	-webkit-border-radius: 0px;
  	border-radius: 0px;
    border:none;
	}
.messageUserBlock div.avatarHolder{
	background-color: inherit;
	padding: 0px;
	-webkit-border-radius: 0px;
    border:none;
	}
.arrow{
	visibility:hidden;
    display: none;
	}

/* reply area */
.quickReply{
  	background-color: inherit;
    border-bottom-style:solid;
    border-top-style:solid;
    border-right-style:solid;
    border-left-style:solid;
    
     border-bottom-right-radius: 0px;
     border-bottom-left-radius: 0px;
     border-top-right-radius: 0px;
     border-top-left-radius: 0px;
      
     border-bottom-width:1px;
     border-top-width:1px;
  
  border-top-color: black;
  border-bottom-color: black;
  
  padding-bottom: 10px;
    padding-top: 10px;
  }
.redactor_toolbar{
     	background-color: inherit; 
  }

/* tooltip box */
.xenPreviewTooltip{
    	background-color: inherit;
     border-bottom-style:solid;
     border-top-style:solid;
     border-right-style:solid;
     border-left-style:solid;
     
     border-bottom-right-radius: 0px;
     border-bottom-left-radius: 0px;
     border-top-right-radius: 0px;
     border-top-left-radius: 0px;
      
     border-bottom-width:1px;
     border-top-width:1px;
    border-left-width: 1px;
      border-right-width:1px;
     border-color: black;
    
  }
.previewContent{
           	background-color: white;
  			border:none;
    }
.avatar .img{
       visibility:hidden;
        width: 0px;
        height: 0px;
        display: none;
      }
 .previewTooltip .text{
        margin-left:5px;
    position:relative;

  
          }
}
