css:
@-moz-document url-prefix("https://bazqux.com") {
div.msgButtons{
    font-size:10px!important;
    }
div.infoMsgText{
  	background-color:#3E4957!important;
  	color:#fff!important;
  	border:#3E4957!important;
	}
.msgFrame{
    border:0!important;
    border-bottom:1px #eee solid!important;
  	background-color:#fff!important;
    }
.expandable.unselected.compact.post.msgFrame,
.read.expandable.unselected.compact.post.msgFrame{
    margin:0!important;
  	border-radius:0!important;
  	border:0!important;
  	border-top:1px solid #eee!important;
  	border-left:0!important;
    }
.expandable.selected.compact.post.msgFrame,
.read.expandable.selected.compact.post.msgFrame{
    margin:0!important;
  	border:0!important;
  	border-top:1px solid #eee!important;
  	border-radius:0!important;
  	border-left:0px!important;
  	background-color:#ccc!important;
    }
.read .postHeader,
.unread .postHeader{
  background-color:inherit!important;
  font-size:14px;
  }
.msgBody p{
    margin-top:10px!important;
    }
.unreadCount{
    background-color:inherit!important;
    color:#444!important;
  	border:0!important;
	}
cite{
  	color:#444!important;
  	font-style:italic!important;
	}
blockquote{
  color:#696969!important;
  border-left:3px solid #39c!important;
  padding-left:20px!important;
  margin:20px 0!important;
  display:block!important;
}
h1,
h2,
h3,
h4,
h5{
  color:#696969!important;
  font-weight:bold;
  }
.msgBody a{
  color:#39c!important;
  text-decoration:none;
  }
a.msubject{
  	color:#444!important;
    font-size:26px!important;
  	font-weight:bold!important;
  	line-height:1.2!important;
  	text-align:left!important;
  	}
.mauthor{
  	display:block!important;
  	font-size:14px!important;
  	}
.fromFolder{
  	font-size:14px!important;
  	}
.mtext *{
    font-size:18px!important;
    line-height:1.4!important;
    color:#444;
	font-family:Helvetica!important;
	}
.msgBody{
	padding:30px 0!important;
    max-width:70%!important;
    }
.authorPic{
  display:none!important;
	}
.mtags{
  display:none!important;
	}
.mtextPad{
  	margin-top:15px!important;
  	}
.msgFooter{
  	padding:0 0 30px 105px!important;
  	}
.mauthor a {
  	color:inherit!important;
    }
.msgToolBar,
.subToolBar{
    margin-top:25px!important;	
  	font-size:12px!important;
  	background-color:#eee!important;
  	height:31px!important;
    }
.subToolBar a,
.msgToolBar a{
  	color:#444!important;
    }
.subToolBar a:hover,
.msgToolBar a:hover{
  	color:#39c!important;
    }
.button, 
.textButton,
.button a:hover{
	background-color: inherit!important;
	background-image: none!important;
	padding:4px 10px!important;
  	border:0!important;
  	margin-top:2px!important;
  	margin-left:10px!important;
	}
.left,
.right{
  	margin-top:25px!important;
  	border-top:1px solid #aaa!important;
    }
.right{
  	border-left:1px solid #aaa!important;
  	background-color:#fff!important;
  	}
.left{
  	background-color:#eee!important;
    }
.left li{
    font-weight:normal!important;
  	padding:5px 10px!important;
  	color:#444!important;
  	font-size:12px!important;
  	border-bottom:1px solid #aaa!important;
  	border-left:0!important;
  	}
.left .favicon{
    display:none!important;
    }
.noTouch .subscriptions li:hover{
  	background-color:#ccc!important;
  	border-left:0!important;
    }
.subscriptions li.selected, 
.noTouch .subscriptions li.selected, 
.subscriptions li.selected .buttonChar{
  	border-left:0!important;
  	background-color: #ccc!important;
	}
.subscriptionTitle{
  	font-size:30px!important;
    padding:10px!important;
  	color:#444!important;
  	}
.searchToolBar {
  	height:25px!important;
    border:1px solid #aaa!important;
  	padding:1px!important;
  	margin-top:9px!important;
  	background-color:#fff!important;
  	}
.searchToolBar input{
    width: 400px!important;
    font-size:16px!important;
  	margin:2px 0!important;
  	}
.searchToolBar .button{
  	margin:0!important;
  	padding:3px!important;
  	color:#fff!important;
  	width:50px!important;
  	font-size:15px!important;
	background-color: #39c!important;
	background-image:none!important;
	}
.top{
  	height:52px!important;
    color:#444!important;
  	background-color:#eee!important;
	}
.logo{
  	font-size:22px!important;
	}
.logoBaz,
.logoQux{
  	color:#39c!important;
    }
.logoReader{
  	color:#808080!important;
    }
.logoQux{
    border-right:1px solid #ccc;
    padding-right:7px;
    }
.msgButtons{
    margin:10px;
  	}
}
