css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("friendfeed.com") {
#rightsearch {
margin-right: 750px;
}
a {
text-decoration:none;
}
.feed .cluster .body { 
-moz-outline: 1px solid #6797D3;
-moz-outline-radius: 0.6em ;
padding:3px
} 

#infobox {
position:absolute;
float: left;
margin: 0 780px 10px 20px;
}

a:hover {
text-decoration: none;
}

div {
font-family:tahoma;
font-size:9pt;
line-height:1.7;
}

#body {
direction:rtl;
}

#settingspromo {
position:absolute;
left:0px;
top:20px;
}

#tabhead {border: 0px;}

.logo {
background: #ECF2FA;
border-bottom: 3px solid #6797D3;
}

.rounded {
background: #F9FAFB;
}

.section {
direction:ltr !important;
border-top:solid 2px #6797D3;
padding-top:2px;
}

.streaminfo {
direction: ltr;
margin-left: 380px;
text-align: left;
}

#rightsearch {
position:absolute;
left:45px;
top:0px;
}

.friend {
text-decoration:none;
}

a.friend:hover {
color:black;
border-bottom-color:#000000;
}

.summary {
border:solid 1px #93BAEB;
border-bottom-width:3px;
padding:5px;
background-color:#E8F2FF;
direction:ltr;
}

.entries {
border:solid 1px #E8F2FF;
border-bottom-width:6px;
padding:5px;
background-color:#F9FAFB;
overflow-x:hidden;}

.info {
margin-top:5px;
direction:ltr;
border:1px solid #dde2e4;
padding-left:4px;
background: #f7f9fa;
}

.likes {
direction:ltr;
}
.pager {
/*position:fixed;
left:5px;
bottom:5px;*/
border:solid 1px #6797D3;
padding:2px;
background-color:#F9FAFB;
text-align:center;
margin:0px;
padding:4px;
}

#feedviews {
text-align:center;
direction:ltr;
margin:0px;
padding:0px;
}

#footer {
margin:0px;
text-align:center;
}

.comment {
direction:rtl;
border-right:solid 4px #DCDCDC;
padding-right:4px;
}

.toggle {
direction:ltr;
}

.friendname {
direction:ltr;
padding-bottom:2px;
padding-top:2px;
margin-bottom:0px;
border-bottom:dotted 1px gray;
}

div.friendname:hover {
background-color:#DFEAF8;
}

#sharedialog {
direction:ltr;
}
.feed .entry .comments .comment.friendcomment .editcomment {
direction: ltr !important;
}

.feed .entry .comments .comment.friendcomment{
background-color:#ffffe0 !important; /*Highlight your friends comments*/
}

.feed .entry .comments .comment.owner.friendcomment {
background-color:#e1f0ff !important; /*Highlight your own comments*/
}

.comments textarea, textarea.singleline {
font: normal .95em Tahoma !important;
color: #333 !important;
padding: 2px 3px !important;
}
.content .editcomment {
text-align: left !important;
direction: ltr !important;
display: block !important;
}

.content .friend,.content .friend l_popup {
text-align: left !important;
direction: ltr !important;
display: inline !important;
}

.feed .entry .comments .comment .quote {
background:url("http://i34.tinypic.com/2qckn6d.jpg") 100% 2px no-repeat !important;
display:inline;
float:right !important;
margin-right:5px;
height:12px;width:11px
}
.feed .entry .comments .comment.friendcomment .quote {
background:url("http://i38.tinypic.com/6h0s9u.jpg") 100% 2px no-repeat !important;
}

.userpopup .stats a.comments {
background:url("http://i38.tinypic.com/2wdvwao.jpg") 100% 2px no-repeat !important;
padding:0 12px 0 0;
}

.feed .entry .comments .comment .content {
padding-left:5px !important;
padding-right:15px !important
}
.userpopup .stats a.likes{
padding-left:20px !important;
}


}
