css:
    @namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"),
    @namespace url("http://www.w3.org/1999/xhtml");
    
    #bookmarks-view, #ybSidebarPanel, #history-panel, #addon-bar,
    #paneMain, #BrowserPreferences, #FindToolbar, #placesToolbar,
    #placesView, #addons-page, #clientBox, #commonDialog, window
    {background: white !important;}

    #organizeBookmarksSeparator, #searchbox, #appmenu-button,
    #identity-box, #star-button, #throbber, #bookmarksPanel > hbox,
    #urlbar-go-button, .autocomplete-history-dropmarker, .tab-icon-image,
    .search-go-button, .searchbar-engine-button, .tab-throbber, menuseparator,
    .tabs-newtab-button, #clearListButton[disabled="true"], #searchbox,
    marquee, #back-button[disabled="true"], #forward-button[disabled="true"],
    .tabs-alltabs-button, .tabs-closebutton-box, .tab-close-button,
    .category[hidden="true"], .category[disabled="true"], .category-icon,
    #back-btn, #forward-btn, #searchFilter .textbox-search-icon,
    #header-search .textbox-search-icon, menuitem[disabled="true"],
    .textbox-search-clear, #notification-popup-box,
    #status-bar {display: none !important;}

    .sorter {border-radius: 0px !important; background: transparent
    !important; color: black !important;}
    #header {margin-bottom: 9px !important; margin-top: -7px !important;}
    #view-port-container {border-radius: 0px !important;
    border: 1px solid #F5F5F5 !important;}
    #categories {margin-top: 0 !important; margin-bottom: 0px !important;
    background: #F5F5F5 !important;}
    .category { font-size: 90%; direction: rtl !important;
    height: 30px !important; padding: 0px 4px 0px 4px !important;}
    #addon-list .addon[selected="true"], #updates-list
    .addon[selected="true"] {background: #F5F5F5 !important;}
    .category[selected="true"] {border: 0px !important;
    border-radius: 0px !important; background: white !important;}
    .category-badge {background: transparent !important; color: black !important;}
    #list-view > #addon-list .addon.addon-view, #updates-list .addon.addon-view,
    #search-list .addon-view{padding: 1px 1px 2px 5px !important;}
    #list-view .addon.addon-view * {-moz-box-orient: horizontal !important;
    -moz-box-align: center !important; font-size: 100% !important;}
    #list-view > #addon-list .icon-container, #list-view > #addon-list .icon
    {height: 16px !important; width: 16px !important;}
    #search-list .addon-view[selected="true"] {background: #F5F5F5 !important;}
    #search-filter-remote[selected="true"], #search-filter-local[selected="true"]
    {border-radius: 0px !important; background: black !important;}
    
    #search-filter-label {color: black !important;}

    sidebarheader {-moz-appearance: none !important; background: black !important;
    color: white !important; margin: -1px !important; font-weight: bold !important;}
    #sidebar-splitter {background: black !important; border: -1px !important;}
    statuspanel {size: 14px; max-width: 100% !important; left: 0%!important;} 
    .statuspanel-label {color: white !important; background: black !important; border: 0px !important;}

    body :focus {outline: 2px solid orange !important;
    outline-offset: -1px !important; -moz-outline-radius: 0px !important;}
    body a:focus {outline-offset: -2px !important;}
    body textarea:focus, body button:focus, body select:focus,
    body input:focus {outline-offset: -1px !important;}

    #PopupAutoCompleteRichResult .autocomplete-richlistbox {background: transparent
    -moz-linear-gradient(left, #F5F5F5 27px, #F5F5F5 28px, transparent 0) repeat-y !important;
    background-size: 28px auto !important; margin: 1px !important;}
    #PopupAutoCompleteRichResult .ac-site-icon {margin: 0 12px 0 0 !important;}
    #PopupAutoCompleteRichResult .ac-title-box {margin: 0!important; padding: 0 !important;}
    #PopupAutoCompleteRichResult .ac-url-box {margin: 1px 0 0 0 !important; padding: 0 !important;
    -moz-transition: opacity .21s linear 0.02s, -moz-transform .21s ease-out 0.02s !important;}

    .autocomplete-richlistitem {border-radius: 0 !important; outline: 0px !important;
    border: 1px solid transparent !important; border-top: 1px solid #F5F5F5 !important;
    box-shadow: none !important; padding: 3px 5px !important; margin: 0 !important;}
    .autocomplete-richlistitem[current] {border-color: black !important;
    background: transparent !important; box-shadow: inset black 0 0 0 1px !important;}
    .autocomplete-richlistitem[actiontype="switchtab"] {background: #F5F5F5 !important;}
    .ac-comment {font-family: Arial; font-size: 12px; font-weight: normal;}
    .ac-url-text {font-size: 12px !important; color: gray !important;}

    #downloadsRichListBox, #placesList, #placeContent, #detailsDeck
    {color: black !important; background: transparent !important;}
    treechildren::-moz-tree-row(current, focus), treechildren::-moz-tree-row(selected)
    {background: black !important; color: white !important; border: none !important;}
    treechildren::-moz-tree-cell-text(container,selected),
    treechildren::-moz-tree-cell-text(selected) {color: white !important;}

    popup, menupopup, popup menu, popup menuitem, menupopup menu, menupopup menuitem
    {background: black !important; -moz-appearance: none !important;
    color: white !important; font-weight: normal !important;
    opacity: 1 !important; padding: 2px 0 !important;}
    contex-menu, popup menu, popup menuitem
    {background: black !important; -moz-appearance: none !important;
    color: white !important; font-size: 12px !important; font-weight: normal !important;}
    menupopup menu, menupopup menuitem {background: black !important;
    color: white !important; font-weight: normal !important;}
    menupopup menu:hover, popup menu:hover, menuitem:hover {background: white !important;
    -moz-border-radius: 12px !important; color: black !important;}
    menupopup, popup {border: 0px !important;}
    .menu-right {-moz-appearance: none !important;
    list-style-image: url("http://i.imgur.com/j3KwJgk.png") !important;}

    scrollbarbutton[sbattr="scrollbar-up-top"], scrollbarbutton[sbattr="scrollbar-down-top"],
    scrollbarbutton[sbattr="scrollbar-up-bottom"], scrollbarbutton[sbattr="scrollbar-down-bottom"]
    {-moz-appearance: none !important; visibility: collapse !important;}
    scrollbar[orient="vertical"] {-moz-appearance: none!important;
    position: relative; background-color: transparent; background-image: none;
    -moz-box-align: start; border: 0; padding: 0px 0px 0px 8px !important;
    -moz-margin-start: -15px; width: 15px; z-index: 2147483647;}
    scrollbar[orient="horizontal"] {-moz-appearance: none!important;
    position: relative; background-color: transparent; background-image: none;
    -moz-box-align: start; border: 0; padding: 9px 0px 0px 0px !important;
    margin-top: -15px; height: 15px; z-index: 2147483647;}
    scrollbar[orient="vertical"] thumb {width: 15px !important;}
    scrollbar[orient="horizontal"] thumb {height: 15px !important;}
    scrollbar thumb {-moz-appearance: none !important;
    background: gray; opacity: 0.7; border: 0;}

    #main-window[tabsontop="true"][sizemode="normal"] #TabsToolbar
    {margin-right: 100px !important;} .tabbrowser-tabs
    {margin-left: -5px !important; margin-top: -15px !important;
    margin-bottom: 0px !important; max-height: 24px !important;}
    #main-window[tabsontop="true"][sizemode="maximized"] .tabbrowser-tabs
    {margin-left: -5px !important; margin-top: 0px !important;
    margin-bottom: 0px !important; max-height: 24px !important;}
    #main-window[tabsontop="true"][sizemode="fullscreen"] .tabbrowser-tabs
    {margin-left: -1px !important; margin-top: 0px !important;
    margin-bottom: 0px !important; max-height: 24px !important;}
    tab[selected="true"] {opacity: 1.00 !important; font-weight: bold !important}
    .tab-text {text-align: center !important; padding-right: 9px !important;
    padding-left: 9px !important; margin-bottom: 2px !important;}
    .tabbrowser-tab {margin-left: 2px !important; background: white !important;
    border-width: 0px !important; border-radius: 0px !important;}
    tab {opacity: 0.50 !important;}

    #back-button {list-style-image: url("http://i.imgur.com/0W8YhSm.png") !important;
    -moz-image-region: rect(0px, 18px, 18px, 0px) !important;}
    #forward-button {list-style-image: url("http://i.imgur.com/djxCpn7.png") !important;
    -moz-image-region: rect(0px, 18px, 18px, 0px) !important;}
    #urlbar-reload-button {list-style-image: url("http://i.imgur.com/WNZYpMy.png") !important;
    -moz-image-region: rect(0px, 18px, 18px, 0px) !important;}
    #urlbar-stop-button {list-style-image: url("http://i.imgur.com/0keejv8.png") !important;
    -moz-image-region: rect(0px, 18px, 18px, 0px) !important;}

    #main-window[sizemode="fullscreen"] {background: black !important;}
    #main-window[sizemode="normal"], #main-window[sizemode="maximized"]
    {background: transparent !important;}

    #status-bar {-moz-appearance: none !important; border: none !important;
    min-height: 18px !important; max-height:18px !important; padding-top: 0px !important;}
    
    #clearListButton {-moz-appearance: none !important;
    background: white !important; color: black !important; border: 0px !important;}
    
    #aboutDialog #updateButton {-moz-appearance: none !important; font-weight: bold !important;
    background: white !important; color: black !important; border: 0px !important;}
    #leftBox {background-image: url("http://i.imgur.com/b0GD77s.png") !important;}

    #downloadView {background: white !important; color: black !important;}
    #downloadView > richlistitem[selected="true"] {background: black !important; color: white !important;}
    
    #btTooltip, #un-toolbar-tooltip, #tooltip, .tooltip, #aHTMLTooltip, #urlTooltip, tooltip
    {-moz-appearance: none !important; border-color: black !important;
    background: black !important; color: white !important;}
    
    #nav-bar, #PersonalToolbar {padding-left: 5px !important;
    background: white !important; border-width: 0px !important;
    margin-left: 1px !important; margin-right: 1px !important;
    margin-top: -2px !important; margin-bottom: -2px !important;}
    
    #viewSource-toolbox {-moz-appearance: none !important;
    background: black !important; border: 0px !important;}
    #menu_file, #menu_edit, #menu_view, #helpMenu, #file-menu,
    #edit-menu, #view-menu, #history-menu, #bookmarksMenu,
    #tools-menu {color: white !important; text-shadow: none;}

    #urlbar, #searchbar .searchbar-textbox {font-family: Arial !important;
    font-size: 14px !important; background: white !important;
    border-width: 0px !important; height: 24px; !important;}
    
    .progress-bar {-moz-appearance: none !important; background: black !important;}
    ::-moz-selection {background: black; color: white; text-shadow: none;}
    blink {text-decoration: none !important;}
