css:
@-moz-document domain("yoyogames.com") {
#header_bar {
    background: #323232;
}
#primary_nav {
    background: #323232 !important;
}
#community_app_menu>li {
    margin: 0 0 0 0;
}
#search_wrap .choice {
    color: #FDFDFD;
}
#search_wrap {
    border-radius: 0px;
}
#search_wrap .submit_input.clickable {
    border-radius: 0px;
}
#search_options {
    border-radius: 0px;
    background: #929292;
}
#primary_nav {
    padding: 0px;
}
#primary_nav ul li a:hover {
    background: #71A034 !important;
    color: white !important;
    text-shadow: none;
}
#secondary_navigation li a{
    background: #365620;
    padding: 6px 15px 8px;
    font-size: 13px;
    margin: 0px 0px 0px 0px;
}
#secondary_navigation li a
{
    position: relative;
    margin-right: -9px;
}
#secondary_navigation li:last-child a
{
    background: #71A034;
}
#secondary_navigation li:last-child a
{
    color: #EEE;
    font-weight: bold;
    text-shadow: none;
}
#secondary_navigation li a
{
    color: #C5D5E2;
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5);
}
#secondary_navigation li a:hover
{
    color: #EEE !important;
    background: #71A034;
    text-shadow: none;
}
.nav_sep
{
    display: none;
}
.maintitle {
    border-radius: 0px !important;
}
.submit_input.clickable {
    border: 0px!important;
}
.breadcrumb.top.ipsList_inline.left {
    position: relative;
    top: 7px;
    left: 88px;
}
#secondary_navigation {
    background: #323232;
    position: absolute;
    left: 0px;
    right: 0px;
    top: 130px;
    height: 30px;
}
#branding {
    background: #323232 !important;
    border: 0px;
}
#header_bar {
    background: #242424;
}
#primary_nav {
    background: #242424 !important;
}
#content {
    background: #242424 !important;
    box-shadow: none;
}
#ipbwrapper {
    background: #242424;
}
#community_app_menu .right {
    float: left;
}
#search .submit_input {
    background-color: #71A034 !important;
    box-shadow: none;
    -webkit-box-shadow: none;
}
.ftoggle.ipsButton_secondary {
    border-radius: 0px;
    border: 0px;
    box-shadow: none !important;
}
.ftoggle.ipsButton_secondary:hover {
    background: #71A034 !important;
    color: #EEE !important;
}
.topic_buttons li a {
    border-radius: 0px;
    background: #323232 !important;
    border: 0px;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
}
.topic_buttons li a:hover {
    color: #EEE !important;
    text-shadow: none;
    background: #71A034 !important;
}
#user_link {
    border-radius: 0px !important;
}
.ipsHeaderMenu {
    border-radius: 0px;
}
#statusSubmitGlobal {
    border-radius: 0px;
}
.ipsBox {
    padding: 0px;
    border: 0px !important;
}
#ips_Posts {
    border: 0px;
}
.topic_buttons li span {
border-radius: 0px;
background: #323232 !important;
border: 0px;
box-shadow: none !important;
-webkit-box-shadow: none !important;
}
.ipsButton_extra.right._fmore strong:after {
content: " Member(s) following this topic";
}
.ipsButton_extra.right._fmore img {
display: none;
}
#rep_filter {
display: none;
}
p.citation {
    border-radius: 0px;
    border: 0px;
}
.ipsBlockquote.built {
    border: 2px solid #989898;
    border-radius: 0px;
}
.post_controls li a {
    background: #365620 !important;
    border: 0px;
    border-radius: 0px;
    box-shadow: none;
    color: #EEE !important;
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5);
    padding-top: 2px !important;
    padding-bottom: 2px !important;
    opacity: 1 !important;
    -webkit-transition: none !important;
    height: 20px;
    line-height: 20px;
    position: relative;
    margin-right: -4px
}
.post_controls li a:hover {
    background: #71A034 !important;
    color: #EEE !important;
    text-shadow: none;
}
.reputation {
    border-radius: 0px;
}
.ipsTag {
    display: none;
}
.post_body .posted_info.desc {
    color: rgb(117, 117, 117);
}
.hide_signature {
    display: none;
}
a.rep_up img,a.rep_down img {
    display: none;
}
a.rep_up {
    background-image: url(http://i.imgur.com/jkTas3V.png);
    width: 19px;
    height: 19px;
    display: block;
    position: relative;
    top: 6px;
}
a.rep_down {
    background-image: url(http://i.imgur.com/A6XV9Vv.png);
    width: 19px;
    height: 19px;
    display: block;
    position: relative;
    top: 6px;
}
.group_icon img {
    display: none;
}
.custom_fields .fc {
    color: white;
}
.custom_fields .ft {
    color: #818181;
}
.pagination .pages li.active {
    border-radius: 0px;
}
.pagination .forward a {
    border-radius: 0px;
}
.ipsFilterbar li.active a, .ipsFilterbar.bar.altbar li.active a {
    border-radius: 0px !important;
    -webkit-transition: none;
    border: 0px;
    box-shadow: none;
}
.ipsBadge {
    border-radius: 0px;
}
.ipsType_pagedesc a {
    text-decoration: none;
}
.topic_buttons li a {
    background: #365620!important;
}
a[data-clicklaunch=forumMarkRead],.topic_buttons li.non_button a
{
    background: #365620!important;
    color: #EEE!important;
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5)!important;
}
a[data-clicklaunch=forumMarkRead]:hover,.topic_buttons li.non_button a:hover
{
    background: #71A034!important;
    color: #EEE!important;
    text-shadow: none!important;
}
a[data-clicklaunch=forumMarkRead] img {
    display: none;
}
#submit_post {
    border-radius: 0px;
    border: 0px;
    box-shadow: none !important;
}
#full_compose {
    border-radius: 0px;
    border: 0px;
    box-shadow: none !important;
}
.pagination .back a {
    border-radius: 0px;
}
.pagination .pages a:hover {
    border-radius: 0px;
}
.desc.lighter.blend_links {
    color: rgb(112, 112, 112);
}
.desc.lighter.blend_links a:hover {
    color: white !important;
    text-decoration: underline;
}
.input_submit {
    border-radius: 0px;
    border: 0px;
    box-shadow: none !important;
}
}
