css:
@-moz-document domain("cab.vn"), domain("beta.cab.vn") {
body{
color: #fff !important;
background: url("http://i.imgur.com/8ajIyoR.jpg") repeat fixed center center #000000 !important;
}
::selection {
    background: #9f9
}
::-webkit-selection {
    background: #9f9
}
::-moz-selection {
    background: #9f9
}
.title {
    margin-top: 70px;
}
.first {
    transition: all 0.4s;
    border-radius: 2px;
    background-color: #000;
}
.first:hover {
    background-color: #1385a9;
}
.last {
    transition: all 0.4s !important;
    border-radius: 2px !important;
    background-color: #000 !important;
}
.last:hover {
    background-color: #1385a9 !important;
}
.category-active, .btn, .submit-comment input {
 	background: #3695c6 !important;
 	border-color: #3695c6 !important;
	transition: color .3s ease-in-out !important;
 	transition: all .3s !important;
 	box-shadow: 0px 1px 2px rgba(0,0,0,0.3), 0 1px 0 rgba(255, 255, 255, 0.3) inset !important;
  	text-shadow: 0 -1px 0 rgba(0,0,0,0.3) !important;
  	border-radius: 2px !important;
}
.btn:hover, .category-active:hover, .submit-comment input:hover {
    background: #7AA300 !important;
  	border-color: #7AA300 !important;
}
.current {
    background-color: #1385a9;
}
.avatar-pic{
    border-radius: 100% !important;
  	border: 2px solid rgba(0, 100, 180, 0.8) !important;
  	transition: 0.3s !important;
}
.avatar-pic:hover {
    box-shadow: 0 0 0 2px rgba(255, 255, 255, 0.8) !important;
    border-radius: 5px !important; 
  	-webkit-transform: scale(1.8,1.8) !important;
    -moz-transform: scale(1.8,1.8) !important;
	-o-transform: scale(1.8,1.8) !important;
}
.cm-user-avatar img{
	border-radius: 100% !important;
  	border: 1px solid #333333 !important;
  	transition: 0.4s !important;
  	box-shadow: 0 0 0 2px rgba(0, 0, 0, 0.3) !important;
}
.cm-user-avatar img:hover {
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.5), 0 0 5px rgba(0, 0, 0, 0.5) !important;
    border-radius: 5px !important;  
}
.page {
    padding: 1px;
    background-color: #000;
    border-radius: 2px;
    transition: all 0.4s;
}
.alert-info {
  	background: transparent;
    color: #CCC;
   	text-shadow: 1px 1px 2px #000;
    border: none;
    font-weight: bold;
}
.row-fluid {
    background-color: transparent !important;
}   
.page:hover {
    background-color: #1385a9;
}
a, p{
    color: #fff !important;
    text-shadow: 2px 2px #323232 !important;
    transition: all 0.4s;
}
a:hover, p:hover {
    color:#FFF !important;
}
.list-header {
    border-radius: 4px;
}
.info-post {
    background: #000 !important;
    color: #fff !important;
    text-shadow: 1px 1px #121212 !important;
    border-radius: 1px;
    height: 35px;
  	padding-top: 70px;
}
header{
    border-bottom: 1px solid #000 !important;
  	margin-top: 40px;
}
legend, span{
    color: white !important;
    text-shadow: 2px 2px #323232 !important;
}
.navbar-inner{
  	color: #fff !important;
    background-color: #000 !important;
	opacity:0.8 !important;
    border-bottom: 1px solid #000 !important;
    font-size: 11px !important;
    z-index: 22;
  	margin-top: 5px;
}
#player, .full-frame {
    z-index: 15;
}
.vote-score {
  	color: #fff !important;
    background-color: #000 !important;
	opacity:0.8 !important;
    border-bottom: 1px solid #000 !important;
    font-size: 11px;
}
.navbar {
    color: #fff !important;
    background-color: #000 !important;
    border-bottom: 1px solid #000 !important;
    font-size: 11px;
    position:fixed;
    width: 100%;
    transition: all 0.4s;
    z-index: 21;
  	height: 40px;
}
#right-column {
    margin-top: 56px;
}
.navbar:hover {
    opacity: 1 !important;
}
.container-fluid {
    width: 1010px !important;
    margin: 0 auto;
}
.list-header{
  	background: #000 !important;
  	color: #fff !important;
	text-shadow: 1px 1px #121212 !important;
}
.menu-hot a {
  	transition: all 0.2s;
  	border-radius: 2px !important;
}
.menu-hot a:hover {
   	background: #7AA300 !important;   
}
.vote, .vote-tag {
    margin-top: 0px;
}
.vote-post, .vote-row-1 td {
    border: 1px dotted #888888 !important;
}
.content-post {
    background: #000 !important;
    color: #fff !important;
    text-shadow: 1px 1px #121212 !important;
  	opacity: .85 !important;
    margin-top: 20px;
    border-radius: 3px;
}
.content-body, .inner{
    background: transparent !important;
}
.user-single-page {
    background: #000 !important;
    opacity:0.9 !important;
    color: #fff !important;
}
.result-post, .type-post{
    background: #3695c6 !important;
	opacity:0.8 !important;
    color: #fff !important;
	text-shadow: 1px 1px #121212 !important;
    border-top: 5px solid #000 !important;
    border-bottom: 5px solid #000 !important;
}
.type-post{
  	margin-top: 10px;
   	background: transparent;
  }
.back-to-top {
    display: none !important;
}
.span8 {
border-right: none !important;
}
}
