css:
@-moz-document domain("yify-torrents.com") {
#top-panel, #header, #footer, .torrentCount, .rssBrowse, .divider, .full-width h2, .std-btn span, .std-btn-small span, .browse-info br, #comment-wrapper {display:none!important;}
* {text-shadow:none!important; font-family:arial;}

body {background:#FFF!important;}
#container {background:#FFF!important; border:none!important;}
#content {margin:0!important;}
#content .full-width {background:#FFF!important; width:970px!important;}
#content .browse {width:970px!important; margin:30px 0!important;}

.SearchPanel {height: 44px!important; width: 948px!important; overflow:hidden!important; margin: 40px 0 30px!important; padding:10px!important; background:#FAFAFA!important; border-color:#DDD!important;}
.SearchPanel:hover {height: 94px!important;}
.searchForm #keywords.field {width: 730px!important; border-color: #DDD!important;}
.searchForm #keywords.field:focus {border-color: #CCC!important;}
.searchForm .std-btn {background: -webkit-linear-gradient(top,#4d90fe,#357ae8)!important; border: 1px solid rgba(0, 0, 0, 0.25)!important; border-radius:3px!important; top: 16px!important; right: 20px!important;}


.browse-content {border:none!important; margin-bottom:20px!important;}
.browse-wrapper {width: 315px!important;}

.browse-img {height: 168px!important; width: 117px!important; overflow: hidden!important; margin-right: 15px!important;}
.browse-img img {margin-top:-17px!important;}

.browse-info .info {min-height:144px!important;}
.browse-info .browseInfoList {width: 165px!important; display: block!important;}
.browse-info .info .browseSeeds {position:static!important; margin-bottom: 17px!important;}

.std-btn-small {background: -webkit-linear-gradient(top,rgba(0, 0, 0, 0),rgba(0, 0, 0, 0.07))!important; border: 1px solid rgba(0, 0, 0, 0.10)!important; border-radius:3px!important; color:#555!important;}
.std-btn-small:hover {color:#FFF!important; background: -webkit-linear-gradient(top,rgba(0, 0, 0, 0),rgba(0, 0, 0, 0.37))!important; background-color: rgba(0, 182, 65, 1)!important;}
}
