css:
@-moz-document domain("upc.blackboard.com") {
	#contentFrame, #schemePreview, body,
	.topTabs {
		background: url("data:image/png;base64,") ! important}
	.topTabs {
		box-shadow: 0 -6px 16px -5px #533 inset !important}
	.brandingImgWrap {
		background: url("data:image/png;base64,") no-repeat 5px !important}
	.brandingImgWrap img, .bannerImage {
		background-image: url("data:image/png;base64,") !important;
		height: 90px !important;
		color: Transparent !important}
	div.loginBody {
		background: url("data:image/png;base64,") !important;
		background-repeat: no-repeat !important;
		background-position: center 94px !important}
	.portal .contentBox {
		background: rgba(248,248,248,.6) !important}

	moduleTitle, .portlet h2,
	/* Buttons , etc */
	#primaryButton, input.submit[type="submit"], .rumble .mainButton > a, .rumble_top .mainButton > a, .button-1, .button-1-img {
		background: #991436 !important}
	#primaryButton, input.submit[type="submit"], .rumble .mainButton > a, .rumble_top .mainButton > a, .button-1, .button-1-img,
	#loginBox input[type="text"]:focus, #loginBox input[type="password"]:focus, #loginBox input[type="text"]:hover, #loginBox input[type="password"]:hover {
		border-color: #991436 !important}
	.collapsible {
		border-color: #B4B4B4 !important}
	.steptitle, .submittitle,
	a:hover, .portlet .subCategories li a:hover, .blockGroups .itemHead:hover, .blockGroups .itemHeadOpen:hover {
		color: #991436 !important}
}
