css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.reddit.com/r/smashbros"), url-prefix("http://reddit.com/r/smashbros"), url-prefix("reddit.com/r/smashbros") {

    /* Submission */
    .res-nightmode .title {
        color: black !important;
    }
    .res-nightmode .buttons li a{
        color: #777777 !important;
    }
    .res-nightmode time {
        color: #888888 !important;
    }
    .res-nightmode .domain a {
        color: #3d5e6a !important;
    }
    .res-nightmode .author {
        color: #548bdd !important;
    }
    .res-nightmode .midcol, .res-nightmode .arrow {
        background-color: transparent !important;
    }
    .res-nightmode .arrow {
        background-image: url('data:image/png;base64,') !important;
    }
    .res-nightmode .score.unvoted {
        color: #777777 !important;
    }
    .res-nightmode .flair {
        background-color: transparent !important;
    }

    /* Submission Text */
    .res-nightmode .usertext-body .md {
        color: #000000 !important;
    }
    .res-nightmode .usertext-body .md a {
        color: #548bdd !important;
    }
    .res-nightmode .usertext-body .md a:hover {
        color: #9bb5dd !important;
    }
    
    /* Comments */
    .res-nightmode .commentarea {
        background-color: transparent !important;
    }
    .res-nightmode .panestack-title .title {
        color: #FFFFFF !important;
    }
    .res-nightmode .comment .md {
        color: #DDDDDD !important;
    }
    
    /* RES Exclusive */
    .res-nightmode .voteWeight {
        background-color: transparent !important;
    }
}
