css:
#st-descr,#stage,#media-title,html,#playlist #playlistcontrols{background-color:black}
.top-descr, #addVid{background-color:#111;border:1px solid rgba(0, 195, 255, 0.35);border-radius:6px;box-shadow:rgba(0, 195, 255, 0.35) 0 0 15px}
#playlist .playlist #playlist_total {border: 1px dotted rgba(0, 195, 255, 0.35);border-top: none!important}
#truetop{background: #262626;
background: -moz-linear-gradient(top, #262626 0%,#000000 100%);
background: -webkit-gradient(linear,left top, left bottom,color-stop(0%,#262626),color-stop(100%,#000000));
background: -webkit-linear-gradient(top,#262626 0%,#000000 100%);
background: -o-linear-gradient(top,#262626 0%,#000000 100%);
background: -ms-linear-gradient(top,#262626 0%,#000000 100%);
background: linear-gradient(to bottom,#262626 0%,#000000 100%);
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#262626',endColorstr='#000000',GradientType=0);border-color:rgba(0, 195, 255, 0.35)}
#loggedInAsMSG{color:#0d7;font-size:10px}
#loggedInAs{border:1px solid #028;border-radius:6px;background-color:rgba(100,100,100,.05)}
#vidTitle.title, .title{color:#16CC2C}
.via {color:#DD14CA}
.top-descr{color:black!important;text-shadow: 0 -1px #EE9900, 1px 0 #0099EE, 0 1px #0099EE, -1px 0 #EE9900!important;}
.descr-stat-value{color:#e90!important;text-shadow:none}
.descr-stat-tip{color:#006bfe!important;text-shadow:none}
#playlist .playlist ul li{background-color:black;border-color:rgba(0, 195, 255, 0.35);border-bottom:none}
#playlist .playlist ul li:hover{background-color:#111!important}
.total-duration{color:#EE9900!important}
.duration{color:#EE9900!important}
.total-videos{color:#006BFE}
#playlist_items{border-color:#002BBE;box-shadow:0 0 10px #006BFE;border-width:1px;overflow:auto}
#skipCounter{color:#006BFE!important;border-radius:4px;background-color:#000033}

#playlist .playlist ul li.active {
  background: -webkit-linear-gradient(top, rgba(0,0,0,1) 50%,rgba(127,0,127,1) 100%) !important;
  background: -moz-linear-gradient(top, rgba(0,0,0,1) 50%,rgba(127,0,127,1) 100%) !important;
  border-color:#004bde;
  
	
 }
#playlist .playlist ul li.active:hover{border-color:red!important}

#playlistactions .basic-btn
{ color:#16CC2C !important;
border-color:#006BFE !important;
-moz-border-radius: 3px !important;
-webkit-border-radius: 3px !important;
-o-border-radius: 3px !important;
-ms-border-radius: 3px !important;
border-radius: 3px !important;
background: -webkit-linear-gradient(top, #45484d 0%,#000000 100%);}
#playlistactions .basic-btn:hover{
    border-color:#00FFFF !important;
box-shadow: 0px 0px 20px #006BFE}
#playlistactions .basic-btn:active{
    border-color:#EE9900 !important;
box-shadow: 0px 0px 20px #EE9900}
.user_list .m span,h2{
color:#005abc!important;
text-shadow: 0 -1px #00bdff, 1px 0 #00bdff, 0 1px #00bdff, -1px 0 #00bdff !important}
span.cun.c1m,div.user_list{color:#ccc!important; text-shadow: 0 -1px #666, 1px 0 #666, 0 1px #666, -1px 0 #666}
span.cun.c1m.r,div.user_list .b{text-shadow:none;color:black!important}

#bio
{ color: #16cc2c!important;
background: rgba(0,0,0,.9);
box-shadow: 0px 0px 10px -1px #006BFE;
border: 1px solid rgba(0,107,254,1);
border-radius: 5px;
}
#bio .avatar{
border-color: #006BFE !important}
#bio .userinfo
{ color: #EE9900!important;
background: rgba(0,0,0,.4);
box-shadow: 0px 0px 10px -1px #006BFE;
border: 1px solid rgba(0,107,254,1);
border-radius: 5px;
}
.removeBtn{left:19px;bottom:14px;font-size:0px;content:url(http://i.imgur.com/dIrt6yQ.png)}
