css:
/*
 * Smoother Klei Forums version 1.1
 * by simplex
*/

/* Default version */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.kleientertainment.com") {
	/* Backgrounds */

	#content {
		background: #c8c8a0 !important;
	}

	.post_block h3 {
		background: #969696 !important;
	}
	
	.post_wrap {
		background: #dcdcdc !important;
	}



	/* Text */

	.post_body, .post {
		color: #282828 !important;
	}

	.desc.lighter, .desc.blend_links, p.desc {
		color: #404040 !important;
	}

	.signature {
		color: #606060 !important;
		word-wrap: break-word !important;
	}



	/* Links */

	.desc.lighter, .desc.lighter.blend_links
	a {
		color: #505050 !important;
	}

	.desc, .desc.blend_links
	a {
		color: #b48c64 !important;
	}
	
	.pages
	a {
		color: #787878 !important;
	}
	
	.desc.blend_links.post_id
	a {
		color: #78b450 !important;
	}



	/* Lua syntax highlighting */

	pre .str, code .str { color: #bf0303 !important; } /* string  */
	pre .kwd, code .kwd { color: #b08000 !important; } /* keyword */
	pre .com, code .com { color: #646464 !important; font-style: italic !important; } /* comment */
	pre .typ, code .typ { color: #5555ff !important; } /* type */
	pre .lit, code .lit { color: #4f3063 !important; } /* literal */
	pre .pun, code .pun { color: #006e28 !important;  } /* punctuation */
	pre .pln, code .pln { color: #5555ff !important; } /* plaintext */
	pre .dec, code .dec { color: #4f3063 !important; } /* decimal */
}
