css:
@-moz-document url-prefix("http://ucp.gtawc.net") {
/* Override some defaults */
html, body {
	background-image: url("http://www.gtawc.net/ftp-design/img/Westcoast_Metro_Blue.png");
                background-repeat: no-repeat;
                background-size: 100%;
  background-attachment:fixed;
}

.mouseover-tablesorter {
    cursor:pointer;
}
h_link {
font-size: 13px;
font-weight: bold;
text-decoration:underline;
}
.controls_user {
padding-top: 15px;
margin-left: 190px;
}
.form-horizontal_user .control-label_user {
  float: left;
  width: 170px;
  padding-top: 5px;
  text-align: right;
}
.control_2 {
padding-top: 10px;
margin-left: 190px;
}
.form-horizontal_2 .control-label_2 {
  float: left;
  width: 170px;
  padding-top: 5px;
  text-align: right;
}
.radio.inline{
padding-top:5px;
}
.span_header {
    width: 1140px;
}
.span_content1 {
    width: 900px;
}
.span_sidebar {
    width: 130px;
}
.span_info1 {
    width: 110px;
}
.span_info2 {
    width: 155px;
}
.span_lizenz {
    width: 100px;
}
.span_waffenlizenz {
    width: 259px;
}
.span_skin {
    width: 175px;
}
.span_cars {
    width: 175px;
}
.span_fraktion {
    width: 259px;
	min-height: 75px;
  

}

.span_fraktion h3 {
       font-family: Arial;
  font-weight: 800;
          color: #000000;
         text-shadow: 0px 0px 2px #000000;
            filter: dropshadow(color=#000000, offx=0, offy=0);
  }
  
.rand1 {
	text-align:center;
	border-width:1px;
	border-style:solid;
	padding: 5px;
}
.container > footer p {
	padding-top: 25px;
	text-align: center; /* center align it with the container */
}
.container {
	width: 1150px; /* downsize our container to make the content feel a bit tighter and more cohesive. NOTE: this removes two full columns from the grid, meaning you only go to 14 columns and not 16. */
}

.thumbnails > li {
  margin: 0px;
}

.thumbnails {
  margin-left: 0px;
}

.imgCenter {
  text-align: center;
  height: 70px;
  vertical-align: middle;
  display: table-cell;
}
.imgCenter * {
    vertical-align: middle;
}
.help-inline {
	color: #B94A48;
}
/* The white background content wrapper */
.content {
	background:rgba(255,255,255,0.5);
	padding: 20px;
	margin: 0 -20px; /* negative indent the amount of the padding to maintain the grid system */
	background:rgba(255,255,255,0.5);
                -moz-border-radius: 10px;
                -webkit-border-radius: 10px;
                -khtml-border-radius: 10px;
                border-radius: 10px;
	box-shadow: 0px 0px 3px rgba(0,0,0,0.5);
                -webkit-box-shadow: 0px 0px 3px rgba(0,0,0,0.5);
                -moz-box-shadow: 0px 0px 3px rgba(0,0,0,0.5);
                -khtml-box-shadow: 0px 0px 3px rgba(0,0,0,0.5);
	border-style: solid;
	border-width: 2px;
  border-color: #000000;
}

/* Give a quick and non-cross-browser friendly divider */
.splitter {
	padding-right: 20px;
	padding-left: 20px;
	border-left: 1px solid #eee;
}

/* Autofill Box */

.suggestionsBox {
	position: relative;
	left: 30px;
	margin: 10px 0px 0px 0px;
	width: 200px;
	background-color: #212427;
	-moz-border-radius: 7px;
	-webkit-border-radius: 7px;
	border: 2px solid #000;	
	color: #fff;
}

.suggestionList {
	margin: 0px;
	padding: 0px;
}

.suggestionList li {
	
	margin: 0px 0px 3px 0px;
	padding: 3px;
	cursor: pointer;
}

.suggestionList li:hover {
	background-color: #659CD8;
}

.addInput {
	padding-top: 14px;
	text-align: right;
}

.label.leader {
  background-color: #0000CD	;
}

.smallsize {
  font-size: 11px;
  color: #999999;
}
form {
  margin: 0px;
}
.nav-tabs > .active > a, .nav-tabs > .active > a:hover {
  color: #555555;
  background-color: #ffffff;
  border: 1px solid #ddd;
  border-bottom-color: transparent;
  cursor: pointer;
}
.hero-unit {
  padding: 15px;
  margin-bottom: 10px;
  background-color: #FFCBAD;
  -webkit-border-radius: 6px;
  -moz-border-radius: 6px;
  border-radius: 15px;
  border-color: #BBBBBB;
  border-style: solid;
  border-width: 1px;
}
.navbar-fixed-bottom {
position: fixed;
bottom: 0;
right: 0;
left: 0;
z-index: 1030;
margin-bottom: -1px;
}
.timerfooter {
  border-top: 1px solid #ddd;
}
.label {
padding: 1px 4px 1px;
}
a.info:hover span
{
display:block;
position:absolute;
top:2em;
left:2em;
}

p {
font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
font-size: 13px;
line-height: 18px;
}

.navbar-inverse .navbar-inner {
background-image: -webkit-linear-gradient(top, #444, #111);
}


input[disabled],
select[disabled],
textarea[disabled],
input[readonly],
select[readonly],
textarea[readonly] {
  cursor: not-allowed;
  background-color: #FFE9D2;
}


.form-horizontal_rp  .control-label_rp {
float: left;
width: 150px;
padding-top: 5px;
text-align: right;
}
.controls_rp {
margin-left: 160px;
}
}
