style_setting_options.value:
/* Dark grey */

* {color:#777!important;
    background-color:#262626!important;
    border-color:#1a1a1a!important;
    text-shadow:none!important;}

img {opacity:0.4!important;}

a {color:#8f8f8f!important;}

strong, h1, h2, h3, span, p, i, #HeaderLogo, .timeZone, .badges, .item-head, .item-image, .item-info {background-color:transparent!important;}

body {background:#262626!important; color:#b7b7b7!important;}

.button {color:#b0b0b0!important;
    border-color:#161616!important;
    background-color:#2f2f2f!important;
    background-image: linear-gradient(to bottom, #353535, #313131)!important;}
.button span, a.button span {color:#777!important;
    background:transparent!important;}

#Header h1 {opacity:0.7!important; background-image: url(data:image/png;base64,)!important;}

#HeaderGrad {background:#262626!important;}

#Header .header-navbar > li > a {border-color:transparent!important;
    color:#666!important;}

#Header .button-broadcast {opacity:0.2!important; border-color:#000!important;}
#Header .button-broadcast span {color:#fff!important;}

.probadge {text-shadow:none!important;
    background:#363636!important;}

.viewer-bg {background-image:none!important;}

#SocialStream {border-bottom:none!important;}

.messageInput.open, #Messages li {
    background:#262626!important;
    border-color:#1f1f1f!important;}

.sstream-more-toggle, #FlagContentToggle {opacity:0.2!important;
    background:#3f3f3f!important;}
.popOut {border:none!important; opacity:0.5!important;}
.viewSwitch .switch {opacity:0.2!important; color:#fff!important;}

.content .box.info .stats, .stats span {
        background:#303030!important;
        border-bottom-color:transparent!important;}

.tabs, .tab {
        background-repeat:repeat-x!important;
        background-position:-1px right!important;
        background-size:2px 32px!important;
        background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAgCAIAAAC3sD5OAAAAM0lEQVR42mNRVVdlAQIGBoYvn7+wfP/2nYOTgwWIf3z/gZ3HzsEOZLEARcjiEWEDNe0DANEHXYjvtY1MAAAAAElFTkSuQmCC)!important;
        border-bottom-color:transparent!important;}
.tab a {color:#777!important;
    text-shadow:none!important;
    background-color:transparent!important;}

.item-image img {opacity:1!important;}
.box-header {background-color:#313131!important; background-image:linear-gradient(to bottom, #353535, #313131)!important;}
.main-content, p {background-color:transparent!important;}
.badge-live {background-color:#bb4444!important; color:#eee!important;}

.side-col {top:360px!important;}

/* White */
#ChannelSection, .transparent-bg, .viewer-bg {background-image:none!important;}
#ChannelSection, .transparent-bg, .viewer-bg, #Footer.super-footer, #AdapTvTitleCard_Mask {
    background-color:#f3f3f3!important; }
#Footer.super-footer a, a.state.switch.discovery, h2 { color:#333!important;}
body#Experience_Empty {background-color:#fff!important; background-image:none!important;}
#Footer.super-footer hr.separator {border-color:#ccc!important;}
#ChooseLangForm.inFooter.control-group {opacity:0.6!important;}
#AdapTvTitleCard_Mask {width:100%!important; height:32px!important; bottom:0px!important;}
.content-col, iframe#SocialStream {margin-top:-32px!important;}

.side-col {top:394px!important;}

/* Large player */
.channel .media {width:980px!important; height:600px!important;}

.tabs, .tab {height:31px!important; line-height:31px!important;}

.box.iframeWrapper.iframeBelowTitleWrapper {transition: width 1s ease-in, max-height 1s ease-in!important;
    width:640px!important; max-height:10px!important;}
iframe.iframeBelowTitle.iframeTypeTop {position:absolute!important; overflow:hidden!important; opacity:0!important; 
    transition: opacity 0.2s ease-in!important;
    width:640px!important; max-height:0px!important;}
.box.iframeWrapper.iframeBelowTitleWrapper:hover {
     max-height:10px!important;
    z-index:9999999!important;}
.box.iframeWrapper.iframeBelowTitleWrapper:hover iframe.iframeBelowTitle.iframeTypeTop {
    overflow:visible!important; opacity:1!important; 
    width:960px!important; max-height:none!important;
    z-index:9999999!important;}

/* Fill window */
body.channel #ViewerSection {position:absolute!important; 
    width:100%!important; height:100%!important; 
    bottom:0px!important;}
body.channel #ViewerContent {overflow:visible!important; height:100%!important;}
body.channel .channel .media {position:absolute!important; 
    width:100%!important; height:100%!important; 
    bottom:0px!important;}
body.channel #UstreamViewer {position:fixed!important; 
    width:100%!important; height:100%!important; 
    top:0px!important; left:0px!important;  
    z-index:9000!important;}
body.channel .side-col {top:392px!important;}

body.channel #Header {position:absolute!important; 
    top:100%!important; bottom:34px!important; 
    height:59px!important; 
    z-index:9006!important;}
body.channel #MainContent {position:absolute!important; 
    top:100%!important; z-index:9000!important;}
body.channel #ChannelSection .main-content {top:59px!important;}
body.channel .viewer-bg {height:100%!important; z-index:9002!important;}
body.channel #ChannelSection {height:100%!important; 
    z-index:9003!important; 
    padding:0px!important;}

body.channel #Footer.super-footer {display:block!important;
    position:fixed!important;
    bottom:0px!important;
    z-index:9001!important;}
body.channel #Footer.super-footer > .main-content {position:fixed!important; height:32px!important; 
    bottom:0px!important; 
    /*left:150px!important;*/
    width:100%!important;
    background-color:#000!important;}
body.channel #Footer.super-footer > .main-content > *, .sstream-more-toggle {display:none!important;}
.content-col, iframe#SocialStream {margin-top:0px!important;}


