css:
@-moz-document url-prefix("https://www.google.co.jp/search?") {
#hdtbMenus.hdtb-td-c,
#hdtbMenus.hdtb-td-o {
    overflow: visible;
    display: inline-block !important;
    width: 19em;
    background: transparent;
}
[aria-label="期間指定なし"] + .hdtb-mn-o,
[aria-label*="以内"] + .hdtb-mn-o,
[aria-label*="年"] + .hdtb-mn-o,
[aria-label="期間指定なし"] + .hdtb-mn-c,
[aria-label*="以内"] + .hdtb-mn-c,
[aria-label*="年"] + .hdtb-mn-c {
    display: block !important;
    width: 10em !important;
    background: transparent;
    border: none;
    bottom: 50% !important;
    left: -1em !important;
    transform: translateY(50%);
    position: fixed;
    box-shadow: none;
    min-width: 0 !important;
}
[aria-label="期間指定なし"] + .hdtb-mn-o .hdtbItm,
[aria-label*="以内"] + .hdtb-mn-o .hdtbItm,
[aria-label*="年"] + .hdtb-mn-o .hdtbItm,
[aria-label="期間指定なし"] + .hdtb-mn-c .hdtbItm,
[aria-label*="以内"] + .hdtb-mn-c .hdtbItm,
[aria-label*="年"] + .hdtb-mn-c .hdtbItml {
    background: transparent !important;
}
[aria-label="期間指定なし"] + .hdtb-mn-o .hdtbItm:not(.hdtbSel):hover,
[aria-label*="以内"] + .hdtb-mn-o .hdtbItm:not(.hdtbSel):hover,
[aria-label*="年"] + .hdtb-mn-o .hdtbItm:not(.hdtbSel):hover,
[aria-label="期間指定なし"] + .hdtb-mn-c .hdtbItm:not(.hdtbSel):hover,
[aria-label*="以内"] + .hdtb-mn-c .hdtbItm:not(.hdtbSel):hover,
[aria-label*="年"] + .hdtb-mn-c .hdtbItml:not(.hdtbSel):hover {
    text-decoration: underline;
}
[aria-label="期間指定なし"] + .hdtb-mn-o .hdtbItm.hdtbSel,
[aria-label*="以内"] + .hdtb-mn-o .hdtbItm.hdtbSel,
[aria-label*="年"] + .hdtb-mn-o .hdtbItm.hdtbSel,
[aria-label="期間指定なし"] + .hdtb-mn-c .hdtbItm.hdtbSel,
[aria-label*="以内"] + .hdtb-mn-c .hdtbItm.hdtbSel,
[aria-label*="年"] + .hdtb-mn-c .hdtbItm.hdtbSel {
    background-image: none;
    border-width: 1px 0;
    border-style: solid;
    border-color: #ebebeb;
    width: calc(100% + 1px);
    color: #4285f4;
    font-weight: bold;
}
[aria-label*="年"] + .hdtb-mn-o #cdrlnk,
[aria-label*="年"] + .hdtb-mn-c #cdrlnk {
    color: inherit !important;
    outline: none;
}
[aria-label*="年"] + .hdtb-mn-o .hdtbSel #cdrlnk,
[aria-label*="年"] + .hdtb-mn-c .hdtbSel #cdrlnk {
    text-decoration: none !important;
    cursor: default;
}
[aria-label="期間指定なし"] + .hdtb-mn-o .hdtbItml:not(.hdtbSel) #cdrlnk:hover,
[aria-label*="以内"] + .hdtb-mn-o .hdtbItml:not(.hdtbSel) #cdrlnk:hover,
[aria-label*="年"] + .hdtb-mn-o .hdtbItml:not(.hdtbSel) #cdrlnk:hover,
[aria-label="期間指定なし"] + .hdtb-mn-c .hdtbItml:not(.hdtbSel) #cdrlnk:hover,
[aria-label*="以内"] + .hdtb-mn-c .hdtbItml:not(.hdtbSel) #cdrlnk:hover,
[aria-label*="年"] + .hdtb-mn-c .hdtbItml:not(.hdtbSel) #cdrlnk:hover {
    text-decoration: underline;
}
[aria-label="期間指定なし"] + .hdtb-mn-o > .hdtbItm,
[aria-label*="以内"] + .hdtb-mn-o > .hdtbItm,
[aria-label*="年"] + .hdtb-mn-o > .hdtbItm,
[aria-label="期間指定なし"] + .hdtb-mn-c > .hdtbItm,
[aria-label*="以内"] + .hdtb-mn-c > .hdtbItm,
[aria-label*="年"] + .hdtb-mn-c > .hdtbItm {
    width: 10em;
    padding: 0.55em 0;
    margin: 0;
    text-align: center;
}
[aria-label="期間指定なし"] + .hdtb-mn-o > .hdtbSel:hove,
[aria-label*="以内"] + .hdtb-mn-o > .hdtbSel:hove,
[aria-label*="年"] + .hdtb-mn-o > .hdtbSel:hover,
[aria-label="期間指定なし"] + .hdtb-mn-c > .hdtbSel:hove,
[aria-label*="以内"] + .hdtb-mn-c > .hdtbSel:hove,
[aria-label*="年"] + .hdtb-mn-c > .hdtbSel:hover {
    background-color: transparent !important;
}
[aria-label="期間指定なし"] + .hdtb-mn-o > .hdtbItm:not(.hdtbSel),
[aria-label*="以内"] + .hdtb-mn-o > .hdtbItm:not(.hdtbSel),
[aria-label*="年"] + .hdtb-mn-o > .hdtbItm:not(.hdtbSel),
[aria-label="期間指定なし"] + .hdtb-mn-c > .hdtbItm:not(.hdtbSel),
[aria-label*="以内"] + .hdtb-mn-c > .hdtbItm:not(.hdtbSel),
[aria-label*="年"] + .hdtb-mn-c > .hdtbItm:not(.hdtbSel) {
    border-right: 1px solid #ebebeb;
}
[aria-label="期間指定なし"] + .hdtb-mn-o > .hdtbItm a:hover,
[aria-label*="以内"] + .hdtb-mn-o > .hdtbItm a:hover,
[aria-label*="年"] + .hdtb-mn-o > .hdtbItm a:hover,
[aria-label="期間指定なし"] + .hdtb-mn-c > .hdtbItm a:hover,
[aria-label*="以内"] + .hdtb-mn-c > .hdtbItm a:hover,
[aria-label*="か月以内"] + .hdtb-mn-c > .hdtbItm a:hover,
[aria-label*="年"] + .hdtb-mn-c > .hdtbItm a:hover {
    background-color: transparent !important;
    text-decoration: underline !important;
}
[aria-label="期間指定なし"] + .hdtb-mn-o .cdr_sep,
[aria-label*="以内"] + .hdtb-mn-o .cdr_sep,
[aria-label*="年"] + .hdtb-mn-o .cdr_sep,
[aria-label="期間指定なし"] + .hdtb-mn-c .cdr_sep,
[aria-label*="以内"] + .hdtb-mn-c .cdr_sep,
[aria-label*="年"] + .hdtb-mn-c .cdr_sep {
    display: none !important;
}
#resultStats {
    display: none;
}
#cdrlnk {
    background: transparent !important;
    background-image: none !important;
}
}
