@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("startpage.com"),domain("ixquick.com") {


  body,
  .bookmark,
  table,
  td,
  #pagenavigation,
  #links,
  .content,
  .title,
  .subtitle,
  #toptoolbar,
  .no_results_top_message,
   html,
   .no_results_messages_new_ui
  {
    background: #0F0E0F !important;
    color : #F9F5C2 !important;
  }

  input,
  button,
  #query,
  #query_top,
  #query_bottom,
  #privacy,
  .searchquery,
  #inner_promo_box,
  #try_ixquick_inner_promo_box
  {
    background:#292429 !important;
    color : #FDF5A9 !important;
    border-color: #FDF5A9 !important;
    border-radius: 4px !important;   
  }


  p,
  .normaltext,
  .reltext,
  .relatedtext,
  .desc
  {
    color: #F9F5C2 !important ;
  }

  #news_message_content,
  #news_message_content a,
  .date_promo,
  #links a,
  #footer a,
  hr,
  .noresults_suggestion_labels,
  #lang_div
  {
    background:#0F0E0F !important;
    color : #F8AC35 !important;
    border:none !important;
  }


  #try_ixquick_promo_box
  {
    border: none !important
  }

  a,
  #footer a
  {
    color: #FF3D73 !important;
  }
  
  a:hover
  {
    color: #FDF5A9 !important;
  }

  #main_link img,
  #logo a img,
  td.tdmenu.tdmenurepeat img,
  #toptablex img,
  #ixquick_logo,
  img[src="/graphics/ixquick.gif?r=2"],
  img[src="graphics/eng/poweredbyblinkx.gif"]
  {
    display: none !important;
  }

  #footer
  {
    color : #F9F5C2 !important;
  }


  #enhanced
  {
    visibility:hidden !important ;
  }


  select
  {
    color: #0F0E0F !important;
  }

  td.button
  {
    border-color: #FDF5A9 !important;
  }

  #results
  {
    border-left:1px solid #FDF5A9 !important;
  }

  #tod_wrap
  {
    border-left: none !important;
  }

  #by_time
  {
    border-right: none !important;
  }

  #query
  {
    width: 472px !important;
  }

  #green,
  strong,
  .url
  {
    color: #9AD74C !important;
  }

  b,
  table font,
  h3,
  .date_promo_head,
  #pnform,
  .numbers,
  #search_form li,
  #search_form label,
  .query_string_on_no_results
  {
    color:#3FB8CD !important;
  }

  #submit,
  #submit1,
  #submit_box,
  .searchbuttonlink
  {
    display:none !important;
  }

  #topbanner
  {
    display: none !important;
  }

  .boxcontainer
  {
    background: none !important;
  }

  .boxcontainercontent
  {
    background:#292429 !important;
  }


  .cssprevious_st
  {
    background:#0F0E0F !important;
    color: #FF3D73 !important;
  }

  noscript input.button
  {
    color:#0F0E0F !important;
  }

}
