css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(www\\.)?python-forum.org/(?!adm/).*") {

/* GENERAL */
html, body {
    background: #000 !important;
}
textarea {
    border: 1px solid #444 !important;
}

/* FORUM PANELS */
.headerbar, .forabg, .forumbg {
    background: #111 !important;
    border-radius: 0 !important;
    border: 1px solid #444 !important;
}

.corners-top, .corners-bottom {
    visibility: hidden !important;
}

li.row, tbody tr, .navbar {
    background: #aaa !important;
}
li.row:hover, tbody tr:hover {
    background: #bbb !important;
}
li.row {
    border-bottom-color: #444 !important;
}

/* POST FORMATTING */
.post, .panel {
    background: #aaa !important;
    border: 1px solid #fff !important;
}
.postlink {
    color: #0d4473 !important;
    border-bottom: 0 !important;
}
.postlink:hover {
    background-color: #bbb !important;
}
.signature, .notice {
    border-top-color: #111 !important;
}

/* CODE BLOCKS AND QUOTES */
.codebox, blockquote {
    background-color: #888 !important;
    border-color: #444 !important;
}
blockquote .codebox,blockquote  blockquote {
    background-color: #777 !important;
}
code {
    color: #444 !important;
}

/* LINKS */
h2 a{
    color: #fff !important;
}
#page-body > p > a:not(.username-coloured):hover,
#page-body > p > strong > a:hover,
#page-body > h3 a:hover,
.copyright a:hover,
.linklist a:hover {
    color: #36bad2 !important;
}
a.forumtitle, a.topictitle, .navbar a, .postbody h3 a{
    color: #333 !important;
}
a.forumtitle:hover, a.topictitle:hover, .navbar a:hover, .postbody h3 a:hover{
    color: #111 !important;
}

}
