}


.ie8 input[type="radio"] {
    display: inline-block;
}
.ie8 input[type="radio"] + label {
  height: auto;
  margin-left: -20px;
  margin-top: -12px;
}

.ie8 input[type="radio"] + label:before {
  background: none;
  position: static;
}

.ie9 input[type="radio"] + label {
  padding-top: 2px;
}

input[type="radio"] {
    display:inline;
}

.radio-hidden {
    display:none;
}

input[type="radio"] + label {
  display: inline-block;
  height: 19px;
    padding-top: 1px;
    margin-right: 14px;
    margin-left: 18px;

    position: relative;
      font-family: Tahoma;
      font-size: 12px;
      color: #B3B3B3 !important;
}

input[type="radio"] + label:before {
    content: "";
    display: inline-block;
    height: 17px;
    width: 16px;
    background: url(http://www.galagiveaways.com/images/radio-sprite.jpg) left bottom no-repeat;
      margin: 0;
      position: absolute;      
      left: -23px;      
      top: 1px;
      z-index: 1;
}

input[type="radio"]:checked + label:before {
    background-position: left top;
}

.radio-create {
  display: inline;
}

input[type="text"] {
  width: 163px;
  height: 26px;
