css:
@-moz-document domain(tumblr.com) {
Body{

    background: url('http://24.media.tumblr.com/0d0a0160d95591413fc38bb9086092b4/tumblr_mpustwcAz21qf0suko1_500.png') no-repeat left bottom fixed,#d993b1 url('http://viper-fox.com/card/site/cvbg2.png') fixed; 
	font-size:12px !important;

}






#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(252, 197, 221, 0.2) !important;
-webkit-border-radius: 15px !important;
border-radius: 15px !important;
padding-top: 20px !important;
padding-bottom: 20px !important;
}



div.l-content #right_column li {
background-color: rgba(252, 197, 221, 0.3) !important;
}

div.l-content #right_column .small_links a {
color:rgba(252, 197, 221, 0.7)!important;
}

div.l-content #right_column li:hover {
background-color: #fcc5dd !important;
}

div.l-content #right_column::after {
background: none !important;
}

div.l-content .setting, div.l-content #left_column .white_pane form div, div.l-content .left_side {
background: transparent !important;
border: none !important;
}

div.l-content .white_pane, #wrapper #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color: #ffffff !important;
}

div.l-content #new_group_name.text_field.big, div.l-content #new_group_password_protected.text_field {
background-color: #ffffff !important;
box-shadow: #fcc5dd  0pt 1px 0pt !important;
background-image: none !important;
border-radius: 3px !important;
-webkit-border-radius: 3px !important;
}

#container .selection_nipple, #container .tab_notice_nipple {
visibility: hidden;
}

div.l-content #right_column a,  div.l-content #right_column small,div.l-content #right_column .link_arrow, div.l-content #right_column #tumblr_radar .radar_footer span, div.l-content #right_column .count, div.l-content #right_column .mark_date, div.l-content a.username, div.l-content #page_heading {
color: #d9aacd !important;
}



div.l-content #right_column .radar {
background-color: #fcc5dd  !important;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
padding-bottom: 5px !important;
}

div.l-content #right_column .radar_avatar .radar_tumblelog_avatar {
box-shadow: none !important;
margin-left: 5px !important;
}

div.l-content #right_column .radar .radar_footer {
margin-top: 4px !important;
margin-bottom: 1px !important;
}

div.l-content #right_column .radar .radar_controls {
border-bottom: 1px solid #fcc5dd  !important;
}


div.l-content #right_column #search_query {
border: 1px solid #ff0112 !important;
background-color: #fcc5dd  !important;
border-bottom: #fcc5dd  solid 1px !important;
border-top: #fcc5dd  1px solid !important;
color: #fcc5dd  !important;
}

div.l-content #right_column a.mass_editor {
border-top: 1px solid #30a49e !important;
}

div.l-content #right_column .thumbnail_anchor {
box-shadow: 0pt 1px 0pt rgba(255, 255, 255, 0.25) inset, 0pt 1px 3px rgba(0, 0, 0, 0.25);
}

div.l-content #right_column .radar_content .post {
background: transparent !important;
}



div.l-content .short_post_labels .short_label a{
border: none !important;
text-shadow: none !important;
color: #ffffff !important;
font-weight: bold !important;
}

div.l-content .dashboard_header, div.l-content .dashboard_header a {
color: #d9aacd !important;
}

div.l-content #new_post.short_new_post.post.new_post {
background: none !important;
background-image: none !important;
border: none !important;
text-shadow: none !important;
box-shadow: none !important;
}






























h1,h2,h3,h4,h5,h6{color:#ffa8c8 !important}























































a, a:visited{color:#de6d96 !important}























































a:hover{color:#f25e94 !important}























































#next_page_link, #previous_page_link,
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    border:0px!important;
    background: rgba(255,255,255,0.6) !important;
}























































.post_question, 
#left_column #posts .post .permalink {
    background: rgba(255,255,255,0.6) !important;
}














































.post_question_nipple, .arrow{
    background:transparent!important;}
















































#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
   background: rgba(255,255,255,0.6) !important;
   border-top:0px solid transparent!important;
   border-bottom:0px solid transparent!important;
   border: 0px solid #B4EB3D;}














































#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs_firt {
    background: rgba(255,255,255,0.6) !important;
   border-top:0px solid transparent!important;
   border-bottom:0px solid transparent!important;}














































#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
   border-top:0px solid transparent!important;
   border-bottom:0px solid transparent!important;
}














































li.post,
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: rgba(255,255,255,0.6) !important;
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    border:0px!important;
    border-radius: 20px;
    margin: auto;
    padding: 20px 20px;
  }


























































#left_column #posts .post .post_avatar,
#left_column #posts .post .sub_avatar .sub_avatar_inner {
border-bottom-color: transparent !important;
}














































#left_column #posts .post{
border-bottom:0px #eee solid !important
}














































#search_controls{
border-bottom-color: transparent !important;
    border-bottom:none!important;
    border-top:none!important;
    border:none!important;
    background: rgba(255,255,255,0.9) !important;
}














































#help_docs:hover, 
#all_of_tumblr:hover,
#everyone_i_follow:hover,
#my_posts:hover{
    background: rgba(255,255,255,0.3) !important;
border-bottom:0px #eee solid !important;
border-top:0px #eee solid !important;
}















































#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: rgba(255,255,255,0.6) !important;
background-image:none !important;
border:0px !important
}














































#nav .nav_item .nav_menu {
border-bottom:0px !important
}






































#nav .nav_item .nav_menu:hover{color:#487A8E !important;
    background: rgba(255,255,255,0.3) !important;
}














































#nav .nav_item .nav_menu a {
color:#d9aacd !important;
border-top-color:0px #eee !important;
}














































#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
    background: none;
  }
















































#right_column .dashboard_nav_item {
    background: none;
    background-attachment: initial;
    background-position-x: 50%;
    background-position-y: 100%;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
}














































#photo_upload,
#embed_a_video  {
    background: rgba(255,255,255,0.6) !important;
}














































#right_column .dashboard_nav_item #hide_radar {
background-image: none;
background-repeat-x: no-repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: transparent;
}














































#nav .nav_item .nav_menu a:hover {color:#487A8E!important;}














































#footer, #footer a {
color:#d9aacd !important;
background-image:none !important;
}























































.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {
color:#ccc !important
}














































#right_column .dashboard_nav_item {
border-bottom:0px solid #eee !important;
background-image:none  !important
}














































#right_column .dashboard_nav_item .dashboard_nav_title,
#right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,
#right_column .dashboard_nav_item ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {
                        color:#d9aacd !important;
			border-bottom:0px solid #eee !important;
}














































#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
color:#d9aacd !important;
border-bottom:0px solid #eee !important;
}














































#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
background: rgba(255,255,255,0.6) !important;
border-bottom:0px solid #eee !important;
}





































#show_radar a, #hide_radar a,
#hide_recommended_blogs a, #show_recommended_blogs a {color:#d9aacd !important;}












#left_column #posts .notification .avatar {
                        border-color:transparent !important;
                        background-color:transparent !important;
			opacity:0.6 !important;
}



















#left_column #posts .notification .avatar:hover{opacity:1 !important;}














































