css:
/* i really want this to be global */
div.ch-list>div.ch-list-wrapper
{padding-top:73px!important}
div.ch-list>div.ch-list-wrapper>div.chl_section,
div#fm-section3>div#fm-footer,
div#fm-header>div#user_info>div.pro-promo,
iframe#fm-rotate-ad
{display:none !important}
div#fm-channel-list>div#fm-section3
{padding-bottom:0!important}
ul.channel_list>li.channel>a.chl_name
{border-radius:8px 20px 20px 8px!important;
text-indent:40px!important;
box-shadow:inset 0 0 30px rgba(150,150,150,0.3)}
body>div#fm-section>div#fm-player>div.player-wrap
{border-radius:20% 18px!important;
box-shadow:0px 0px 15px -5px rgba(0,0,0,0.5)!important;}
body>div#simulate-sec,
body>div#fm-section2
{left:100px!important}
body>div#fm-section>div#fm-player
{top:185px!important}
#fm-header ul li #rec_played
{color:#7dab90!important}
#fm-header ul li #rec_liked,
#fm-header ul li #faved
{color:orchid!important}
div#fm-section>div#fm-header>div#user_info
{top:130px!important}
